export const handleAPIError = (error) => {
  console.error("API Ïò§Î•ò ÏÉÅÏÑ∏ Î∂ÑÏÑù:", {
    message: error.message,
    code: error.code,
    status: error.response?.status,
    statusText: error.response?.statusText,
    data: error.response?.data,
    timestamp: new Date().toISOString(),
  });

  // 403 Forbidden ÌäπÎ≥Ñ Ï≤òÎ¶¨
  if (error.response?.status === 403) {
    return {
      message: "API Ï†ëÍ∑ºÏù¥ Ï∞®Îã®ÎêòÏóàÏäµÎãàÎã§. Í¥ÄÎ¶¨ÏûêÏóêÍ≤å Î¨∏ÏùòÌïòÏÑ∏Ïöî.",
      statusCode: 403,
      errorType: "FORBIDDEN",
    };
  }

  // Gateway Timeout ÌäπÎ≥Ñ Ï≤òÎ¶¨
  if (error.response?.status === 504) {
    return {
      message:
        "ÏÑúÎ≤Ñ ÏùëÎãµ ÏãúÍ∞ÑÏù¥ Ï¥àÍ≥ºÎêòÏóàÏäµÎãàÎã§. ÏöîÏ≤≠ÏùÑ Í∞ÑÏÜåÌôîÌïòÍ±∞ÎÇò Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.",
      statusCode: 504,
      errorType: "GATEWAY_TIMEOUT",
    };
  }

  // CORS Ïò§Î•ò ÌäπÎ≥Ñ Ï≤òÎ¶¨
  if (
    error.message?.includes("CORS") ||
    error.code === "ERR_NETWORK" ||
    error.message?.includes("Access-Control-Allow-Origin")
  ) {
    return {
      message:
        "ÏÑúÎ≤Ñ Ïó∞Í≤∞ ÏÑ§Ï†ïÏóê Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§. ÌéòÏù¥ÏßÄÎ•º ÏÉàÎ°úÍ≥†Ïπ®ÌïòÍ≥† Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.",
      statusCode: 0,
      errorType: "CORS_ERROR",
    };
  }

  // ÌÉÄÏûÑÏïÑÏõÉ Ïò§Î•ò ÌäπÎ≥Ñ Ï≤òÎ¶¨
  if (error.code === "ECONNABORTED") {
    return {
      message:
        "ÏöîÏ≤≠ Ï≤òÎ¶¨ ÏãúÍ∞ÑÏù¥ Ï¥àÍ≥ºÎêòÏóàÏäµÎãàÎã§. ÏûÖÎ†•ÏùÑ Ï§ÑÏù¥Í±∞ÎÇò Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.",
      statusCode: 0,
      errorType: "TIMEOUT_ERROR",
    };
  }

  if (error.response) {
    const status = error.response.status;
    const message =
      error.response.data?.message ||
      error.response.data?.error ||
      "ÏÑúÎ≤Ñ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§";

    switch (status) {
      case 400:
        return { message: `ÏûòÎ™ªÎêú ÏöîÏ≤≠: ${message}`, statusCode: 400 };
      case 401:
        return { message: "Ïù∏Ï¶ùÏù¥ ÌïÑÏöîÌï©ÎãàÎã§", statusCode: 401 };
      case 404:
        return { message: "ÏöîÏ≤≠Ìïú Î¶¨ÏÜåÏä§Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§", statusCode: 404 };
      case 429:
        return {
          message: "ÏöîÏ≤≠Ïù¥ ÎÑàÎ¨¥ ÎßéÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî",
          statusCode: 429,
        };
      case 500:
        return { message: "ÏÑúÎ≤Ñ ÎÇ¥Î∂Ä Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§", statusCode: 500 };
      default:
        return {
          message: `ÏÑúÎ≤Ñ Ïò§Î•ò (${status}): ${message}`,
          statusCode: status,
        };
    }
  } else if (error.request) {
    return {
      message: "ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§. ÎÑ§Ìä∏ÏõåÌÅ¨ Ïó∞Í≤∞ÏùÑ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî",
      statusCode: 0,
      errorType: "NETWORK_ERROR",
    };
  } else {
    return {
      message: `ÏöîÏ≤≠ Ïò§Î•ò: ${error.message}`,
      statusCode: -1,
      errorType: "REQUEST_ERROR",
    };
  }
};

