import React, { useState, useEffect, useRef } from 'react';
import ReactMarkdown from 'react-markdown';
import { 
  PaperAirplaneIcon, 
  ClipboardDocumentIcon,
  CheckCircleIcon,
  UserIcon,
  CpuChipIcon 
} from '@heroicons/react/24/outline';
import { toast } from 'react-hot-toast';
import { generateAPI } from '../services/api';

const ChatInterface = ({ projectId, projectName }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [copiedMessage, setCopiedMessage] = useState(null);
  const [streamingMessage, setStreamingMessage] = useState('');
  const [isStreaming, setIsStreaming] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);
  const streamingRef = useRef(null);

  // ÎåÄÌôî ÏÑ∏ÏÖò Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    const welcomeMessage = {
      id: Date.now(),
      type: 'ai',
      content: `ÏïàÎÖïÌïòÏÑ∏Ïöî! Ï†ÄÎäî ${projectName}Ïùò AI Ï†úÎ™© ÏûëÍ∞ÄÏûÖÎãàÎã§. üéØ\n\nÍ∏∞ÏÇ¨ ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏãúÎ©¥ Îã§ÏñëÌïú Ïä§ÌÉÄÏùºÏùò Ï†úÎ™©ÏùÑ Ï†úÏïàÌï¥ÎìúÎ¶¥Í≤åÏöî. Ï†úÎ™©ÏùÑ ÏàòÏ†ïÌïòÍ±∞ÎÇò Îã§Î•∏ Ïä§ÌÉÄÏùºÎ°ú Î∞îÍæ∏Í≥† Ïã∂ÏúºÏãúÎ©¥ Ïñ∏Ï†úÎì† ÎßêÏîÄÌï¥Ï£ºÏÑ∏Ïöî!`,
      timestamp: new Date()
    };
    setMessages([welcomeMessage]);
  }, [projectName]);

  // Î©îÏãúÏßÄ Ï∂îÍ∞Ä Ïãú Ïä§ÌÅ¨Î°§ ÌïòÎã®ÏúºÎ°ú
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  // Ïä§Ìä∏Î¶¨Î∞ç ÌÉÄÏù¥Ìïë Ìö®Í≥º
  const simulateStreaming = (text, onComplete) => {
    setIsStreaming(true);
    setStreamingMessage('');

    let index = 0;
    const interval = setInterval(() => {
      if (index < text.length) {
        setStreamingMessage(prev => prev + text[index]);
        index++;
        scrollToBottom();
      } else {
        clearInterval(interval);
        setIsStreaming(false);
        setStreamingMessage('');
        onComplete();
      }
    }, 8); // 8ms Í∞ÑÍ≤©ÏúºÎ°ú Ìïú Í∏ÄÏûêÏî© Ï∂úÎ†• (Îß§Ïö∞ Îπ†Î•¥Í≤å)

    streamingRef.current = interval;
  };

  // Î©îÏãúÏßÄ Ï†ÑÏÜ°
  const handleSendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: inputMessage,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    const currentInput = inputMessage;
    setInputMessage('');
    setIsLoading(true);

    try {
      // ÎåÄÌôî Ïª®ÌÖçÏä§Ìä∏ ÏÉùÏÑ± (ÏµúÍ∑º 5Í∞ú Î©îÏãúÏßÄ)
      const recentMessages = messages.slice(-5);
      const conversationContext = recentMessages
        .map(msg => `${msg.type === 'user' ? 'Human' : 'Assistant'}: ${msg.content}`)
        .join('\n\n');

      const contextualInput = recentMessages.length > 0 
        ? `[Ïù¥Ï†Ñ ÎåÄÌôî Ïª®ÌÖçÏä§Ìä∏]\n${conversationContext}\n\n[ÏÉàÎ°úÏö¥ ÏöîÏ≤≠]\nHuman: ${currentInput}`
        : currentInput;

      const result = await generateAPI.generateTitle(
        projectId,
        contextualInput,
        null // onProgress callback
      );

      // Ïä§Ìä∏Î¶¨Î∞ç Ìö®Í≥ºÎ°ú AI ÏùëÎãµ Ï∂úÎ†•
      simulateStreaming(result.result, () => {
        const aiMessage = {
          id: Date.now() + 1,
          type: 'ai',
          content: result.result,
          timestamp: new Date(),
          debug: result.debug
        };
        setMessages(prev => [...prev, aiMessage]);
      });

    } catch (error) {
      console.error('Î©îÏãúÏßÄ Ï†ÑÏÜ° Ïã§Ìå®:', error);
      const errorMessage = {
        id: Date.now() + 1,
        type: 'ai',
        content: 'Ï£ÑÏÜ°Ìï©ÎãàÎã§. Î©îÏãúÏßÄ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.',
        timestamp: new Date(),
        isError: true
      };
      setMessages(prev => [...prev, errorMessage]);
      toast.error('Î©îÏãúÏßÄ Ï†ÑÏÜ°Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setIsLoading(false);
    }
  };

  // Enter ÌÇ§Î°ú Ï†ÑÏÜ°
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Ï†úÎ™©Îßå Ï∂îÏ∂úÌïòÏó¨ Î≥µÏÇ¨
  const copyTitlesOnly = (text, messageId) => {
    const extractedTitles = extractTitles(text);
    const textToCopy = extractedTitles.length > 0 ? extractedTitles.join('\n') : text;

    navigator.clipboard.writeText(textToCopy)
      .then(() => {
        setCopiedMessage(messageId);
        toast.success('Ï†úÎ™©Ïù¥ ÌÅ¥Î¶ΩÎ≥¥ÎìúÏóê Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!');
        setTimeout(() => setCopiedMessage(null), 2000);
      })
      .catch(() => {
        toast.error('Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      });
  };

  // Ï†ÑÏ≤¥ ÌÖçÏä§Ìä∏ Î≥µÏÇ¨
  const copyFullText = (text, messageId) => {
    navigator.clipboard.writeText(text)
      .then(() => {
        setCopiedMessage(messageId + '_full');
        toast.success('Ï†ÑÏ≤¥ ÌÖçÏä§Ìä∏Í∞Ä Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!');
        setTimeout(() => setCopiedMessage(null), 2000);
      })
      .catch(() => {
        toast.error('Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      });
  };

  // Ï†úÎ™© Ï∂îÏ∂ú Ìï®Ïàò - Îã§ÏñëÌïú ÌòïÏãù ÏßÄÏõê
  const extractTitles = (text) => {
    const titles = [];

    // 1. Î≤àÌò∏ ÌòïÏãù: "1. [Ï†úÎ™©]" (ÏÉà ÌÖúÌîåÎ¶ø)
    const numberedMatches = text.match(/^\d+\.\s+(.+?)(?=\n\s*-|$)/gm);
    if (numberedMatches) {
      numberedMatches.forEach(match => {
        const title = match.replace(/^\d+\.\s+/, '').trim();
        if (title && !title.includes('ÌíàÏßà ÌèâÍ∞Ä')) {
          titles.push(title);
        }
      });
    }

    // 2. Bullet ÌòïÏãù: "‚Ä¢ [Ï†úÎ™©]" (ÌòÑÏû¨ Ï∂úÎ†• ÌòïÏãù)
    const bulletMatches = text.match(/‚Ä¢ "([^"]+)"/g);
    if (bulletMatches) {
      bulletMatches.forEach(match => {
        const title = match.replace(/‚Ä¢ "(.+)"/, '$1').trim();
        if (title) {
          titles.push(title);
        }
      });
    }

    // 3. ÏùºÎ∞ò Bullet ÌòïÏãù: "‚Ä¢ [Ï†úÎ™©]"
    if (titles.length === 0) {
      const simpleBulletMatches = text.match(/‚Ä¢ ([^\n]+)/g);
      if (simpleBulletMatches) {
        simpleBulletMatches.forEach(match => {
          const title = match.replace('‚Ä¢ ', '').trim();
          // ÏÑ§Î™ÖÎ¨∏Ïù¥ ÏïÑÎãå Ï†úÎ™©Îßå Ï∂îÏ∂ú
          if (title && !title.includes('Ïù¥Ïú†:') && !title.includes('Ïù¥Í≤ΩÍ∑úÏùò') && title.length < 100) {
            titles.push(title);
          }
        });
      }
    }

    return titles;
  };

  // Í∞úÎ≥Ñ Ï†úÎ™© Î≥µÏÇ¨
  const copyIndividualTitle = (title, messageId, index) => {
    navigator.clipboard.writeText(title)
      .then(() => {
        setCopiedMessage(`${messageId}_title_${index}`);
        toast.success('Ï†úÎ™©Ïù¥ Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!');
        setTimeout(() => setCopiedMessage(null), 2000);
      })
      .catch(() => {
        toast.error('Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      });
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-lg shadow-sm border border-gray-200">
      {/* Ï±ÑÌåÖ Ìó§Îçî */}
      <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-white rounded-t-lg">
        <div className="flex items-center">
          <CpuChipIcon className="h-5 w-5 text-gray-600 mr-2" />
          <h3 className="text-md font-medium text-gray-900">AI ÎåÄÌôî</h3>
          <span className="ml-2 text-sm text-gray-500">{projectName}</span>
        </div>
        <button
          onClick={() => setMessages(messages.slice(0, 1))} // Ï≤´ Î≤àÏß∏ ÌôòÏòÅ Î©îÏãúÏßÄÎßå ÎÇ®Í∏∞Í∏∞
          className="text-sm text-gray-500 hover:text-gray-700 px-2 py-1 rounded hover:bg-gray-100 transition-colors"
        >
          Ï¥àÍ∏∞Ìôî
        </button>
      </div>

      {/* Î©îÏãúÏßÄ ÏòÅÏó≠ */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 min-h-0">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div className={`flex max-w-[80%] ${message.type === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
              {/* ÏïÑÎ∞îÌÉÄ */}
              <div className={`flex-shrink-0 ${message.type === 'user' ? 'ml-3' : 'mr-3'}`}>
                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                  message.type === 'user' 
                    ? 'bg-blue-600 text-white' 
                    : 'bg-gray-200 text-gray-600'
                }`}>
                  {message.type === 'user' ? (
                    <UserIcon className="w-5 h-5" />
                  ) : (
                    <CpuChipIcon className="w-5 h-5" />
                  )}
                </div>
              </div>

              {/* Î©îÏãúÏßÄ Î≤ÑÎ∏î */}
              <div className={`rounded-lg px-4 py-3 ${
                message.type === 'user'
                  ? 'bg-blue-500 text-white'
                  : message.isError
                  ? 'bg-red-50 text-red-900 border border-red-200'
                  : 'bg-gray-50 text-gray-900'
              }`}>
                {message.type === 'ai' ? (
                  <div className="text-sm leading-relaxed">
                    <ReactMarkdown
                      components={{
                        p: ({ children }) => <p className="mb-3 last:mb-0 leading-relaxed">{children}</p>,
                        strong: ({ children }) => <strong className="font-semibold text-gray-900">{children}</strong>,
                        ul: ({ children }) => <ul className="list-disc list-inside space-y-2 my-3">{children}</ul>,
                        li: ({ children }) => <li className="text-gray-800 leading-relaxed">{children}</li>,
                        br: () => <br className="mb-1" />,
                        h1: ({ children }) => <h1 className="text-lg font-bold mb-3 mt-4 first:mt-0">{children}</h1>,
                        h2: ({ children }) => <h2 className="text-md font-bold mb-2 mt-3 first:mt-0">{children}</h2>,
                        h3: ({ children }) => <h3 className="text-sm font-bold mb-2 mt-3 first:mt-0">{children}</h3>
                      }}
                    >
                      {message.content}
                    </ReactMarkdown>
                  </div>
                ) : (
                  <div className="whitespace-pre-wrap text-sm leading-relaxed">
                    {message.content}
                  </div>
                )}

                {/* AI Î©îÏãúÏßÄÏóê Ï†úÎ™© Ï∂îÏ∂ú Î∞è Î≥µÏÇ¨ Í∏∞Îä• */}
                {message.type === 'ai' && !message.isError && (
                  <>
                    {/* Ï∂îÏ∂úÎêú Ï†úÎ™©Îì§ */}
                    {extractTitles(message.content).length > 0 && (
                      <div className="mt-4 p-3 bg-white rounded border border-gray-200">
                        <h4 className="text-sm font-medium text-gray-700 mb-2">üìù Ï∂îÏ∂úÎêú Ï†úÎ™© (ÌÅ¥Î¶≠ÌïòÏó¨ Î≥µÏÇ¨)</h4>
                        <div className="space-y-2">
                          {extractTitles(message.content).map((title, index) => (
                            <div 
                              key={index} 
                              onClick={() => copyIndividualTitle(title, message.id, index)}
                              className={`text-sm text-gray-800 p-3 rounded-lg cursor-pointer transition-all duration-200 border ${
                                copiedMessage === `${message.id}_title_${index}`
                                  ? 'bg-green-50 border-green-200 text-green-800'
                                  : 'bg-blue-50 border-blue-200 hover:bg-blue-100 hover:border-blue-300'
                              }`}
                            >
                              <div className="flex items-center justify-between">
                                <span className="flex-1">{title}</span>
                                {copiedMessage === `${message.id}_title_${index}` ? (
                                  <CheckCircleIcon className="w-4 h-4 text-green-600 ml-2 flex-shrink-0" />
                                ) : (
                                  <ClipboardDocumentIcon className="w-4 h-4 text-blue-600 ml-2 flex-shrink-0 opacity-60" />
                                )}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Î≥µÏÇ¨ Î≤ÑÌäºÎì§ */}
                    <div className="flex items-center justify-between mt-3 pt-2 border-t border-gray-200">
                      <span className="text-xs text-gray-500">
                        {message.timestamp.toLocaleTimeString('ko-KR', {
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </span>
                      <div className="flex space-x-2">
                        {extractTitles(message.content).length > 0 && (
                          <button
                            onClick={() => copyTitlesOnly(message.content, message.id)}
                            className="flex items-center text-xs text-blue-600 hover:text-blue-700 px-2 py-1 rounded hover:bg-blue-50 transition-colors"
                          >
                            {copiedMessage === message.id ? (
                              <>
                                <CheckCircleIcon className="w-3 h-3 mr-1 text-green-600" />
                                Ï†úÎ™© Î≥µÏÇ¨Îê®
                              </>
                            ) : (
                              <>
                                <ClipboardDocumentIcon className="w-3 h-3 mr-1" />
                                Ï†úÎ™©Îßå Î≥µÏÇ¨
                              </>
                            )}
                          </button>
                        )}
                        <button
                          onClick={() => copyFullText(message.content, message.id)}
                          className="flex items-center text-xs text-gray-500 hover:text-gray-700 px-2 py-1 rounded hover:bg-gray-200 transition-colors"
                        >
                          {copiedMessage === message.id + '_full' ? (
                            <>
                              <CheckCircleIcon className="w-3 h-3 mr-1 text-green-600" />
                              Ï†ÑÏ≤¥ Î≥µÏÇ¨Îê®
                            </>
                          ) : (
                            <>
                              <ClipboardDocumentIcon className="w-3 h-3 mr-1" />
                              Ï†ÑÏ≤¥ Î≥µÏÇ¨
                            </>
                          )}
                        </button>
                      </div>
                    </div>
                  </>
                )}

                {/* ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ (Í∞úÎ∞ú Î™®Îìú) */}
                {message.debug && (
                  <details className="mt-2 text-xs">
                    <summary className="text-gray-500 cursor-pointer hover:text-gray-700">
                      üîç ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥
                    </summary>
                    <div className="mt-1 p-2 bg-white rounded border text-gray-600 space-y-1">
                      <div>ÌîÑÎ°¨ÌîÑÌä∏ Ïàò: {message.debug.promptCount || 0}Í∞ú</div>
                      <div>Í∏∞Î≥∏ Í∞ÄÏù¥ÎìúÎùºÏù∏: {message.debug.usedDefaultGuidelines ? 'ÏÇ¨Ïö©' : 'ÎØ∏ÏÇ¨Ïö©'}</div>
                      {message.debug.promptTitles && (
                        <div>Ï†ÅÏö©Îêú ÌîÑÎ°¨ÌîÑÌä∏: {message.debug.promptTitles.join(', ')}</div>
                      )}
                    </div>
                  </details>
                )}
              </div>
            </div>
          </div>
        ))}

        {/* Ïä§Ìä∏Î¶¨Î∞ç Î©îÏãúÏßÄ */}
        {isStreaming && (
          <div className="flex justify-start">
            <div className="flex mr-3">
              <div className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center">
                <CpuChipIcon className="w-5 h-5 text-gray-600" />
              </div>
            </div>
            <div className="bg-gray-50 rounded-lg px-4 py-3 text-gray-900 max-w-[80%]">
              <div className="text-sm leading-relaxed">
                <ReactMarkdown
                  components={{
                    p: ({ children }) => <p className="mb-3 last:mb-0 leading-relaxed">{children}</p>,
                    strong: ({ children }) => <strong className="font-semibold text-gray-900">{children}</strong>,
                    ul: ({ children }) => <ul className="list-disc list-inside space-y-2 my-3">{children}</ul>,
                    li: ({ children }) => <li className="text-gray-800 leading-relaxed">{children}</li>,
                    br: () => <br className="mb-1" />,
                    h1: ({ children }) => <h1 className="text-lg font-bold mb-3 mt-4 first:mt-0">{children}</h1>,
                    h2: ({ children }) => <h2 className="text-md font-bold mb-2 mt-3 first:mt-0">{children}</h2>,
                    h3: ({ children }) => <h3 className="text-sm font-bold mb-2 mt-3 first:mt-0">{children}</h3>
                  }}
                >
                  {streamingMessage}
                </ReactMarkdown>
                <span className="animate-pulse text-gray-400">|</span>
              </div>
            </div>
          </div>
        )}

        {/* Î°úÎî© Ïù∏ÎîîÏºÄÏù¥ÌÑ∞ */}
        {isLoading && !isStreaming && (
          <div className="flex justify-start">
            <div className="flex mr-3">
              <div className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center">
                <CpuChipIcon className="w-5 h-5 text-gray-600" />
              </div>
            </div>
            <div className="bg-gray-100 rounded-lg px-4 py-3">
              <div className="flex items-center space-x-1">
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* ÏûÖÎ†• ÏòÅÏó≠ */}
      <div className="border-t border-gray-200 p-4">
        <div className="flex items-end space-x-3">
          <div className="flex-1">
            <textarea
              ref={inputRef}
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Í∏∞ÏÇ¨ ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÍ±∞ÎÇò Ï†úÎ™© ÏàòÏ†ï ÏöîÏ≤≠ÏùÑ Ìï¥Ï£ºÏÑ∏Ïöî..."
              className="w-full p-3 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              rows="3"
              disabled={isLoading}
            />
          </div>
          <button
            onClick={handleSendMessage}
            disabled={!inputMessage.trim() || isLoading}
            className="bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 disabled:bg-gray-300 disabled:cursor-not-allowed transition-colors"
          >
            <PaperAirplaneIcon className="w-5 h-5" />
          </button>
        </div>

        <div className="flex items-center justify-between mt-2 text-xs text-gray-500">
          <span>Shift + EnterÎ°ú Ï§ÑÎ∞îÍøà, EnterÎ°ú Ï†ÑÏÜ°</span>
          <span>{inputMessage.length}/2000</span>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
