import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  PaperAirplaneIcon,
  DocumentPlusIcon,
  XMarkIcon,
  ChatBubbleLeftRightIcon,
  SparklesIcon,
} from "@heroicons/react/24/outline";
import { useChat } from "../hooks/useChat";
import AnimatedChatMessage from "./AnimatedChatMessage";

const ChatInterface = ({ projectId, projectName, promptCards = [] }) => {
  const navigate = useNavigate();
  const [uploadedFile, setUploadedFile] = useState(null);
  const [dragOver, setDragOver] = useState(false);

  const {
    messages,
    inputValue,
    setInputValue,
    copiedMessage,
    isGenerating,
    messagesEndRef,
    inputRef,
    handleSendMessage,
    handleKeyPress,
    handleCopyMessage,
    handleCopyTitle,
    wsConnected,
    wsConnecting,
    wsError,
  } = useChat(projectId, projectName, promptCards);

  const handleFileUpload = (event) => {
    const file = event.target.files?.[0];
    if (file && file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (e) => {
        setInputValue(e.target?.result || "");
        setUploadedFile(file);
      };
      reader.readAsText(file);
    }
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setDragOver(false);
    const files = event.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type === "text/plain") {
        const reader = new FileReader();
        reader.onload = (e) => {
          setInputValue(e.target?.result || "");
          setUploadedFile(file);
        };
        reader.readAsText(file);
      }
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = () => {
    setDragOver(false);
  };

  const removeFile = () => {
    setUploadedFile(null);
    setInputValue("");
  };

  return (
    <div className="flex flex-col h-full bg-gray-50">
      {/* Î©îÏãúÏßÄ ÏòÅÏó≠ */}
      <div className="flex-1 overflow-y-auto px-6 py-6 min-h-0 bg-gray-50">
        <div className="max-w-4xl mx-auto space-y-6">
          {messages.length === 0 ? (
            <div className="flex flex-col items-center justify-center py-24 mt-12">
              <div className="bg-white rounded-2xl shadow-md p-8 max-w-2xl w-full">
                <div className="flex justify-center mb-6">
                  <div className="bg-blue-100 p-3 rounded-full">
                    <ChatBubbleLeftRightIcon className="h-8 w-8 text-blue-600" />
                  </div>
                </div>
                <h2 className="text-xl font-semibold text-center text-gray-800 mb-4">
                  AI Ìó§ÎìúÎùºÏù∏ Ïñ¥ÏãúÏä§ÌÑ¥Ìä∏
                </h2>
                <p className="text-gray-600 text-center mb-6">
                  Î≥∏Î¨∏ÏùÑ ÏûÖÎ†•ÌïòÎ©¥ AIÍ∞Ä ÏµúÏ†ÅÏùò Ìó§ÎìúÎùºÏù∏ÏùÑ Ï†úÏïàÌï¥ ÎìúÎ¶ΩÎãàÎã§.
                  <br></br>
                  Ïò§Î•∏Ï™Ω ÏÇ¨Ïù¥ÎìúÎ∞îÏóêÏÑú ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥ÎìúÎ•º ÏûëÏÑ±ÌïòÏó¨ Ï∞ΩÏùòÏ†ÅÏù∏ Ï†úÎ™©ÏùÑ
                  ÏÉùÏÑ±Ìï¥Î≥¥ÏÑ∏Ïöî.
                </p>

                <div className="space-y-4">
                  <div className="flex items-start p-3 bg-gray-50 rounded-lg">
                    <div className="mr-3 mt-1">
                      <SparklesIcon className="h-5 w-5 text-blue-500" />
                    </div>
                    <div>
