    }

    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// ÏùëÎãµ Ïù∏ÌÑ∞ÏÖâÌÑ∞
api.interceptors.response.use(
  (response) => {
    console.log("API ÏùëÎãµ:", response.status, response.config.url);
    return response;
  },
  (error) => {
    console.error(
      "API Ïò§Î•ò:",
      error.response?.status,
      error.response?.data || error.message
