import React, { useState, useEffect, useMemo, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import {
  FolderOpenIcon,
  PlusIcon,
  EllipsisHorizontalIcon,
  TrashIcon,
  PencilIcon,
  CalendarIcon,
  DocumentTextIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  Squares2X2Icon,
  ListBulletIcon,
  TagIcon,
  BookmarkIcon,
  SparklesIcon,
  ArrowPathIcon,
  FolderIcon,
  XMarkIcon,
  ChevronDownIcon,
  CheckIcon,
  Cog6ToothIcon,
} from "@heroicons/react/24/outline";
import {
  projectAPI,
  handleAPIError,
  DEFAULT_PROJECT_CATEGORIES,
  categoryAPI,
  projectCategoryAPI,
  getCategoryInfo,
  getCategoryColorClasses,
  filterProjects,
  // í†µê³„ ê´€ë ¨ í•¨ìˆ˜ ì œê±°ë¨
  // formatTokenCount,
  // formatFileSize,
  // calculatePromptStats,
  promptCardAPI,
  COLOR_OPTIONS,
} from "../services/api";
import { ProjectListSkeleton, ProjectCardSkeleton } from "./SkeletonLoader";
import CreateProject from "./CreateProject";

const ProjectList = () => {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // í•„í„°ë§ ìƒíƒœ
  const [activeCategory, setActiveCategory] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("created");
  const [viewMode, setViewMode] = useState("grid");
  const [sortDropdownOpen, setSortDropdownOpen] = useState(false);
  const sortDropdownRef = useRef(null);

  // ì¹´í…Œê³ ë¦¬ ìƒíƒœ
  const [userCategories, setUserCategories] = useState([]);
  const [showCategoryModal, setShowCategoryModal] = useState(false);
  const [showCategoryManager, setShowCategoryManager] = useState(false); // ï¿½ï¿½ ë³µì›: ì¹´í…Œê³ ë¦¬ ê´€ë¦¬ state

  // í”„ë¡œì íŠ¸ íŽ¸ì§‘ ìƒíƒœ
  const [editingProject, setEditingProject] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);

  // ë“œëž˜ê·¸ì•¤ë“œë¡­ ìƒíƒœ
  const [draggedProject, setDraggedProject] = useState(null);
  const [dropTargetCategory, setDropTargetCategory] = useState(null);

  // í†µê³„ ìƒíƒœ - ì œê±°ë¨
  // const [projectStats, setProjectStats] = useState({});
  // const [overallStats, setOverallStats] = useState({
  //   totalProjects: 0,
  //   totalPrompts: 0,
  //   totalTokens: 0,
  //   categoriesUsed: 0,
  // });

  const [showCreateModal, setShowCreateModal] = useState(false);

  useEffect(() => {
    loadProjects();
    loadUserCategories();
  }, []);

  const loadUserCategories = async () => {
    try {
      const data = await categoryAPI.getUserCategories();
      setUserCategories(data.categories || []);
    } catch (error) {
      console.error("ì¹´í…Œê³ ë¦¬ ë¡œë“œ ì‹¤íŒ¨:", error);
      setUserCategories(DEFAULT_PROJECT_CATEGORIES);
    }
  };

  const loadProjects = async () => {
    try {
      console.log("ðŸ”„ loadProjects ì‹œìž‘");
      setLoading(true);
      setError(null);
      const data = await projectAPI.getProjects();
      const projectsWithStats = data.projects || [];
      console.log("ðŸ“Š ë¡œë“œëœ í”„ë¡œì íŠ¸ ìˆ˜:", projectsWithStats.length);
      console.log("ðŸ“‹ í”„ë¡œì íŠ¸ ëª©ë¡:", projectsWithStats);

      // í†µê³„ ë¡œë”© ì œê±° - ì§ì ‘ í”„ë¡œì íŠ¸ ì„¤ì •
      setProjects(projectsWithStats);
    } catch (err) {
      const errorInfo = handleAPIError(err);
      setError(errorInfo.message);
      toast.error(errorInfo.message);
    } finally {
      setLoading(false);
    }
  };

  const deleteProject = async (projectId, projectName) => {
