      await promptCardAPI.updatePromptCard(updatedCard.id, updatedCard);
      onCardsChanged?.();
      toast.success('í”„ë¡¬í”„íŠ¸ ì¹´ë“œê°€ ìˆ˜ì •ë˜ì—ˆìŠµë‹ˆë‹¤');
    } catch (error) {
      console.error('ì¹´ë“œ ìˆ˜ì • ì‹¤íŒ¨:', error);
      toast.error('ì¹´ë“œ ìˆ˜ì •ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤');
    }
  };

  const handleFileUploaded = () => {
    toast.success('íŒŒì¼ì´ í”„ë¡œì íŠ¸ ì§€ì‹ì— ì¶”ê°€ë˜ì—ˆìŠµë‹ˆë‹¤');
  };

  const handleTextSubmit = (textData) => {
    console.log('í…ìŠ¤íŠ¸ ì¶”ê°€:', textData);
  };

  const categorizedCards = promptCards.reduce((acc, card) => {
    const category = card.category || 'other';
    if (!acc[category]) acc[category] = [];
    acc[category].push(card);
    return acc;
  }, {});

  const categoryNames = {
    role: 'ì—­í•  ì •ì˜',
    guideline: 'ìž‘ì„± ê°€ì´ë“œë¼ì¸', 
    workflow: 'ì›Œí¬í”Œë¡œìš°',
    output_format: 'ì¶œë ¥ í˜•ì‹',
    few_shot: 'ì˜ˆì‹œ í…œí”Œë¦¿',
    scoring: 'í’ˆì§ˆ í‰ê°€',
    other: 'ê¸°íƒ€ ì„¤ì •'
  };

  return (
    <>
      <div className={`
        relative h-full w-96 bg-white border-l border-gray-200 shadow-sm flex flex-col
        ${!isOpen && 'hidden md:flex'}
      `}>
        {/* í—¤ë” */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-white">
          <div className="flex items-center space-x-2">
            <AdjustmentsHorizontalIcon className="h-5 w-5 text-gray-600" />
            <h3 className="text-sm font-semibold text-gray-900">í”„ë¡œì íŠ¸ ì„¤ì •</h3>
          </div>
          <button
            onClick={onClose}
            className="md:hidden p-1 text-gray-400 hover:text-gray-600 rounded"
          >
            <XMarkIcon className="h-4 w-4" />
          </button>
        </div>

        <div className="flex-1 overflow-y-auto">
          {/* ì§€ì‹ ì—…ë¡œë“œ ì„¹ì…˜ */}
          <div className="p-4 border-b border-gray-100">
            <h4 className="text-sm font-medium text-gray-900 mb-3">í”„ë¡œì íŠ¸ ì§€ì‹ ê´€ë¦¬</h4>

            {/* ì»´íŒ©íŠ¸í•œ ì—…ë¡œë“œ ì˜ì—­ */}
            <div className="space-y-3">
              <FileUploadArea projectId={projectId} onFileUploaded={handleFileUploaded} />

              {/* ì§€ì‹ ëª©ë¡ (ì»´íŒ©íŠ¸í•˜ê²Œ) */}
              <div className="space-y-1">
                <div className="text-xs text-gray-500 mb-2">ì—…ë¡œë“œëœ ì§€ì‹ (4ê°œ)</div>
                {[
                  { id: 1, title: 'ì„œìš¸ê²½ì œ ìŠ¤íƒ€ì¼ ê°€ì´ë“œ', size: '189ìž' },
                  { id: 2, title: 'NotebookLM TTS ê°€ì´ë“œ', size: '262ìž' },
                  { id: 3, title: 'Korea-France Cultural', size: '227ìž' },
                  { id: 4, title: 'Script FranÃ§ais', size: '189ìž' },
                ].map((item) => (
                  <div key={item.id} className="flex items-center justify-between p-2 bg-gray-50 rounded text-xs">
                    <span className="truncate flex-1 text-gray-700">{item.title}</span>
