import { useState, useEffect, useRef, useCallback } from "react";
import { toast } from "react-hot-toast";
import { copyToClipboard } from "../utils/clipboard";
import { generateAPI } from "../services/api";

/**
 * Ï±ÑÌåÖ Í∏∞Îä•ÏùÑ ÏúÑÌïú Îã®ÏàúÌôîÎêú ÌõÖ
 * @param {string} projectId - ÌîÑÎ°úÏ†ùÌä∏ ID
 * @param {string} projectName - ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ
 */
export const useChat = (projectId, projectName) => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [copiedMessage, setCopiedMessage] = useState(null);
  const [isGenerating, setIsGenerating] = useState(false);

  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Ï¥àÍ∏∞ ÌôòÏòÅ Î©îÏãúÏßÄ ÏÑ§Ï†ï
  useEffect(() => {
    const welcomeMessage = {
      id: "welcome",
      type: "assistant",
      content: `ÏïàÎÖïÌïòÏÑ∏Ïöî! ${projectName}Ïùò AI Ï†úÎ™© ÏÉùÏÑ±Í∏∞ÏûÖÎãàÎã§.\n\nÍ∏∞ÏÇ¨ ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏãúÎ©¥ Ï†úÎ™©ÏùÑ ÏÉùÏÑ±Ìï¥ÎìúÎ¶¥Í≤åÏöî.`,
      timestamp: new Date(),
    };
    setMessages([welcomeMessage]);
  }, [projectName]);

  // Î©îÏãúÏßÄ Ï∂îÍ∞Ä Ïãú Ïä§ÌÅ¨Î°§ ÌïòÎã®ÏúºÎ°ú
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  // Î©îÏãúÏßÄ Ï†ÑÏÜ° Î∞è Ï†úÎ™© ÏÉùÏÑ±
  const handleSendMessage = useCallback(
    async (content = inputValue, userRequest = "") => {
      const messageContent = content.trim();
      if (!messageContent) {
        toast.error("ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî");
        return;
      }

      // ÏÇ¨Ïö©Ïûê Î©îÏãúÏßÄ Ï∂îÍ∞Ä
      const userMessage = {
        id: `user-${Date.now()}`,
        type: "user",
        content: messageContent,
        timestamp: new Date(),
      };

      setMessages((prev) => [...prev, userMessage]);
      setInputValue("");
      setIsGenerating(true);

      try {
        console.log("üöÄ Ï†úÎ™© ÏÉùÏÑ± ÏöîÏ≤≠:", { projectId, userRequest });

        // Ïã§Ï†ú Bedrock Claude API Ìò∏Ï∂ú
        const response = await generateAPI.generateTitle(
          projectId,
          messageContent,
          userRequest
        );

        // AI ÏùëÎãµ Î©îÏãúÏßÄ Ï∂îÍ∞Ä
        const aiMessage = {
          id: `ai-${Date.now()}`,
          type: "assistant",
          content:
            response.result || response.titles || "Ï†úÎ™© ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.",
          timestamp: new Date(),
          metadata: {
            promptCount: response.promptCount,
            model: response.model,
          },
        };

        setMessages((prev) => [...prev, aiMessage]);
        toast.success("Ï†úÎ™©Ïù¥ ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§!");
      } catch (error) {
        console.error("‚ùå Ï†úÎ™© ÏÉùÏÑ± Ïã§Ìå®:", error);

        const errorMessage = {
          id: `error-${Date.now()}`,
          type: "assistant",
          content:
            "Ï£ÑÏÜ°Ìï©ÎãàÎã§. Ï†úÎ™© ÏÉùÏÑ± Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.",
          timestamp: new Date(),
          isError: true,
        };

        setMessages((prev) => [...prev, errorMessage]);
        toast.error("Ï†úÎ™© ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§");
      } finally {
        setIsGenerating(false);
      }
    },
    [projectId, inputValue]
  );

  // ÌÇ§Î≥¥Îìú Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
  const handleKeyPress = useCallback(
    (e) => {
      if (e.key === "Enter" && !e.shiftKey) {
        e.preventDefault();
        if (!isGenerating) {
          handleSendMessage();
        }
      }
    },
    [handleSendMessage, isGenerating]
  );

  // Î©îÏãúÏßÄ Î≥µÏÇ¨
  const handleCopyMessage = useCallback(async (messageId, content) => {
    try {
      await copyToClipboard(content);
      setCopiedMessage(messageId);
      toast.success("ÌÅ¥Î¶ΩÎ≥¥ÎìúÏóê Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§");
      setTimeout(() => setCopiedMessage(null), 2000);
    } catch (error) {
      toast.error("Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§");
    }
  }, []);

  // Ï†úÎ™©Îßå Î≥µÏÇ¨
  const handleCopyTitle = useCallback(async (title) => {
    try {
      await copyToClipboard(title);
      toast.success("Ï†úÎ™©Ïù¥ Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§");
    } catch (error) {
      toast.error("Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§");
    }
  }, []);

  return {
    messages,
    inputValue,
    setInputValue,
    copiedMessage,
    isGenerating,
    messagesEndRef,
    inputRef,
    handleSendMessage,
    handleKeyPress,
    handleCopyMessage,
    handleCopyTitle,
  };
};
