import { ChevronDownIcon, SparklesIcon } from "@heroicons/react/24/outline";

const MODELS = [
  // Anthropic Claude Î™®Îç∏Îì§ (Ï∂îÏ≤ú)
  {
    id: "anthropic.claude-3-5-sonnet-20241022-v2:0",
    name: "Claude 3.5 Sonnet v2",
    provider: "Anthropic",
