    new: true,
  },
  {
    id: "anthropic.claude-3-opus-20240229-v1:0",
    name: "Claude 3 Opus",
    provider: "Anthropic",
    category: "premium",
    speed: "ÎäêÎ¶º",
    quality: "ÏµúÍ≥†",
    description: "Ïù¥ÎØ∏ÏßÄÎ•º ÌÖçÏä§Ìä∏ Î∞è ÏΩîÎìúÎ°ú Î≥ÄÌôò, Î≥µÏû°Ìïú Ï∂îÎ°†",
  },
  {
    id: "anthropic.claude-3-sonnet-20240229-v1:0",
    name: "Claude 3 Sonnet",
    provider: "Anthropic",
    category: "balanced",
    speed: "Î≥¥ÌÜµ",
    quality: "Ï¢ãÏùå",
    description: "Í∑†ÌòïÏû°Ìûå ÏÑ±Îä•Í≥º ÏÜçÎèÑ",
  },
  {
    id: "anthropic.claude-3-haiku-20240307-v1:0",
    name: "Claude 3 Haiku",
    provider: "Anthropic",
    category: "fast",
    speed: "Îπ†Î¶Ñ",
    quality: "Î≥¥ÌÜµ",
    description: "ÎåÄÌôî, Ï±ÑÌåÖ ÏµúÏ†ÅÌôî",
  },

  // Amazon Nova Î™®Îç∏Îì§
  {
    id: "amazon.nova-pro-v1:0",
    name: "Nova Pro",
    provider: "Amazon",
    category: "balanced",
    speed: "Îπ†Î¶Ñ",
    quality: "Ï¢ãÏùå",
    description: "ÌÖçÏä§Ìä∏ ÏÉùÏÑ±, ÏΩîÎìú ÏÉùÏÑ±, Î≥µÏû°Ìïú Ï∂îÎ°†",
    recommended: true,
  },
  {
    id: "amazon.nova-lite-v1:0",
    name: "Nova Lite",
    provider: "Amazon",
    category: "fast",
    speed: "Îß§Ïö∞ Îπ†Î¶Ñ",
    quality: "Î≥¥ÌÜµ",
    description: "Í∞ÄÎ≤ºÏö¥ Î™®Îç∏Î°ú Îπ†Î•∏ ÏùëÎãµ, Îã§Íµ≠Ïñ¥ ÏßÄÏõê",
  },
  {
    id: "amazon.nova-micro-v1:0",
    name: "Nova Micro",
    provider: "Amazon",
    category: "fast",
    speed: "Ï¥àÍ≥†ÏÜç",
    quality: "Í∏∞Î≥∏",
    description: "Ï¥àÍ≤ΩÎüâ Î™®Îç∏Î°ú Îß§Ïö∞ Îπ†Î•∏ ÏùëÎãµ",
  },

  // Meta Llama Î™®Îç∏Îì§
  {
    id: "meta.llama3-3-70b-instruct-v1:0",
    name: "Llama 3.3 70B",
    provider: "Meta",
    category: "balanced",
    speed: "Î≥¥ÌÜµ",
    quality: "Ï¢ãÏùå",
    description: "Ìà¥ ÏÇ¨Ïö©, ÏΩîÎìú ÏÉùÏÑ±, Í≥†Í∏â Ï∂îÎ°†",
  },
  {
    id: "meta.llama3-2-3b-instruct-v1:0",
    name: "Llama 3.2 3B",
    provider: "Meta",
    category: "fast",
