    quality: "Î≥¥ÌÜµ",
    description: "Í≤ΩÎüâ Î™®Îç∏Î°ú Îπ†Î•∏ Ï≤òÎ¶¨",
  },
];

const ModelSelector = ({ selectedModel, onModelChange }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [filter, setFilter] = useState("all");

  console.log("ü§ñ ModelSelector Î†åÎçîÎßÅÎê®:", { selectedModel, isOpen });

  const currentModel =
    MODELS.find((model) => model.id === selectedModel) || MODELS[0];

  const filteredModels = MODELS.filter((model) => {
    if (filter === "all") return true;
    return model.provider.toLowerCase() === filter;
  });

  const handleModelSelect = (modelId) => {
    onModelChange(modelId);
    setIsOpen(false);
  };

  return (
    <div className="relative">
      {/* Î™®Îç∏ ÏÑ†ÌÉù Î≤ÑÌäº - Ïª¥Ìå©Ìä∏ Î≤ÑÏ†Ñ */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="flex items-center gap-2 px-2 py-1 bg-gray-50 dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-md hover:bg-gray-100 dark:hover:bg-gray-600 transition-colors text-xs"
        title={`${currentModel.name} (${currentModel.provider})`}
      >
        <span className="text-gray-700 dark:text-gray-300 font-medium">
          {currentModel.name.replace(/Claude |Llama |Nova /, "")}
        </span>
        <ChevronDownIcon
          className={`h-3 w-3 text-gray-500 transition-transform ${
            isOpen ? "rotate-180" : ""
          }`}
        />
      </button>

      {/* ÎìúÎ°≠Îã§Ïö¥ Î©îÎâ¥ */}
      {isOpen && (
        <div className="absolute bottom-full left-0 mb-2 bg-white dark:bg-dark-secondary border border-gray-200 dark:border-gray-600 rounded-lg shadow-lg z-50 max-h-[400px] overflow-hidden min-w-[300px]">
          {/* ÌïÑÌÑ∞ Î≤ÑÌäºÎì§ */}
          <div className="p-3 border-b border-gray-200 dark:border-gray-600">
            <div className="flex gap-1 text-xs">
              {[
                { key: "all", label: "Ï†ÑÏ≤¥" },
                { key: "anthropic", label: "Anthropic" },
                { key: "amazon", label: "Amazon" },
                { key: "meta", label: "Meta" },
              ].map((filterOption) => (
                <button
                  key={filterOption.key}
                  onClick={() => setFilter(filterOption.key)}
                  className={`px-2 py-1 rounded transition-colors ${
                    filter === filterOption.key
                      ? "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200"
                      : "text-gray-600 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-dark-tertiary"
                  }`}
                >
                  {filterOption.label}
                </button>
              ))}
            </div>
          </div>

          {/* Î™®Îç∏ Î™©Î°ù */}
          <div className="max-h-[300px] overflow-y-auto">
            {filteredModels.map((model) => (
              <button
                key={model.id}
                onClick={() => handleModelSelect(model.id)}
                className={`w-full p-3 text-left hover:bg-gray-50 dark:hover:bg-dark-tertiary border-b border-gray-100 dark:border-gray-700 last:border-b-0 transition-colors ${
                  model.id === selectedModel
                    ? "bg-blue-50 dark:bg-blue-900/20"
                    : ""
                }`}
              >
                <div className="flex-1 min-w-0">
                  <div className="mb-1">
                    <span className="font-medium text-gray-900 dark:text-white">
                      {model.name}
                    </span>
                  </div>
                  <div className="text-xs text-gray-500 dark:text-gray-400 mb-1">
                    {model.provider} ‚Ä¢ ÏÜçÎèÑ: {model.speed} ‚Ä¢ ÌíàÏßà:{" "}
                    {model.quality}
                  </div>
                  <div className="text-xs text-gray-600 dark:text-gray-400">
                    {model.description}
                  </div>
                </div>
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Î∞∞Í≤Ω ÌÅ¥Î¶≠ Ïãú Îã´Í∏∞ */}
      {isOpen && (
        <div className="fixed inset-0 z-40" onClick={() => setIsOpen(false)} />
      )}
    </div>
  );
};

export default ModelSelector;
