export const useWebSocket = (projectId) => {
  const [isConnected, setIsConnected] = useState(false);
  const [isConnecting, setIsConnecting] = useState(false);
  const [error, setError] = useState(null);
  const wsRef = useRef(null);
  const reconnectTimeoutRef = useRef(null);
  const reconnectAttempts = useRef(0);
  const maxReconnectAttempts = 5;

  // WebSocket URL (ÌôòÍ≤ΩÎ≥ÄÏàòÎÇò Ïã§Ï†ú Î∞∞Ìè¨Îêú URLÎ°ú ÏÑ§Ï†ï)
  const getWebSocketUrl = useCallback(async () => {
    const wsUrl =
      process.env.REACT_APP_WS_URL ||
      "wss://your-websocket-api.execute-api.us-east-1.amazonaws.com/prod";

    // URL ÌòïÏãù Í≤ÄÏ¶ù Î∞è Ï†ïÍ∑úÌôî
    if (!wsUrl.startsWith("wss://") && !wsUrl.startsWith("ws://")) {
      console.error("ÏûòÎ™ªÎêú WebSocket URL ÌòïÏãù:", wsUrl);
      return null;
    }

    // ÎÅùÏóê Ïä¨ÎûòÏãú Ï†úÍ±∞
    let normalizedUrl = wsUrl.replace(/\/$/, "");

    // Ïù∏Ï¶ù ÌÜ†ÌÅ∞ÏùÑ ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞Î°ú Ï∂îÍ∞Ä
    try {
      const { fetchAuthSession } = await import('aws-amplify/auth');
      const session = await fetchAuthSession();
      const token = session?.tokens?.idToken?.toString();

      if (token) {
        // URLÏóê ÌÜ†ÌÅ∞ÏùÑ ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞Î°ú Ï∂îÍ∞Ä
        normalizedUrl += `?token=${encodeURIComponent(token)}`;
        console.log("‚úÖ WebSocket URLÏóê Ïù∏Ï¶ù ÌÜ†ÌÅ∞ Ï∂îÍ∞ÄÎê®");
      } else {
        console.log("‚ö†Ô∏è Ïù∏Ï¶ù ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏùå - Í≥µÍ∞ú WebSocket Ïó∞Í≤∞ ÏãúÎèÑ");
      }
    } catch (authError) {
      console.log("üìù Ïù∏Ï¶ù ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:", authError.message);
    }

    console.log("WebSocket URL ÌôïÏù∏:", normalizedUrl.replace(/token=[^&]+/, 'token=***'));
    console.log("ÌôòÍ≤ΩÎ≥ÄÏàò REACT_APP_WS_URL:", process.env.REACT_APP_WS_URL);

    return normalizedUrl;
  }, []);

  // WebSocket Ïó∞Í≤∞
  const connect = useCallback(() => {
    // Ïù¥ÎØ∏ Ïó∞Í≤∞ Ï§ëÏù¥Í±∞ÎÇò Ïó∞Í≤∞Îêú Í≤ΩÏö∞ Ï§ëÎ≥µ Ïó∞Í≤∞ Î∞©ÏßÄ
    if (wsRef.current?.readyState === WebSocket.OPEN || 
        wsRef.current?.readyState === WebSocket.CONNECTING) {
      console.log("Ïù¥ÎØ∏ Ïó∞Í≤∞Îêú WebSocketÏù¥ ÏûàÏäµÎãàÎã§ (readyState:", wsRef.current.readyState, ")");
      return;
    }

    // Ïù¥Ï†Ñ Ïó∞Í≤∞Ïù¥ ÏûàÎã§Î©¥ Ï†ïÎ¶¨
    if (wsRef.current) {
      wsRef.current.close();
      wsRef.current = null;
    }

    setIsConnecting(true);
    setError(null);

    try {
      const wsUrl = getWebSocketUrl();

      if (!wsUrl) {
        setError("Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ WebSocket URL");
        setIsConnecting(false);
        return;
      }

      console.log("WebSocket Ïó∞Í≤∞ ÏãúÎèÑ:", wsUrl);
      console.log("Î∏åÎùºÏö∞Ï†Ä WebSocket ÏßÄÏõê:", !!window.WebSocket);

      // Ïó∞Í≤∞ ÏãúÎèÑ ÏãúÍ∞Ñ Í∏∞Î°ù
      window.wsConnectStart = Date.now();

      wsRef.current = new WebSocket(wsUrl);

      // Ïó∞Í≤∞ ÏÉÅÌÉú Î™®ÎãàÌÑ∞ÎßÅ
      const connectionTimeout = setTimeout(() => {
        if (wsRef.current?.readyState === WebSocket.CONNECTING) {
          console.error("WebSocket Ïó∞Í≤∞ ÏãúÍ∞Ñ Ï¥àÍ≥º");
          wsRef.current.close();
          setError("Ïó∞Í≤∞ ÏãúÍ∞ÑÏù¥ Ï¥àÍ≥ºÎêòÏóàÏäµÎãàÎã§");
          setIsConnecting(false);
        }
      }, 10000); // 10Ï¥à ÌÉÄÏûÑÏïÑÏõÉ

      wsRef.current.onopen = (event) => {
        clearTimeout(connectionTimeout);
        console.log("üü¢ WebSocket Ïó∞Í≤∞ ÏÑ±Í≥µ!");
        console.log("- Event:", event);
        console.log("- URL:", wsRef.current?.url);
        console.log("- Protocol:", wsRef.current?.protocol);
        console.log("- Extensions:", wsRef.current?.extensions);
        setIsConnected(true);
        setIsConnecting(false);
        setError(null);
        reconnectAttempts.current = 0;
      };

      wsRef.current.onclose = (event) => {
        clearTimeout(connectionTimeout);
        const connectionDuration = Date.now() - (window.wsConnectStart || 0);

        console.log("üî¥ WebSocket Ïó∞Í≤∞ Ï¢ÖÎ£å:");
        console.log("- Code:", event.code);
        console.log("- Reason:", event.reason || "(no reason provided)");
        console.log("- WasClean:", event.wasClean);
        console.log("- Connection duration:", connectionDuration + "ms");

        setIsConnected(false);
        setIsConnecting(false);

        // Ï¶âÏãú Ï¢ÖÎ£åÎêú Í≤ΩÏö∞ (500ms Ïù¥ÎÇ¥) - ÏÑúÎ≤Ñ Î¨∏Ï†ú
        if (connectionDuration < 500) {
          console.error("‚ö†Ô∏è WebSocketÏù¥ Ï¶âÏãú Ï¢ÖÎ£åÎê® - ÏÑúÎ≤Ñ Î¨∏Ï†ú Í∞ÄÎä•ÏÑ±");

          // ÌäπÏ†ï ÏóêÎü¨ ÏΩîÎìúÏóê Îî∞Î•∏ ÏÑ∏Î∂ÑÌôîÎêú ÏóêÎü¨ Î©îÏãúÏßÄ
          let errorMessage = "ÏÑúÎ≤Ñ Ïó∞Í≤∞Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.";
          switch(event.code) {
            case 1006:
              errorMessage = "ÎπÑÏ†ïÏÉÅÏ†ÅÏù∏ Ïó∞Í≤∞ Ï¢ÖÎ£å (ÎÑ§Ìä∏ÏõåÌÅ¨ Î¨∏Ï†ú Í∞ÄÎä•ÏÑ±)";
              break;
            case 1002:
              errorMessage = "ÌîÑÎ°úÌÜ†ÏΩú Ïò§Î•ò";
              break;
            case 1003:
              errorMessage = "ÏßÄÏõêÌïòÏßÄ ÏïäÎäî Îç∞Ïù¥ÌÑ∞ ÌÉÄÏûÖ";
              break;
            case 1011:
              errorMessage = "ÏÑúÎ≤Ñ ÎÇ¥Î∂Ä Ïò§Î•ò";
              break;
          }

          setError(errorMessage);
          return;
        }

        // ÏûêÎèô Ïû¨Ïó∞Í≤∞ (Ï†ïÏÉÅ Ï¢ÖÎ£åÍ∞Ä ÏïÑÎãå Í≤ΩÏö∞)
        if (
          event.code !== 1000 &&
          reconnectAttempts.current < maxReconnectAttempts
        ) {
          const delay = Math.min(
            1000 * Math.pow(2, reconnectAttempts.current),
            30000
          );
          console.log(
            `üîÑ ${delay}ms ÌõÑ Ïû¨Ïó∞Í≤∞ ÏãúÎèÑ (${
              reconnectAttempts.current + 1
            }/${maxReconnectAttempts})`
          );

          reconnectTimeoutRef.current = setTimeout(() => {
            reconnectAttempts.current++;
            connect();
          }, delay);
        } else if (reconnectAttempts.current >= maxReconnectAttempts) {
          setError("ÏµúÎåÄ Ïû¨Ïó∞Í≤∞ ÏãúÎèÑ ÌöüÏàòÏóê ÎèÑÎã¨ÌñàÏäµÎãàÎã§.");
        }
      };

      wsRef.current.onerror = (error) => {
        clearTimeout(connectionTimeout);
        console.error("üí• WebSocket Ïò§Î•ò:", error);
        console.log("- ReadyState:", wsRef.current?.readyState);
        console.log("- URL:", wsRef.current?.url);
        setError("WebSocket Ïó∞Í≤∞ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§");
        setIsConnecting(false);
      };

    } catch (err) {
      console.error("üí• WebSocket ÏÉùÏÑ± Ïã§Ìå®:", err);
      setError("WebSocket Ïó∞Í≤∞Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§: " + err.message);
      setIsConnecting(false);
    }
  }, [getWebSocketUrl]);

  // WebSocket Ïó∞Í≤∞ Ìï¥Ï†ú
  const disconnect = useCallback(() => {
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = null;
    }

    if (wsRef.current) {
      wsRef.current.close(1000, "Manual disconnect");
      wsRef.current = null;
    }

    setIsConnected(false);
    setIsConnecting(false);
    reconnectAttempts.current = 0;
  }, []);

  // Î©îÏãúÏßÄ Ï†ÑÏÜ°
  const sendMessage = useCallback((message) => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify(message));
      return true;
    } else {
      console.error("WebSocketÏù¥ Ïó∞Í≤∞ÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§");
      setError("WebSocket Ïó∞Í≤∞Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§");
      return false;
    }
  }, []);

  // Ïä§Ìä∏Î¶¨Î∞ç ÏöîÏ≤≠
  const startStreaming = useCallback(
    (userInput, chatHistory = [], promptCards = [], modelId = null, conversationId = null, userSub = null) => {
      if (!isConnected) {
        setError("WebSocket Ïó∞Í≤∞Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§");
        return false;
      }

      const message = {
        action: "stream",
        projectId,
        userInput,
        chat_history: chatHistory,
        prompt_cards: promptCards,
        modelId: modelId,
        conversationId: conversationId,
        userSub: userSub,
      };

      console.log('WebSocket Î©îÏãúÏßÄ Ï†ÑÏÜ°:', {
        action: message.action,
        projectId: message.projectId,
        inputLength: userInput.length,
        historyLength: chatHistory.length,
        promptCardsCount: promptCards.length,
        conversationId: message.conversationId,
        userSub: message.userSub
      });

      return sendMessage(message);
    },
    [isConnected, projectId, sendMessage]
  );

  // Î©îÏãúÏßÄ Î¶¨Ïä§ÎÑà Îì±Î°ù
  const addMessageListener = useCallback((listener) => {
    if (wsRef.current) {
      wsRef.current.addEventListener("message", listener);
    }
  }, []);

  // Î©îÏãúÏßÄ Î¶¨Ïä§ÎÑà Ï†úÍ±∞
  const removeMessageListener = useCallback((listener) => {
    if (wsRef.current) {
      wsRef.current.removeEventListener("message", listener);
    }
  }, []);

  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú Ïó∞Í≤∞, Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ìï¥Ï†ú
  useEffect(() => {
    // Ï¥àÍ∏∞ Ïó∞Í≤∞ ÏßÄÏó∞ÏùÑ ÌÜµÌï¥ React strict mode Ïù¥Ïäà ÌöåÌîº
    const timer = setTimeout(() => {
      connect();
    }, 100);

    return () => {
      clearTimeout(timer);
      disconnect();
    };
  }, []); // ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥ÏùÑ Îπà Î∞∞Ïó¥Î°ú Î≥ÄÍ≤Ω

  // projectId Î≥ÄÍ≤Ω Ïãú Ïû¨Ïó∞Í≤∞ (Ïù¥Ï†Ñ Í∞íÍ≥º ÎπÑÍµêÌïòÏó¨ Ïã§Ï†ú Î≥ÄÍ≤ΩÏãúÏóêÎßå)
  const prevProjectIdRef = useRef(projectId);
  useEffect(() => {
    if (prevProjectIdRef.current !== projectId && isConnected && projectId) {
      console.log('ProjectId Î≥ÄÍ≤ΩÎê®, Ïû¨Ïó∞Í≤∞ Ï§ë:', prevProjectIdRef.current, '->', projectId);
      disconnect();
      setTimeout(connect, 200);
    }
    prevProjectIdRef.current = projectId;
  }, [projectId, isConnected]); // connect, disconnect ÏùòÏ°¥ÏÑ± Ï†úÍ±∞

  return {
    isConnected,
    isConnecting,
    error,
    connect,
    disconnect,
    sendMessage,
    startStreaming,
    addMessageListener,
    removeMessageListener,
  };
};
