    setPromptCards(updatedCards);

    try {
      // ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÍ∞Ä ÏïÑÎãå Í≤ΩÏö∞ÏóêÎßå API Ìò∏Ï∂ú
      const card = promptCards.find((c) => (c.promptId || c.id) === promptId);
      if (card && !card.isTemplate) {
        await promptCardAPI.updatePromptCard(promptId, { enabled });
      }
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      // Ïã§Ìå® Ïãú Î≥µÏõê
      setPromptCards(originalCards);
      const errorInfo = handleAPIError(error);
      toast.error(`Ïπ¥Îìú ÏÉÅÌÉú Î≥ÄÍ≤Ω Ïã§Ìå®: ${errorInfo.message}`);
    }
  };

  // Ïπ¥Îìú ÏÇ≠Ï†ú - ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÎäî ÏÇ≠Ï†ú Î∂àÍ∞Ä
  const handleDeleteCard = async (promptId) => {
    const card = promptCards.find((c) => (c.promptId || c.id) === promptId);

    if (card && card.isTemplate) {
      toast.error("Í∏∞Î≥∏ ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÎäî ÏÇ≠Ï†úÌï† Ïàò ÏóÜÏäµÎãàÎã§.");
      return;
    }

    if (!window.confirm("Ï†ïÎßêÎ°ú Ïù¥ ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥ÎìúÎ•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) {
      return;
    }

    try {
      await promptCardAPI.deletePromptCard(projectId, promptId);
      await loadPromptCards(); // ÏÇ≠Ï†ú ÌõÑ Îã§Ïãú Î°úÎìúÌïòÏó¨ ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÎ°ú Î≥µÏõê
      toast.success("ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥ÎìúÍ∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§!");
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`Ïπ¥Îìú ÏÇ≠Ï†ú Ïã§Ìå®: ${errorInfo.message}`);
    }
  };

  // Ìé∏Ïßë Î™®Îìú ÏãúÏûë
  const handleEditCard = (card) => {
    setEditingCard(card);
    setShowForm(true);
  };

  // Ìèº Ï∑®ÏÜå
  const handleCancelForm = () => {
    setShowForm(false);
    setEditingCard(null);
  };

  // promptCardsÍ∞Ä Î∞∞Ïó¥Ïù∏ÏßÄ ÌôïÏù∏ÌïòÍ≥† ÏïàÏ†ÑÌïòÍ≤å Ï≤òÎ¶¨
  const safePromptCards = Array.isArray(promptCards) ? promptCards : [];

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† - Ï±ÑÌåÖ Ïù∏ÌÑ∞ÌéòÏù¥Ïä§ */}
      <div className="flex-1 relative p-8">
        {/* Ïö∞Ï∏° ÏÉÅÎã® Î©îÎâ¥ Î≤ÑÌäº */}
        <div className="absolute top-6 right-6 z-10">
          <button className="p-3 text-gray-500 hover:text-gray-700 hover:bg-white hover:shadow-lg rounded-lg transition-all duration-200">
            <Bars3Icon className="h-5 w-5" />
          </button>
        </div>

        <div className="h-full bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
          <ChatInterface
            projectId={projectId}
            projectName={projectName}
            promptCards={safePromptCards}
          />
        </div>
      </div>

      {/* Ïö∞Ï∏° ÏÇ¨Ïù¥ÎìúÎ∞î */}
      <div className="w-96 bg-white border-l border-gray-200 flex flex-col p-8">
        <div className="h-full bg-white rounded-xl shadow-sm border border-gray-200 flex flex-col overflow-hidden">
          {/* ÏÇ¨Ïù¥ÎìúÎ∞î Ìó§Îçî */}
          <div className="p-6 border-b border-gray-200 bg-gradient-to-r from-gray-50 to-white">
            <div className="flex items-center justify-between">
              <h2 className="text-xl font-bold text-gray-900">
                ÌîÑÎ°¨ÌîÑÌä∏ ÌÖúÌîåÎ¶ø
              </h2>
              <div className="text-sm text-gray-500 bg-white px-3 py-1 rounded-full border">
                {safePromptCards.filter((card) => card.enabled).length}/
                {safePromptCards.length} ÌôúÏÑ±
              </div>
            </div>
            <p className="text-sm text-gray-600 mt-2">
              Í∞Å Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ ÌîÑÎ°¨ÌîÑÌä∏Î•º ÏÑ§Ï†ïÌïòÏÑ∏Ïöî
            </p>
          </div>

          {/* ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú Î™©Î°ù */}
          <div className="flex-1 overflow-y-auto p-6">
            {loading ? (
              <SidebarSkeleton />
            ) : (
              <div className="space-y-4">
                {safePromptCards.map((card, index) => (
                  <PromptCard
                    key={card.promptId || card.id || index}
                    card={card}
                    onEdit={handleEditCard}
                    onToggle={handleToggleCard}
                    onDelete={handleDeleteCard}
                    stepNumber={index + 1}
                    hideDeleteButton={true} // Í∏∞Î≥∏ ÌÖúÌîåÎ¶øÏù¥ÎØÄÎ°ú ÏÇ≠Ï†ú Î≤ÑÌäº Ïà®ÍπÄ
                  />
                ))}
              </div>
            )}
          </div>

          {/* ÌïòÎã® Ï†ïÎ≥¥ */}
          <div className="p-6 border-t border-gray-200 bg-gradient-to-r from-blue-50 to-indigo-50">
            <div className="text-sm text-gray-700">
              <p className="font-semibold mb-2 flex items-center">
                <span className="text-blue-500 mr-2">üí°</span>
                Í≥†Ï†ï ÌÖúÌîåÎ¶ø ÏãúÏä§ÌÖú
              </p>
              <p className="text-gray-600 leading-relaxed">
                6Í∞úÏùò Í≥†Ï†ï Ïπ¥ÌÖåÍ≥†Î¶¨Î°ú Ï≤¥Í≥ÑÏ†ÅÏù∏ ÌîÑÎ°¨ÌîÑÌä∏ Í¥ÄÎ¶¨Í∞Ä Í∞ÄÎä•Ìï©ÎãàÎã§.
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Ìé∏Ïßë Î™®Îã¨ */}
      <PromptEditModal
        isOpen={showForm}
        onSubmit={handleUpdateCard}
        onCancel={handleCancelForm}
        initialData={editingCard}
      />
    </div>
  );
};

export default PromptCardManager;
