// ğŸ†• ì¸ì¦ API
export const authAPI = {
  // íšŒì›ê°€ì…
  signup: async userData => {
    const response = await api.post("/auth/signup", userData);
    return response.data;
  },
  // ë¡œê·¸ì¸
  signin: async credentials => {
    const response = await api.post("/auth/signin", credentials);
    const {
      accessToken,
      idToken,
      refreshToken
    } = response.data;

    // í† í° ì €ì¥
    localStorage.setItem("accessToken", accessToken);
    localStorage.setItem("idToken", idToken);
    localStorage.setItem("refreshToken", refreshToken);
    return response.data;
  },
  // ë¡œê·¸ì•„ì›ƒ
  signout: async () => {
    try {
      await api.post("/auth/signout");
    } finally {
      // ë¡œì»¬ í† í° ì‚­ì œ
      localStorage.removeItem("accessToken");
      localStorage.removeItem("idToken");
      localStorage.removeItem("refreshToken");
    }
  },
  // í† í° ê°±ì‹ 
  refreshToken: async () => {
    const refreshToken = localStorage.getItem("refreshToken");
    if (!refreshToken) {
      throw new Error("ë¦¬í”„ë ˆì‹œ í† í°ì´ ì—†ìŠµë‹ˆë‹¤");
    }
    const response = await api.post("/auth/refresh", {
      refreshToken
    });
    const {
      accessToken,
      idToken
    } = response.data;

    // ìƒˆ í† í° ì €ì¥
    localStorage.setItem("accessToken", accessToken);
    localStorage.setItem("idToken", idToken);
    return response.data;
  },
  // ì´ë©”ì¼ ì¸ì¦
  verifyEmail: async verificationData => {
    const response = await api.post("/auth/verify", verificationData);
    return response.data;
  },
  // ë¹„ë°€ë²ˆí˜¸ ì°¾ê¸°
  forgotPassword: async email => {
    const response = await api.post("/auth/forgot-password", {
      email
    });
    return response.data;
  },
  // ë¹„ë°€ë²ˆí˜¸ ì¬ì„¤ì •
  confirmPassword: async resetData => {
    const response = await api.post("/auth/confirm-password", resetData);
    return response.data;
  },
  // í˜„ì¬ ì‚¬ìš©ì ì •ë³´ (í† í°ì—ì„œ ì¶”ì¶œ)
  getCurrentUser: () => {
    const token = localStorage.getItem("idToken");
    if (!token) return null;
    try {
      // JWT í† í° ë””ì½”ë”© (ê°„ë‹¨í•œ ë°©ë²• - ì‹¤ì œë¡œëŠ” jwt-decode ë¼ì´ë¸ŒëŸ¬ë¦¬ ì‚¬ìš© ê¶Œì¥)
      const payload = JSON.parse(atob(token.split(".")[1]));
      return {
        email: payload.email,
        name: payload.name,
        sub: payload.sub
      };
    } catch (error) {
      console.error("í† í° ë””ì½”ë”© ì˜¤ë¥˜:", error);
      return null;
    }
  },
  // ë¡œê·¸ì¸ ìƒíƒœ í™•ì¸
  isAuthenticated: () => {
    const token = localStorage.getItem("accessToken");
    if (!token) return false;
    try {
      // í† í° ë§Œë£Œ ì‹œê°„ í™•ì¸
      const payload = JSON.parse(atob(token.split(".")[1]));
      const currentTime = Date.now() / 1000;
      return payload.exp > currentTime;
    } catch (error) {
      return false;
    }
  }
};

