      };
    }

    let retries = 0;

    while (retries < maxRetries) {
      try {
        const status = await generateAPI.getExecutionStatus(executionArn);

        if (status.status === "SUCCEEDED") {
          return {
            success: true,
            data: status,
          };
        } else if (status.status === "FAILED") {
          return {
            success: false,
            error: status.error || "ì‹¤í–‰ì´ ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤",
