// ë¬¸ì¥ì—ì„œ ì£¼ì–´ ì¶”ì¶œ (ê°„ë‹¨í•œ ë°©ì‹)
const extractSubjectFromSentence = (content) => {
  const sentences = content.split(/[.!?]/).filter((s) => s.length > 10);
  if (sentences.length === 0) return "ê¸°ì—…";

  const firstSentence = sentences[0].trim();
  const words = firstSentence.split(" ");

  // ì²« ë²ˆì§¸ ë¬¸ì¥ì˜ ì²« ë²ˆì§¸ ë‹¨ì–´ê°€ ì£¼ì–´ì¸ ê²½ìš°ê°€ ë§ìŒ
  if (words.length > 0) {
    const subject = words[0].replace(/[^ê°€-í£a-zA-Z0-9]/g, "");
    if (subject.length > 1) {
      return subject;
    }
  }

  return "ê¸°ì—…";
};

// ì‹¤ì œ í‚¤ì›Œë“œ ì¶”ì¶œ (ë¹ˆë„ ê¸°ë°˜)
const extractRealKeywords = (content) => {
  const commonWords = [
    "ê·¸ëŠ”",
    "ê·¸ë…€ëŠ”",
    "ì´ëŠ”",
    "ì´ë²ˆ",
    "ìµœê·¼",
    "ë‹¹ì‹œ",
    "í˜„ì¬",
    "ì•ì„œ",
    "ë˜í•œ",
    "í•˜ì§€ë§Œ",
    "ê·¸ëŸ¬ë‚˜",
    "ë”°ë¼ì„œ",
  ];
  const words = content
    .split(/\s+/)
    .map((word) => word.replace(/[^ê°€-í£a-zA-Z0-9]/g, ""))
    .filter((word) => word.length > 1 && !commonWords.includes(word));

  // ë¹ˆë„ ê³„ì‚°
  const frequency = {};
  words.forEach((word) => {
    frequency[word] = (frequency[word] || 0) + 1;
  });

  // ìƒìœ„ í‚¤ì›Œë“œ ë°˜í™˜
  return Object.entries(frequency)
    .sort(([, a], [, b]) => b - a)
    .slice(0, 5)
    .map(([word]) => word);
};

