stats.totalSize+=new Blob([card.content||""]).size;// Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ ÌÜµÍ≥Ñ
const category=card.category||"uncategorized";if(!stats.categories[category]){stats.categories[category]={count:0,tokens:0,size:0};}stats.categories[category].count++;stats.categories[category].tokens+=estimatedTokens;stats.categories[category].size+=new Blob([card.content||""]).size;});return stats;};// ÏÉâÏÉÅ ÏòµÏÖò
