export const filterProjects = (projects, filters) => {
  let filtered = [...projects];

  // Ïπ¥ÌÖåÍ≥†Î¶¨ ÌïÑÌÑ∞
  if (filters.category && filters.category !== "all") {
    filtered = filtered.filter(project => project.category === filters.category);
  }

  // Í≤ÄÏÉâ ÌïÑÌÑ∞
  if (filters.searchQuery) {
    const query = filters.searchQuery.toLowerCase();
    filtered = filtered.filter(project =>
      project.name.toLowerCase().includes(query) ||
      (project.description && project.description.toLowerCase().includes(query)) ||
      (project.tags && project.tags.some(tag => tag.toLowerCase().includes(query)))
    );
  }

  // Ï†ïÎ†¨
  filtered.sort((a, b) => {
    switch (filters.sortBy) {
      case "name":
        return a.name.localeCompare(b.name, "ko");
      case "updated":
        return new Date(b.updatedAt || b.createdAt) - new Date(a.updatedAt || a.createdAt);
      case "created":
      default:
        return new Date(b.createdAt) - new Date(a.createdAt);
    }
  });

  return filtered;
};

export const formatTokenCount = (count) => {
  if (!count || count === 0) return "0";
  if (count < 1000) return count.toString();
  if (count < 1000000) return `${(count / 1000).toFixed(1)}K`;
  return `${(count / 1000000).toFixed(1)}M`;
};

export const formatFileSize = (bytes) => {
  if (!bytes || bytes === 0) return "0 B";
  const sizes = ["B", "KB", "MB", "GB"];
  const i = Math.floor(Math.log(bytes) / Math.log(1024));
  return `${(bytes / Math.pow(1024, i)).toFixed(1)} ${sizes[i]}`;
};

export const calculatePromptStats = (promptCards) => {
  if (!promptCards || promptCards.length === 0) {
    return {
      totalCards: 0,
      totalTokens: 0,
      totalSize: 0,
      categories: {},
    };
  }

  const stats = {
    totalCards: promptCards.length,
    totalTokens: 0,
    totalSize: 0,
    categories: {},
  };

  promptCards.forEach(card => {
    // ÌÜ†ÌÅ∞ Ïàò Í≥ÑÏÇ∞ (ÎåÄÎûµÏ†ÅÏúºÎ°ú Í∏ÄÏûê Ïàò / 4)
    const contentLength = (card.content || "").length;
    const estimatedTokens = Math.ceil(contentLength / 4);
    stats.totalTokens += estimatedTokens;

    // ÌååÏùº ÌÅ¨Í∏∞ Í≥ÑÏÇ∞ (UTF-8 Í∏∞Ï§Ä)
    stats.totalSize += new Blob([card.content || ""]).size;

    // Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ ÌÜµÍ≥Ñ
    const category = card.category || "uncategorized";
    if (!stats.categories[category]) {
      stats.categories[category] = { count: 0, tokens: 0, size: 0 };
    }
    stats.categories[category].count++;
    stats.categories[category].tokens += estimatedTokens;
    stats.categories[category].size += new Blob([card.content || ""]).size;
  });

  return stats;
};

