import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  PaperAirplaneIcon,
  ArrowLeftIcon,
  DocumentPlusIcon,
  XMarkIcon,
} from "@heroicons/react/24/outline";
import { useChat } from "../hooks/useChat";
import ChatMessage from "./chat/ChatMessage";

const ChatInterface = ({ projectId, projectName, promptCards = [] }) => {
  const navigate = useNavigate();
  const [uploadedFile, setUploadedFile] = useState(null);
  const [dragOver, setDragOver] = useState(false);

  const {
    messages,
    inputValue,
    setInputValue,
    copiedMessage,
    isGenerating,
    messagesEndRef,
    inputRef,
    handleSendMessage,
    handleKeyPress,
    handleCopyMessage,
    handleCopyTitle,
  } = useChat(projectId, projectName, promptCards);

  const handleFileUpload = (event) => {
    const file = event.target.files?.[0];
    if (file && file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (e) => {
        setInputValue(e.target?.result || "");
        setUploadedFile(file);
      };
      reader.readAsText(file);
    }
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setDragOver(false);
    const files = event.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type === "text/plain") {
        const reader = new FileReader();
        reader.onload = (e) => {
          setInputValue(e.target?.result || "");
          setUploadedFile(file);
        };
        reader.readAsText(file);
      }
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = () => {
    setDragOver(false);
  };

  const removeFile = () => {
    setUploadedFile(null);
    setInputValue("");
  };

  return (
    <div className="flex flex-col h-full bg-gray-50">
      {/* Ìó§Îçî */}
      <div className="flex-shrink-0 bg-white border-b border-gray-200 px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => navigate("/")}
              className="text-gray-500 hover:text-gray-700 transition-colors p-2 hover:bg-gray-100 rounded-lg"
            >
              <ArrowLeftIcon className="h-5 w-5" />
            </button>
            <div>
              <h1 className="text-xl font-semibold text-gray-800">
                {projectName}
              </h1>
            </div>
          </div>
          <div className="flex items-center gap-3">
            <div className="flex items-center gap-2">
              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
              <span className="text-sm text-gray-600">Ï§ÄÎπÑÏôÑÎ£å</span>
            </div>
          </div>
        </div>
      </div>

      {/* Î©îÏãúÏßÄ ÏòÅÏó≠ */}
      <div className="flex-1 overflow-y-auto px-6 py-4 min-h-0">
        <div className="max-w-4xl mx-auto space-y-4">
          {messages.map((message) => (
            <ChatMessage
              key={message.id}
              message={message}
              onCopyMessage={handleCopyMessage}
              onCopyTitle={handleCopyTitle}
              copiedMessage={copiedMessage}
            />
          ))}
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* ÏûÖÎ†• ÏòÅÏó≠ */}
      <div className="flex-shrink-0 bg-white border-t border-gray-200 px-6 py-4">
        <div className="max-w-4xl mx-auto">
          {/* ÏóÖÎ°úÎìúÎêú ÌååÏùº ÌëúÏãú */}
          {uploadedFile && (
            <div className="mb-3 p-3 bg-blue-50 border border-blue-200 rounded-lg flex items-center justify-between">
              <div className="flex items-center gap-2">
                <DocumentPlusIcon className="h-4 w-4 text-blue-600" />
                <span className="text-sm text-blue-700">
                  {uploadedFile.name}
                </span>
              </div>
              <button
                onClick={removeFile}
                className="text-blue-600 hover:text-blue-800 p-1"
              >
                <XMarkIcon className="h-4 w-4" />
              </button>
            </div>
          )}

          <div
            className={`flex items-center space-x-4 ${
              dragOver ? "opacity-60" : ""
            }`}
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
          >
            <div className="flex-1 relative">
              <textarea
                ref={inputRef}
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
                className={`w-full px-4 py-2 border rounded-lg focus:outline-none resize-none transition-colors ${
                  dragOver
                    ? "border-blue-400 bg-blue-50 border-dashed"
                    : "border-gray-300 focus:border-blue-500 bg-white"
                }`}
                rows={1}
                style={{ minHeight: "42px", lineHeight: "1.5" }}
                disabled={isGenerating}
              />

              {/* Í∏ÄÏûê Ïàò Ïπ¥Ïö¥ÌÑ∞ */}
              <div className="flex items-center justify-between mt-2 text-xs text-gray-500">
                <div className="flex items-center gap-4">
                  <span
                    className={`${
                      inputValue.length < 50 ? "text-gray-500" : "text-gray-600"
                    }`}
                  >
                    {inputValue.length}Ïûê
                  </span>
                  {inputValue.length < 50 && (
                    <span className="text-gray-500">üìù 50Ïûê Ïù¥ÏÉÅ Í∂åÏû•</span>
                  )}
                  {inputValue.length >= 50 && inputValue.length < 200 && (
                    <span className="text-gray-600">‚úÖ Ï†ÅÏ†àÌïú Í∏∏Ïù¥</span>
                  )}
                  {inputValue.length >= 200 && (
                    <span className="text-gray-600">üéØ Ï∂©Î∂ÑÌïú Í∏∏Ïù¥</span>
                  )}
                </div>
                <div className="text-gray-400">Shift + EnterÎ°ú Ï§ÑÎ∞îÍøà</div>
              </div>
            </div>

            {/* Ï†ÑÏÜ° Î≤ÑÌäº */}
            <button
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || isGenerating}
              className="flex-shrink-0 bg-blue-600 text-white px-4 rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center justify-center gap-2 h-[42px] self-start"
            >
              {isGenerating ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                  <span>Ï≤òÎ¶¨Ï§ë...</span>
                </>
              ) : (
                <>
                  <PaperAirplaneIcon className="h-4 w-4" />
                  <span>Ï†ÑÏÜ°</span>
                </>
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
