import axios from "axios";

// ğŸ”§ API ê¸°ë³¸ ì„¤ì • ê°œì„ 
const API_BASE_URL =
  process.env.REACT_APP_API_URL ||
  "https://gcm3qzoy04.execute-api.us-east-1.amazonaws.com/prod";

// Axios ì¸ìŠ¤í„´ìŠ¤ ê°œì„ 
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { "Content-Type": "application/json" },
  timeout: 300000, // ğŸ”§ ìˆ˜ì •: 5ë¶„ìœ¼ë¡œ ë‹¨ì¶• (ê¸°ì¡´ 15ë¶„ì€ ë„ˆë¬´ ê¸¸ì–´ì„œ 504 íƒ€ì„ì•„ì›ƒ ë°œìƒ)
});

// ìš”ì²­ ì¸í„°ì…‰í„°
api.interceptors.request.use((config) => {
  console.log("API ìš”ì²­:", config.method?.toUpperCase(), config.url);
  return config;
});

// ì‘ë‹µ ì¸í„°ì…‰í„°
api.interceptors.response.use(
  (response) => {
    console.log("API ì‘ë‹µ:", response.status, response.config.url);
    return response;
  },
  (error) => {
    console.error("API ì˜¤ë¥˜ ìƒì„¸:", {
      status: error.response?.status,
      message: error.message,
      code: error.code,
      url: error.config?.url,
      data: error.response?.data,
    });
    return Promise.reject(error);
  }
);

// =============================================================================
// 1. í”„ë¡œì íŠ¸ API
// =============================================================================

export const projectAPI = {
  getProjects: async () => {
    const response = await api.get("/projects");
