  },
};

// =============================================================================
// 2. í”„ë¡¬í”„íŠ¸ ì¹´ë“œ API
// =============================================================================

export const promptCardAPI = {
  getPromptCards: async (
    projectId,
    includeContent = false,
    includeStats = false
