  },

  reorderPromptCards: async (projectId, reorderData) => {
    // ë°±ì—”ë“œì—ì„œ ë³„ë„ì˜ reorder APIê°€ ì—†ìœ¼ë¯€ë¡œ ê°œë³„ ì—…ë°ì´íŠ¸ë¡œ ì²˜ë¦¬
    // reorderData = [{ promptId, stepOrder }, ...]
    const updatePromises = reorderData.map(({ promptId, stepOrder }) =>
      api.put(`/prompts/${projectId}/${promptId}`, { stepOrder })
    );

    const responses = await Promise.all(updatePromises);
    return {
      message: "í”„ë¡¬í”„íŠ¸ ì¹´ë“œ ìˆœì„œê°€ ì—…ë°ì´íŠ¸ë˜ì—ˆìŠµë‹ˆë‹¤.",
      updatedCards: responses.map((r) => r.data),
    };
  },
};

// =============================================================================
// 3. ì œëª© ìƒì„± API
// =============================================================================

// ğŸ”§ ê°œì„ ëœ generateAPI
export const generateAPI = {
  generateTitle: async (projectId, data) => {
    console.log("ëŒ€í™” ìƒì„± ìš”ì²­ ì‹œì‘:", {
      projectId,
      inputLength: data.userInput.length,
      historyLength: data.chat_history?.length || 0,
      timestamp: new Date().toISOString(),
    });

    try {
      const response = await api.post(`/projects/${projectId}/generate`, data);

      console.log("ëŒ€í™” ìƒì„± ì„±ê³µ:", {
        status: response.status,
        mode: response.data.mode,
        message: response.data.message,
        timestamp: new Date().toISOString(),
      });

      return response.data;
    } catch (error) {
      console.error("ëŒ€í™” ìƒì„± ì‹¤íŒ¨:", {
        code: error.code,
        message: error.message,
        status: error.response?.status,
        responseData: error.response?.data,
        timestamp: new Date().toISOString(),
      });
      throw error;
    }
  },

  // ğŸ”§ SSE ìŠ¤íŠ¸ë¦¬ë° API ì™„ì „ ê°œì„  - EventSource ì‚¬ìš©
  generateTitleStream: async (
    projectId,
    data,
    onChunk,
    onError,
    onComplete
  ) => {
    console.log("ğŸ”„ SSE ìŠ¤íŠ¸ë¦¬ë° ëŒ€í™” ìƒì„± ìš”ì²­ ì‹œì‘:", {
      projectId,
      inputLength: data.userInput.length,
      historyLength: data.chat_history?.length || 0,
      timestamp: new Date().toISOString(),
    });

    // ğŸ”§ ë°©ë²• 1: EventSourceë¥¼ ì‚¬ìš©í•œ ì§„ì§œ SSE (GET ìš”ì²­)
    try {
      const queryParams = new URLSearchParams({
        userInput: data.userInput,
        chat_history: JSON.stringify(data.chat_history || []),
        projectId: projectId,
      });

      const eventSource = new EventSource(
        `${API_BASE_URL}/projects/${projectId}/generate/stream?${queryParams}`
      );

      let fullResponse = "";

      eventSource.onmessage = function (event) {
        try {
          const eventData = JSON.parse(event.data);

          if (eventData.type === "chunk" && eventData.response) {
            fullResponse += eventData.response;

            if (onChunk) {
              onChunk(eventData.response, eventData);
            }
          } else if (eventData.type === "complete") {
            console.log("âœ… SSE ìŠ¤íŠ¸ë¦¬ë° ì™„ë£Œ:", {
              fullResponseLength: fullResponse.length,
              timestamp: new Date().toISOString(),
            });

            eventSource.close();

            if (onComplete) {
              onComplete({
                result: fullResponse,
                timestamp: eventData.timestamp || new Date().toISOString(),
              });
            }
          } else if (eventData.type === "error") {
            console.error("âŒ SSE ìŠ¤íŠ¸ë¦¬ë° ì˜¤ë¥˜:", eventData.error);
            eventSource.close();

            if (onError) {
              onError(new Error(eventData.error));
            }
          }
        } catch (parseError) {
          console.error("SSE ë°ì´í„° íŒŒì‹± ì˜¤ë¥˜:", parseError);
        }
      };

      eventSource.onerror = function (error) {
        console.error("SSE ì—°ê²° ì˜¤ë¥˜:", error);
        eventSource.close();

        // ğŸ”§ SSE ì‹¤íŒ¨ ì‹œ í´ë°± - fetch ê¸°ë°˜ ìŠ¤íŠ¸ë¦¬ë°ìœ¼ë¡œ ì „í™˜
        console.log("ğŸ”§ EventSource ì‹¤íŒ¨, fetch ê¸°ë°˜ ìŠ¤íŠ¸ë¦¬ë°ìœ¼ë¡œ í´ë°±...");
        return generateTitleStreamFallback(
          projectId,
          data,
          onChunk,
          onError,
          onComplete
        );
      };

      // ì—°ê²° íƒ€ì„ì•„ì›ƒ ì„¤ì • (2ë¶„)
      setTimeout(() => {
        if (eventSource.readyState !== EventSource.CLOSED) {
          console.log("ğŸ• SSE ì—°ê²° íƒ€ì„ì•„ì›ƒ, í´ë°±ìœ¼ë¡œ ì „í™˜");
          eventSource.close();
          return generateTitleStreamFallback(
            projectId,
            data,
            onChunk,
            onError,
            onComplete
          );
        }
      }, 120000);

      return { eventSource }; // EventSource ê°ì²´ ë°˜í™˜í•˜ì—¬ í•„ìš”ì‹œ ì™¸ë¶€ì—ì„œ ì œì–´ ê°€ëŠ¥
    } catch (sseError) {
      console.error("EventSource ìƒì„± ì‹¤íŒ¨:", sseError);
      // ì¦‰ì‹œ í´ë°±ìœ¼ë¡œ ì „í™˜
      return generateTitleStreamFallback(
        projectId,
        data,
        onChunk,
        onError,
        onComplete
      );
    }
  },

  // ğŸ”§ í´ë°± í•¨ìˆ˜: fetch ê¸°ë°˜ ìŠ¤íŠ¸ë¦¬ë° (ê¸°ì¡´ ë°©ì‹ ê°œì„ )
  generateTitleStreamFallback: async (
    projectId,
    data,
    onChunk,
    onError,
    onComplete
  ) => {
    console.log("ğŸ”„ í´ë°± ìŠ¤íŠ¸ë¦¬ë° ì‹œì‘ (fetch ê¸°ë°˜):", {
      projectId,
      timestamp: new Date().toISOString(),
    });

    try {
      const response = await fetch(
        `${API_BASE_URL}/projects/${projectId}/generate/stream`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Accept: "text/event-stream",
          },
          body: JSON.stringify(data),
        }
      );

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let fullResponse = "";

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value, { stream: true });
        const messages = chunk.split("\n\n");

        for (const message of messages) {
          if (message.trim().startsWith("data: ")) {
            try {
              const jsonStr = message.trim().slice(6);
              const eventData = JSON.parse(jsonStr);

              if (eventData.type === "chunk" && eventData.response) {
                fullResponse += eventData.response;

                if (onChunk) {
                  onChunk(eventData.response, eventData);
                }
              } else if (eventData.type === "complete") {
                console.log("âœ… í´ë°± ìŠ¤íŠ¸ë¦¬ë° ì™„ë£Œ:", {
                  fullResponseLength: fullResponse.length,
                  timestamp: new Date().toISOString(),
                });

                if (onComplete) {
                  onComplete({
                    result: fullResponse,
                    timestamp: eventData.timestamp || new Date().toISOString(),
                  });
                }
                return { result: fullResponse };
              } else if (eventData.type === "error") {
                throw new Error(eventData.error);
              }
            } catch (parseError) {
              console.warn("í´ë°± ìŠ¤íŠ¸ë¦¬ë° ë°ì´í„° íŒŒì‹± ì˜¤ë¥˜:", parseError);
            }
          }
        }
      }
    } catch (fetchError) {
      console.error("í´ë°± ìŠ¤íŠ¸ë¦¬ë° ì‹¤íŒ¨:", fetchError);

      // ğŸ”§ ìµœì¢… í´ë°±: ì¼ë°˜ API í˜¸ì¶œ
      console.log("ğŸ”„ ìµœì¢… í´ë°±: ì¼ë°˜ API í˜¸ì¶œ...");
      try {
        const fallbackResponse = await api.post(
          `/projects/${projectId}/generate`,
          data
        );

        console.log("âœ… ìµœì¢… í´ë°± ì„±ê³µ:", {
          mode: fallbackResponse.data.mode,
          timestamp: new Date().toISOString(),
        });

        // ì¼ë°˜ ì‘ë‹µì„ ìŠ¤íŠ¸ë¦¬ë° í˜•íƒœë¡œ ì‹œë®¬ë ˆì´ì…˜
        if (fallbackResponse.data.result && onChunk) {
          const fullText = fallbackResponse.data.result;
          const words = fullText.split(" ");

          for (let i = 0; i < words.length; i++) {
            const word = words[i] + (i < words.length - 1 ? " " : "");
            onChunk(word, { content: word, type: "simulation" });
            await new Promise((resolve) => setTimeout(resolve, 50));
          }
        }

        if (onComplete) {
          onComplete({
            result: fallbackResponse.data.result,
            model_info: fallbackResponse.data.model_info,
            performance_metrics: fallbackResponse.data.performance_metrics,
            timestamp: new Date().toISOString(),
          });
        }

        return fallbackResponse.data;
      } catch (finalError) {
        console.error("âŒ ìµœì¢… í´ë°±ë„ ì‹¤íŒ¨:", finalError);

        if (onError) {
          onError(
            new Error(
              "ëª¨ë“  API ì—”ë“œí¬ì¸íŠ¸ê°€ ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤. ì„œë²„ ìƒíƒœë¥¼ í™•ì¸í•´ì£¼ì„¸ìš”."
            )
          );
        }

        throw new Error(
          "ì„œë¹„ìŠ¤ë¥¼ ì‚¬ìš©í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤. ì ì‹œ í›„ ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”."
        );
      }
    }
  },

  getExecutionStatus: async (executionArn) => {
    return {
      status: "SUCCEEDED",
      output: "{}",
    };
  },
};

// =============================================================================
// 4. ì±„íŒ… API (generate APIë¡œ ë¦¬ë‹¤ì´ë ‰íŠ¸)
// =============================================================================

export const chatAPI = {
