import React, { useState, useEffect, useMemo, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import {
  FolderOpenIcon,
  PlusIcon,
  EllipsisHorizontalIcon,
  TrashIcon,
  PencilIcon,
  CalendarIcon,
  DocumentTextIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  Squares2X2Icon,
  ListBulletIcon,
  TagIcon,
  BookmarkIcon,
  SparklesIcon,
  ArrowPathIcon,
  FolderIcon,
  XMarkIcon,
  ChevronDownIcon,
  CheckIcon,
  Cog6ToothIcon,
} from "@heroicons/react/24/outline";
import {
  projectAPI,
  handleAPIError,
  DEFAULT_PROJECT_CATEGORIES,
  categoryAPI,
  projectCategoryAPI,
  getCategoryInfo,
  getCategoryColorClasses,
  filterProjects,
  formatTokenCount,
  formatFileSize,
  calculatePromptStats,
  promptCardAPI,
  COLOR_OPTIONS,
} from "../services/api";
import { ProjectListSkeleton, ProjectCardSkeleton } from "./SkeletonLoader";
import CreateProject from "./CreateProject";

const ProjectList = () => {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // í•„í„°ë§ ìƒíƒœ
  const [activeCategory, setActiveCategory] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("created");
  const [viewMode, setViewMode] = useState("grid");
  const [sortDropdownOpen, setSortDropdownOpen] = useState(false);
  const sortDropdownRef = useRef(null);

  // ì¹´í…Œê³ ë¦¬ ìƒíƒœ
  const [userCategories, setUserCategories] = useState([]);
  const [showCategoryModal, setShowCategoryModal] = useState(false);
  const [showCategoryManager, setShowCategoryManager] = useState(false); // ï¿½ï¿½ ë³µì›: ì¹´í…Œê³ ë¦¬ ê´€ë¦¬ state

  // í”„ë¡œì íŠ¸ íŽ¸ì§‘ ìƒíƒœ
  const [editingProject, setEditingProject] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);

  // ë“œëž˜ê·¸ì•¤ë“œë¡­ ìƒíƒœ
  const [draggedProject, setDraggedProject] = useState(null);
  const [dropTargetCategory, setDropTargetCategory] = useState(null);

  // í†µê³„ ìƒíƒœ
  const [projectStats, setProjectStats] = useState({});
  const [overallStats, setOverallStats] = useState({
    totalProjects: 0,
    totalPrompts: 0,
    totalTokens: 0,
    categoriesUsed: 0,
  });

  const [showCreateModal, setShowCreateModal] = useState(false);

  useEffect(() => {
    loadProjects();
    loadUserCategories();
  }, []);

  const loadUserCategories = async () => {
    try {
      const data = await categoryAPI.getUserCategories();
      setUserCategories(data.categories || []);
    } catch (error) {
      console.error("ì¹´í…Œê³ ë¦¬ ë¡œë“œ ì‹¤íŒ¨:", error);
      setUserCategories(DEFAULT_PROJECT_CATEGORIES);
    }
  };

  const loadProjects = async () => {
    try {
      console.log("ðŸ”„ loadProjects ì‹œìž‘");
      setLoading(true);
      setError(null);
      const data = await projectAPI.getProjects();
      const projectsWithStats = data.projects || [];
      console.log("ðŸ“Š ë¡œë“œëœ í”„ë¡œì íŠ¸ ìˆ˜:", projectsWithStats.length);
      console.log("ðŸ“‹ í”„ë¡œì íŠ¸ ëª©ë¡:", projectsWithStats);

      // ê° í”„ë¡œì íŠ¸ë³„ í”„ë¡¬í”„íŠ¸ í†µê³„ ë¡œë“œ
      const statsPromises = projectsWithStats.map(async (project) => {
        try {
          const promptsData = await promptCardAPI.getPromptCards(
            project.projectId,
            true,
            true
          );
          const stats = calculatePromptStats(promptsData.promptCards || []);
          return { projectId: project.projectId, stats };
        } catch (err) {
          console.error(`í”„ë¡œì íŠ¸ ${project.projectId} í†µê³„ ë¡œë“œ ì‹¤íŒ¨:`, err);
          return { projectId: project.projectId, stats: null };
        }
      });

      const statsResults = await Promise.all(statsPromises);
      const statsMap = {};
      statsResults.forEach(({ projectId, stats }) => {
        statsMap[projectId] = stats;
      });

      setProjectStats(statsMap);
      setProjects(projectsWithStats);

      // ì „ì²´ í†µê³„ ê³„ì‚°
      const overall = {
        totalProjects: projectsWithStats.length,
        totalPrompts: Object.values(statsMap).reduce(
          (sum, stats) => sum + (stats?.totalCards || 0),
          0
        ),
        totalTokens: Object.values(statsMap).reduce(
          (sum, stats) => sum + (stats?.totalTokens || 0),
          0
        ),
        categoriesUsed: new Set(
          projectsWithStats.map((p) => p.category).filter(Boolean)
        ).size,
      };
      setOverallStats(overall);
    } catch (err) {
      const errorInfo = handleAPIError(err);
      setError(errorInfo.message);
      toast.error(errorInfo.message);
    } finally {
      setLoading(false);
    }
  };

  const deleteProject = async (projectId, projectName) => {
    if (!window.confirm(`"${projectName}" í”„ë¡œì íŠ¸ë¥¼ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?`)) {
      return;
    }

    // ë‚™ê´€ì  ì—…ë°ì´íŠ¸: ì¦‰ì‹œ UIì—ì„œ í”„ë¡œì íŠ¸ ì œê±°
    const originalProjects = [...projects];
    const updatedProjects = projects.filter((p) => p.projectId !== projectId);
    setProjects(updatedProjects);

    // í†µê³„ë„ ì—…ë°ì´íŠ¸
    const newStats = { ...overallStats };
    newStats.totalProjects = Math.max(0, newStats.totalProjects - 1);
    setOverallStats(newStats);

    // ì¦‰ì‹œ ì„±ê³µ í† ìŠ¤íŠ¸ í‘œì‹œ
    toast.success("í”„ë¡œì íŠ¸ê°€ ì‚­ì œë˜ì—ˆìŠµë‹ˆë‹¤");

    try {
      // ë°±ê·¸ë¼ìš´ë“œì—ì„œ ì‹¤ì œ ì‚­ì œ ì§„í–‰
      await projectAPI.deleteProject(projectId);
      console.log("í”„ë¡œì íŠ¸ ì‚­ì œ ì„±ê³µ:", projectId);
    } catch (err) {
      console.error("í”„ë¡œì íŠ¸ ì‚­ì œ ì˜¤ë¥˜:", err);

      // ì‹¤íŒ¨ ì‹œ ì›ëž˜ ìƒíƒœë¡œ ë³µì›
      setProjects(originalProjects);
      setOverallStats(overallStats); // ì›ëž˜ í†µê³„ë¡œ ë³µì›

      let errorMessage = "í”„ë¡œì íŠ¸ ì‚­ì œì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤";

      if (err.response?.status === 403) {
        errorMessage =
          "ì‚­ì œ ê¶Œí•œì´ ì—†ìŠµë‹ˆë‹¤. S3 íŒŒì¼ ì‚­ì œ ê¶Œí•œì„ í™•ì¸í•´ì£¼ì„¸ìš”.";
      } else if (err.response?.status === 404) {
        errorMessage = "ì´ë¯¸ ì‚­ì œëœ í”„ë¡œì íŠ¸ìž…ë‹ˆë‹¤";
        // 404ì˜ ê²½ìš° ì‹¤ì œë¡œëŠ” ì‚­ì œëœ ê²ƒì´ë¯€ë¡œ ë³µì›í•˜ì§€ ì•ŠìŒ
        return;
      } else if (err.response) {
        errorMessage =
          err.response.data?.message || `ì„œë²„ ì˜¤ë¥˜ (${err.response.status})`;
      } else if (err.request) {
        errorMessage = "ë„¤íŠ¸ì›Œí¬ ì˜¤ë¥˜: CORS ë˜ëŠ” ì—°ê²° ë¬¸ì œ";
      }

      toast.error(errorMessage);
    }
  };

  // í”„ë¡œì íŠ¸ ì¹´í…Œê³ ë¦¬ ë³€ê²½
  const changeProjectCategory = async (projectId, categoryId) => {
    try {
      await projectCategoryAPI.updateProjectCategory(projectId, categoryId);
      toast.success("ì¹´í…Œê³ ë¦¬ê°€ ë³€ê²½ë˜ì—ˆìŠµë‹ˆë‹¤");
      loadProjects();
    } catch (err) {
      toast.error("ì¹´í…Œê³ ë¦¬ ë³€ê²½ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤");
    }
  };

  // í”„ë¡œì íŠ¸ íŽ¸ì§‘ í•¨ìˆ˜ë“¤
  const handleEditProject = (project) => {
    setEditingProject(project);
    setShowEditModal(true);
  };

  const handleUpdateProject = async (projectData) => {
    try {
      await projectAPI.updateProject(editingProject.projectId, projectData);
      toast.success("í”„ë¡œì íŠ¸ê°€ ìˆ˜ì •ë˜ì—ˆìŠµë‹ˆë‹¤");
      setShowEditModal(false);
      setEditingProject(null);
      loadProjects();
    } catch (err) {
      const errorInfo = handleAPIError(err);
      toast.error(`í”„ë¡œì íŠ¸ ìˆ˜ì • ì‹¤íŒ¨: ${errorInfo.message}`);
    }
  };

  const handleCancelEdit = () => {
    setShowEditModal(false);
    setEditingProject(null);
  };

  // ë“œëž˜ê·¸ì•¤ë“œë¡­ í•¸ë“¤ëŸ¬ë“¤
  const handleDragStart = (e, project) => {
    setDraggedProject(project);
    if (e.dataTransfer) {
      e.dataTransfer.effectAllowed = "move";
      e.dataTransfer.setData("text/html", e.target.outerHTML);
    }
  };

  const handleDragOver = (e, categoryId) => {
    e.preventDefault();
    if (e.dataTransfer) {
      e.dataTransfer.dropEffect = "move";
    }
    setDropTargetCategory(categoryId);
  };

  const handleDragLeave = (e) => {
    // ë“œëž˜ê·¸ê°€ ì¹´í…Œê³ ë¦¬ ì˜ì—­ì„ ë²—ì–´ë‚  ë•Œ
    if (!e.currentTarget.contains(e.relatedTarget)) {
      setDropTargetCategory(null);
    }
  };

  const handleDrop = async (e, categoryId) => {
    e.preventDefault();
    setDropTargetCategory(null);

    if (draggedProject) {
      // "ì „ì²´" ì¹´í…Œê³ ë¦¬ë¡œ ë“œëž˜ê·¸í•˜ë©´ ì¹´í…Œê³ ë¦¬ë¥¼ ê¸°ë³¸ê°’ìœ¼ë¡œ ì„¤ì •
      const targetCategory = categoryId === "all" ? "news" : categoryId;

      if (targetCategory !== draggedProject.category) {
        await changeProjectCategory(draggedProject.projectId, targetCategory);
      }
    }
    setDraggedProject(null);
  };

  const handleDragEnd = () => {
    setDraggedProject(null);
    setDropTargetCategory(null);
  };

  // í•„í„°ë§ëœ í”„ë¡œì íŠ¸ ëª©ë¡
  const filteredProjects = useMemo(() => {
    return filterProjects(projects, {
      category: activeCategory,
      searchQuery,
      sortBy,
    });
  }, [projects, activeCategory, searchQuery, sortBy]);

  // ì¹´í…Œê³ ë¦¬ë³„ í”„ë¡œì íŠ¸ ìˆ˜
  const categoryStats = useMemo(() => {
    const stats = { all: projects.length };
    userCategories.forEach((category) => {
      stats[category.id] = projects.filter(
        (p) => p.category === category.id
      ).length;
    });
    return stats;
  }, [projects, userCategories]);

  // ë“œë¡­ë‹¤ìš´ ì™¸ë¶€ í´ë¦­ ê°ì§€
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        sortDropdownRef.current &&
        !sortDropdownRef.current.contains(event.target)
      ) {
        setSortDropdownOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // ì •ë ¬ ì˜µì…˜ ë°ì´í„°
  const sortOptions = [
    { value: "created", label: "ìƒì„±ì¼ìˆœ" },
    { value: "updated", label: "ìˆ˜ì •ì¼ìˆœ" },
    { value: "name", label: "ì´ë¦„ìˆœ" },
  ];

  const currentSortOption = sortOptions.find(
    (option) => option.value === sortBy
  );

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">í”„ë¡œì íŠ¸ ëª©ë¡ì„ ë¶ˆëŸ¬ì˜¤ëŠ” ì¤‘...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <div className="text-red-600 mb-4">âš ï¸ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤</div>
        <p className="text-gray-600 mb-6">{error}</p>
        <button
          onClick={loadProjects}
          className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
        >
          ë‹¤ì‹œ ì‹œë„
        </button>
      </div>
    );
  }

  const handleCreateSuccess = () => {
    console.log("ðŸ“ handleCreateSuccess í˜¸ì¶œë¨ - í”„ë¡œì íŠ¸ ëª©ë¡ ìƒˆë¡œê³ ì¹¨ ì‹œìž‘");
    loadProjects(); // í”„ë¡œì íŠ¸ ëª©ë¡ ìƒˆë¡œê³ ì¹¨
  };

  return (
    <div className="space-y-6">
      {/* í—¤ë” & ì „ì²´ í†µê³„ */}
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">í”„ë¡œì íŠ¸ ëª©ë¡</h2>
          <p className="text-gray-600 mt-1">
            AI ì œëª© ìƒì„± í”„ë¡œì íŠ¸ë¥¼ ê´€ë¦¬í•˜ê³  ìƒˆë¡œìš´ í”„ë¡œì íŠ¸ë¥¼ ìƒì„±í•˜ì„¸ìš”
          </p>
        </div>
      </div>

      {/* ë“œëž˜ê·¸ ì•ˆë‚´ ë©”ì‹œì§€ */}
      {draggedProject && (
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></div>
            <p className="text-blue-700 font-medium">
              "{draggedProject.name}" í”„ë¡œì íŠ¸ë¥¼ ì›í•˜ëŠ” ì¹´í…Œê³ ë¦¬ì— ë“œëž˜ê·¸í•´ì„œ
              ë†“ìœ¼ì„¸ìš”
            </p>
          </div>
        </div>
      )}

      {/* í•„í„°ë§ ë°” */}
      <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
        {/* ì¹´í…Œê³ ë¦¬ íƒ­ */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex flex-wrap gap-2">
            <button
              onClick={() => setActiveCategory("all")}
              onDragOver={(e) => handleDragOver(e, "all")}
              onDragLeave={handleDragLeave}
              onDrop={(e) => handleDrop(e, "all")}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-all ${
                activeCategory === "all"
                  ? "bg-blue-600 text-white"
                  : "bg-gray-100 text-gray-700 hover:bg-gray-200"
              } ${
                dropTargetCategory === "all" && draggedProject
                  ? "ring-2 ring-blue-400 bg-blue-100 scale-105"
                  : ""
              }`}
            >
              ì „ì²´ ({categoryStats.all})
              {dropTargetCategory === "all" && draggedProject && (
                <span className="ml-2 text-xs">â† ì—¬ê¸°ì— ë†“ê¸°</span>
              )}
            </button>
            {userCategories.map((category) => (
              <button
                key={category.id}
                onClick={() => setActiveCategory(category.id)}
                onDragOver={(e) => handleDragOver(e, category.id)}
                onDragLeave={handleDragLeave}
                onDrop={(e) => handleDrop(e, category.id)}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-all flex items-center space-x-2 ${
                  activeCategory === category.id
                    ? `bg-${category.color}-600 text-white`
                    : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                } ${
                  dropTargetCategory === category.id && draggedProject
                    ? `ring-2 ring-${category.color}-400 bg-${category.color}-100 scale-105`
                    : ""
                }`}
              >
                <div
                  className={`w-2 h-2 rounded-full bg-${category.color}-500`}
                ></div>
                <span>{category.name}</span>
                <span className="bg-white bg-opacity-20 px-2 py-0.5 rounded-full text-xs">
                  {categoryStats[category.id] || 0}
                </span>
                {dropTargetCategory === category.id && draggedProject && (
                  <span className="text-xs">â† ë†“ê¸°</span>
                )}
              </button>
            ))}
          </div>

          {/* ðŸ”§ ì¹´í…Œê³ ë¦¬ ê´€ë¦¬ ë²„íŠ¼ ë³µì› - ì¹´í…Œê³ ë¦¬ ìžìœ  ê´€ë¦¬ ê°€ëŠ¥ */}
          <div className="flex items-center space-x-3">
            <button
              onClick={() => setShowCategoryManager(true)}
              className="inline-flex items-center px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
            >
              <Cog6ToothIcon className="h-4 w-4 mr-2" />
              ì¹´í…Œê³ ë¦¬ ê´€ë¦¬
            </button>
            <span className="text-xs text-gray-500 bg-gray-50 px-3 py-1 rounded-full">
              ðŸ’¡ í”„ë¡œì íŠ¸ ë‚´ í”„ë¡¬í”„íŠ¸ ì¹´ë“œë¡œ ì„¸ë¶€ ì»¤ìŠ¤í„°ë§ˆì´ì§•í•˜ì„¸ìš”
            </span>
          </div>
        </div>

        {/* ê²€ìƒ‰ ë° ì •ë ¬ */}
        <div className="flex flex-col sm:flex-row gap-4">
          {/* ê²€ìƒ‰ë°” */}
          <div className="flex-1 relative">
            <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
            <input
              type="text"
              placeholder="í”„ë¡œì íŠ¸ ì´ë¦„, ì„¤ëª…, íƒœê·¸ë¡œ ê²€ìƒ‰..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* ì •ë ¬ ì˜µì…˜ - ì»¤ìŠ¤í…€ ë“œë¡­ë‹¤ìš´ */}
          <div className="flex items-center space-x-3">
            <FunnelIcon className="h-5 w-5 text-gray-400 flex-shrink-0" />
            <div className="relative" ref={sortDropdownRef}>
              <button
                onClick={() => setSortDropdownOpen(!sortDropdownOpen)}
                className="flex items-center justify-between pl-4 pr-3 py-3 bg-white border border-gray-300 rounded-lg hover:border-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 min-w-[140px]"
              >
                <div className="flex items-center space-x-2">
                  <span className="text-gray-700 font-medium">
                    {currentSortOption?.label}
                  </span>
                </div>
                <ChevronDownIcon
                  className={`h-4 w-4 text-gray-400 transition-transform duration-200 ${
                    sortDropdownOpen ? "rotate-180" : ""
                  }`}
                />
              </button>

              {/* ë“œë¡­ë‹¤ìš´ ë©”ë‰´ */}
              {sortDropdownOpen && (
                <div className="absolute top-full left-0 mt-1 w-full bg-white border border-gray-200 rounded-lg shadow-lg z-50 overflow-hidden">
                  {sortOptions.map((option) => (
                    <button
                      key={option.value}
                      onClick={() => {
                        setSortBy(option.value);
                        setSortDropdownOpen(false);
                      }}
                      className={`w-full flex items-center justify-between px-4 py-3 text-left hover:bg-gray-50 transition-colors duration-150 ${
                        sortBy === option.value
                          ? "bg-blue-50 text-blue-600"
                          : "text-gray-700"
                      }`}
                    >
                      <div className="flex items-center space-x-2">
                        <span className="font-medium">{option.label}</span>
                      </div>
                      {sortBy === option.value && (
                        <CheckIcon className="h-4 w-4 text-blue-600" />
                      )}
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* ë·° ëª¨ë“œ */}
          <div className="flex items-center space-x-1 bg-gray-100 rounded-lg p-1">
            <button
              onClick={() => setViewMode("grid")}
              className={`p-2 rounded-md ${
                viewMode === "grid" ? "bg-white shadow-sm" : "hover:bg-gray-200"
              }`}
            >
              <Squares2X2Icon className="h-4 w-4" />
            </button>
            <button
              onClick={() => setViewMode("list")}
              className={`p-2 rounded-md ${
                viewMode === "list" ? "bg-white shadow-sm" : "hover:bg-gray-200"
              }`}
            >
              <ListBulletIcon className="h-4 w-4" />
            </button>
          </div>

          {/* ìƒˆ í”„ë¡œì íŠ¸ ë²„íŠ¼ */}
          <button
            onClick={() => setShowCreateModal(true)}
            className="inline-flex items-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors font-medium"
          >
            <PlusIcon className="h-4 w-4 mr-2" />ìƒˆ í”„ë¡œì íŠ¸
          </button>
        </div>
      </div>

      {/* í”„ë¡œì íŠ¸ ëª©ë¡ */}
      {filteredProjects.length === 0 ? (
        <div className="text-center py-16 bg-white rounded-xl border border-gray-200">
          <FolderOpenIcon className="mx-auto h-16 w-16 text-gray-400 mb-4" />
          <h3 className="text-xl font-medium text-gray-900 mb-2">
            {searchQuery || activeCategory !== "all"
              ? "ì¡°ê±´ì— ë§žëŠ” í”„ë¡œì íŠ¸ê°€ ì—†ìŠµë‹ˆë‹¤"
              : "í”„ë¡œì íŠ¸ê°€ ì—†ìŠµë‹ˆë‹¤"}
          </h3>
          <p className="text-gray-500 mb-8">
            {searchQuery || activeCategory !== "all"
              ? "ë‹¤ë¥¸ ì¡°ê±´ìœ¼ë¡œ ê²€ìƒ‰í•´ë³´ì„¸ìš”"
