import axios from "axios";

// üîß API Í∏∞Î≥∏ ÏÑ§Ï†ï Í∞úÏÑ†
const API_BASE_URL =
  process.env.REACT_APP_API_URL ||
  "https://gcm3qzoy04.execute-api.us-east-1.amazonaws.com/prod";

// Axios Ïù∏Ïä§ÌÑ¥Ïä§ Í∞úÏÑ†
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { "Content-Type": "application/json" },
  timeout: 300000, // üîß ÏàòÏ†ï: 5Î∂ÑÏúºÎ°ú Îã®Ï∂ï (Í∏∞Ï°¥ 15Î∂ÑÏùÄ ÎÑàÎ¨¥ Í∏∏Ïñ¥ÏÑú 504 ÌÉÄÏûÑÏïÑÏõÉ Î∞úÏÉù)
});

// üîß Í∞úÏÑ†Îêú generateAPI
export const generateAPI = {
  generateTitle: async (projectId, data) => {
    console.log("ÎåÄÌôî ÏÉùÏÑ± ÏöîÏ≤≠ ÏãúÏûë:", {
      projectId,
      inputLength: data.userInput.length,
      historyLength: data.chat_history?.length || 0,
      timestamp: new Date().toISOString(),
    });

    try {
      const response = await api.post(`/projects/${projectId}/generate`, data);

      console.log("ÎåÄÌôî ÏÉùÏÑ± ÏÑ±Í≥µ:", {
        status: response.status,
        mode: response.data.mode,
        message: response.data.message,
        timestamp: new Date().toISOString(),
      });

      return response.data;
    } catch (error) {
      console.error("ÎåÄÌôî ÏÉùÏÑ± Ïã§Ìå®:", {
        code: error.code,
        message: error.message,
        status: error.response?.status,
        responseData: error.response?.data,
        timestamp: new Date().toISOString(),
      });
      throw error;
    }
  },

  // üîß Ïä§Ìä∏Î¶¨Î∞ç API ÏôÑÏ†Ñ Í∞úÏÑ† - Ïã§Ìå® Ïãú Ï¶âÏãú Ìè¥Î∞±
  generateTitleStream: async (
    projectId,
    data,
    onChunk,
    onError,
    onComplete
  ) => {
    console.log("Ïä§Ìä∏Î¶¨Î∞ç ÎåÄÌôî ÏÉùÏÑ± ÏöîÏ≤≠ ÏãúÏûë:", {
      projectId,
      inputLength: data.userInput.length,
      historyLength: data.chat_history?.length || 0,
      timestamp: new Date().toISOString(),
    });

    // üîß Ïä§Ìä∏Î¶¨Î∞ç ÏãúÎèÑ Ï†ÑÏóê Í∞ÑÎã®Ìïú Ïó∞Í≤∞ ÌÖåÏä§Ìä∏
    try {
      // Ïä§Ìä∏Î¶¨Î∞ç ÏóîÎìúÌè¨Ïù∏Ìä∏ ÏÇ¨Ï†Ñ ÌÖåÏä§Ìä∏ (Îß§Ïö∞ ÏßßÏùÄ ÌÉÄÏûÑÏïÑÏõÉ)
      await api.post(
        `/projects/${projectId}/generate/stream`,
        { userInput: "test", chat_history: [] },
        {
          timeout: 5000, // 5Ï¥à ÌÖåÏä§Ìä∏
          validateStatus: () => false, // Î™®Îì† ÏÉÅÌÉú ÏΩîÎìú ÌóàÏö© (ÌÖåÏä§Ìä∏Ïö©)
        }
      );
    } catch (testError) {
      console.log(
        "üîß Ïä§Ìä∏Î¶¨Î∞ç ÏóîÎìúÌè¨Ïù∏Ìä∏ ÏÇ¨Ï†Ñ ÌÖåÏä§Ìä∏ Ïã§Ìå®, Ï¶âÏãú Ìè¥Î∞±:",
        testError.code
      );

      // Ï¶âÏãú ÏùºÎ∞ò APIÎ°ú Ìè¥Î∞±
      try {
        const fallbackResponse = await api.post(
          `/projects/${projectId}/generate`,
          data
        );

        console.log("‚úÖ Ï¶âÏãú Ìè¥Î∞± ÏÑ±Í≥µ:", {
          mode: fallbackResponse.data.mode,
          timestamp: new Date().toISOString(),
        });

        // üîß Ìè¥Î∞± ÏùëÎãµÏùÑ Ïä§Ìä∏Î¶¨Î∞ç ÌòïÌÉúÎ°ú ÏãúÎÆ¨Î†àÏù¥ÏÖò
        if (fallbackResponse.data.result && onChunk) {
          const fullText = fallbackResponse.data.result;
          const words = fullText.split(" ");

          // Îπ†Î•∏ ÏÜçÎèÑÎ°ú ÏõåÎìú Îã®ÏúÑ Ïä§Ìä∏Î¶¨Î∞ç ÏãúÎÆ¨Î†àÏù¥ÏÖò
          for (let i = 0; i < words.length; i++) {
            const word = words[i] + (i < words.length - 1 ? " " : "");
            onChunk(word, { content: word });

            // Îß§Ïö∞ ÏßßÏùÄ ÏßÄÏó∞ (Ïã§ÏãúÍ∞Ñ ÎäêÎÇå)
            await new Promise((resolve) => setTimeout(resolve, 50));
          }
        }

        // ÏôÑÎ£å ÏΩúÎ∞± Ìò∏Ï∂ú
        if (onComplete) {
          onComplete({
            result: fallbackResponse.data.result,
            model_info: fallbackResponse.data.model_info,
            performance_metrics: fallbackResponse.data.performance_metrics,
            timestamp: new Date().toISOString(),
          });
        }

        return fallbackResponse.data;
      } catch (fallbackError) {
        console.error("‚ùå Ìè¥Î∞±ÎèÑ Ïã§Ìå®:", fallbackError);
        if (onError) {
          onError(
            new Error(
              "Î™®Îì† API ÏóîÎìúÌè¨Ïù∏Ìä∏Í∞Ä Ïã§Ìå®ÌñàÏäµÎãàÎã§. ÏÑúÎ≤Ñ ÏÉÅÌÉúÎ•º ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî."
            )
          );
        }
        throw new Error(
          "ÏÑúÎπÑÏä§Î•º ÏÇ¨Ïö©Ìï† Ïàò ÏóÜÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
        );
      }
    }

    // üîß Ïã§Ï†ú Ïä§Ìä∏Î¶¨Î∞ç ÏãúÎèÑ (ÏÇ¨Ï†Ñ ÌÖåÏä§Ìä∏ ÌÜµÍ≥ºÌïú Í≤ΩÏö∞ÏóêÎßå)
    try {
      console.log("üîß Ïã§Ï†ú Ïä§Ìä∏Î¶¨Î∞ç API Ìò∏Ï∂ú ÏãúÎèÑ...");

      const response = await api.post(
        `/projects/${projectId}/generate/stream`,
        data,
        {
          timeout: 120000, // üîß 2Î∂ÑÏúºÎ°ú Îã®Ï∂ï
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        }
      );

      if (!response || !response.data) {
        throw new Error("Ïä§Ìä∏Î¶¨Î∞ç ÏùëÎãµÏù¥ ÏóÜÏäµÎãàÎã§");
      }

      const responseData = response.data;

      console.log("‚úÖ Ïä§Ìä∏Î¶¨Î∞ç ÏùëÎãµ ÏàòÏã†:", {
        mode: responseData.mode,
        resultLength: responseData.result?.length || 0,
        chunksCount: responseData.chunks?.length || 0,
        timestamp: new Date().toISOString(),
      });

      // Ï≤≠ÌÅ¨ Îç∞Ïù¥ÌÑ∞ Ï≤òÎ¶¨
      if (responseData.chunks && Array.isArray(responseData.chunks)) {
        for (const chunk of responseData.chunks) {
          if (chunk.content && onChunk) {
            onChunk(chunk.content, chunk);
            await new Promise((resolve) => setTimeout(resolve, 30)); // Ïä§Ìä∏Î¶¨Î∞ç Ìö®Í≥º
          }
        }
      } else if (responseData.result && onChunk) {
        // Ï≤≠ÌÅ¨ ÏóÜÎäî Í≤ΩÏö∞ ÌÖçÏä§Ìä∏Î•º Îã®Ïñ¥ Îã®ÏúÑÎ°ú Î∂ÑÌï†
        const words = responseData.result.split(" ");
        const chunkSize = Math.max(1, Math.floor(words.length / 20)); // 20Í∞ú Ï≤≠ÌÅ¨

        for (let i = 0; i < words.length; i += chunkSize) {
          const chunkWords = words.slice(i, i + chunkSize);
          const chunkContent =
            chunkWords.join(" ") + (i + chunkSize < words.length ? " " : "");

          if (chunkContent.trim()) {
            onChunk(chunkContent, { content: chunkContent });
            await new Promise((resolve) => setTimeout(resolve, 80));
          }
        }
      }

      // ÏôÑÎ£å ÏΩúÎ∞± Ìò∏Ï∂ú
      if (onComplete) {
        onComplete({
          result: responseData.result,
          model_info: responseData.model_info,
          performance_metrics: responseData.performance_metrics,
          timestamp: responseData.timestamp || new Date().toISOString(),
        });
      }

      return responseData;
    } catch (error) {
      console.error("Ïä§Ìä∏Î¶¨Î∞ç ÎåÄÌôî ÏÉùÏÑ± Ïã§Ìå®:", {
        code: error.code,
        message: error.message,
        status: error.response?.status,
        timestamp: new Date().toISOString(),
      });

      // üîß Ïä§Ìä∏Î¶¨Î∞ç Ïã§Ìå® Ïãú Ï¶âÏãú ÏùºÎ∞ò APIÎ°ú Ìè¥Î∞±
      if (
        error.code === "ECONNABORTED" ||
        error.code === "ERR_NETWORK" ||
        error.response?.status === 504
      ) {
        console.log("üîß Ïä§Ìä∏Î¶¨Î∞ç Ïã§Ìå®Î°ú Ïù∏Ìïú ÏùºÎ∞ò API Ìè¥Î∞±...");

        try {
          const fallbackResponse = await api.post(
            `/projects/${projectId}/generate`,
            data
          );

          console.log("‚úÖ Ìè¥Î∞± API ÏÑ±Í≥µ:", {
            mode: fallbackResponse.data.mode,
            timestamp: new Date().toISOString(),
          });

          if (onComplete) {
            onComplete({
              result: fallbackResponse.data.result,
              model_info: fallbackResponse.data.model_info,
              performance_metrics: fallbackResponse.data.performance_metrics,
              timestamp: new Date().toISOString(),
            });
          }

          return fallbackResponse.data;
        } catch (fallbackError) {
          console.error("‚ùå Ìè¥Î∞± APIÎèÑ Ïã§Ìå®:", fallbackError);

          if (onError) {
            onError(
              new Error(
                "ÎÑ§Ìä∏ÏõåÌÅ¨ Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
              )
            );
          }

          throw new Error("ÏÑúÎπÑÏä§ Ïó∞Í≤∞Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
        }
      }

      // Í∏∞ÌÉÄ Ïò§Î•ò
      if (onError) {
        onError(error);
      }

      throw error;
    }
  },

  getExecutionStatus: async (executionArn) => {
    return {
      status: "SUCCEEDED",
      output: "{}",
    };
  },
};

// =============================================================================
// 4. Ï±ÑÌåÖ API (generate APIÎ°ú Î¶¨Îã§Ïù¥Î†âÌä∏)
// =============================================================================

export const chatAPI = {
  sendMessage: async (projectId, message, sessionId, userId = "default") => {
    // Ï±ÑÌåÖÏùÄ generate APIÎ•º ÏÇ¨Ïö©ÌïòÏó¨ Ï≤òÎ¶¨
    console.log("Ï±ÑÌåÖ Î©îÏãúÏßÄÎ•º generate APIÎ°ú Ï†ÑÎã¨:", {
      projectId,
      message,
      sessionId,
      userId,
    });

    try {
      const response = await generateAPI.generateTitle(projectId, {
        userInput: message,
        userRequest: "",
        chat_history: [], // ÌòÑÏû¨ Ï±ÑÌåÖ ÌûàÏä§ÌÜ†Î¶¨Îäî ÎπÑÏõåÎë†
      });

      return {
        response: response.result,
        sessionId,
        userId,
        timestamp: new Date().toISOString(),
        mode: response.mode || "chat",
      };
    } catch (error) {
      console.error("Ï±ÑÌåÖ Î©îÏãúÏßÄ Ï≤òÎ¶¨ Ïã§Ìå®:", error);
      throw error;
