  getChatHistory: async (projectId, sessionId, userId = "default") => {
    // ÌòÑÏû¨ Î∞±ÏóîÎìúÏóêÏÑú Ï±ÑÌåÖ ÌûàÏä§ÌÜ†Î¶¨Î•º Î≥ÑÎèÑÎ°ú Ï†ÄÏû•ÌïòÏßÄ ÏïäÏùå
    console.log("Ï±ÑÌåÖ ÌûàÏä§ÌÜ†Î¶¨ Ï°∞Ìöå:", { projectId, sessionId, userId });

    return {
      messages: [],
      sessionId,
      userId,
      message:
        "Ï±ÑÌåÖ ÌûàÏä§ÌÜ†Î¶¨Îäî ÌòÑÏû¨ ÏßÄÏõêÎêòÏßÄ ÏïäÏäµÎãàÎã§. Í∞Å Î©îÏãúÏßÄÎäî ÎèÖÎ¶ΩÏ†ÅÏúºÎ°ú Ï≤òÎ¶¨Îê©ÎãàÎã§.",
    };
  },

  getChatSessions: async (projectId, userId = "default") => {
    // ÌòÑÏû¨ Î∞±ÏóîÎìúÏóêÏÑú ÏÑ∏ÏÖòÏùÑ Î≥ÑÎèÑÎ°ú Í¥ÄÎ¶¨ÌïòÏßÄ ÏïäÏùå
    console.log("Ï±ÑÌåÖ ÏÑ∏ÏÖò Î™©Î°ù Ï°∞Ìöå:", { projectId, userId });

    return {
      sessions: [],
      message:
        "Ï±ÑÌåÖ ÏÑ∏ÏÖòÏùÄ ÌòÑÏû¨ ÏßÄÏõêÎêòÏßÄ ÏïäÏäµÎãàÎã§. Í∞Å ÎåÄÌôîÎäî ÎèÖÎ¶ΩÏ†ÅÏúºÎ°ú Ï≤òÎ¶¨Îê©ÎãàÎã§.",
    };
  },

  deleteChatSession: async (projectId, sessionId, userId = "default") => {
    // ÌòÑÏû¨ Î∞±ÏóîÎìúÏóêÏÑú ÏÑ∏ÏÖòÏùÑ Î≥ÑÎèÑÎ°ú Í¥ÄÎ¶¨ÌïòÏßÄ ÏïäÏùå
    console.log("Ï±ÑÌåÖ ÏÑ∏ÏÖò ÏÇ≠Ï†ú:", { projectId, sessionId, userId });

    return {
      message: "Ï±ÑÌåÖ ÏÑ∏ÏÖò ÏÇ≠Ï†úÍ∞Ä ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.",
      sessionId,
      userId,
    };
