export const authAPI = {
  isAuthenticated: () => {
    // Ïã§Ï†ú ÌÜ†ÌÅ∞ Í≤ÄÏ¶ù Î°úÏßÅÏúºÎ°ú ÎåÄÏ≤¥ ÌïÑÏöî
    return true;
  },

  getCurrentUser: () => {
