import { useState, useCallback } from "react";
import { toast } from "react-hot-toast";
import { generateAPI } from "../services/api";

/**
 * Ï†úÎ™© ÏÉùÏÑ± Ïã§Ìñâ Î∞è Í≤∞Í≥º Ìè¥ÎßÅÏùÑ ÏúÑÌïú Ïª§Ïä§ÌÖÄ ÌõÖ
 * @param {string} projectId - ÌîÑÎ°úÏ†ùÌä∏ ID
 * @returns {Object} - Ï†úÎ™© ÏÉùÏÑ± Í¥ÄÎ†® ÏÉÅÌÉúÏôÄ Ìï®ÏàòÎì§
 */
export const useOrchestration = (projectId) => {
  const [isExecuting, setIsExecuting] = useState(false);
  const [currentExecution, setCurrentExecution] = useState(null);
  const [executionStatus, setExecutionStatus] = useState(null);
  const [isStreaming, setIsStreaming] = useState(false);

  /**
   * Ï†úÎ™© ÏÉùÏÑ± Ïã§Ìñâ
   * @param {string} userInput - ÏÇ¨Ïö©Ïûê ÏûÖÎ†•
   * @param {Object} options - Ï∂îÍ∞Ä ÏòµÏÖò (Ïòà: chat_history, useStreaming)
   * @returns {Promise<Object>} - ÏÉùÏÑ± Í≤∞Í≥º
   */
  const executeOrchestration = useCallback(
    async (userInput, options = {}) => {
      if (!userInput.trim()) {
        toast.error("Î©îÏãúÏßÄÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
        return null;
      }

      try {
        setIsExecuting(true);
        setExecutionStatus("STARTING");

        // chat_history, prompt_cards, userInput, modelIdÎ•º Ìè¨Ìï®ÌïòÎäî data Í∞ùÏ≤¥ ÏÉùÏÑ±
        const data = {
          userInput: userInput,
          chat_history: options.chat_history || [],
          prompt_cards: options.prompt_cards || [],
          modelId: options.modelId || null,
        };

        console.log("üöÄ ÎåÄÌôî ÏÉùÏÑ± ÏöîÏ≤≠ ÏãúÏûë:", {
          projectId,
          inputLength: userInput.length,
          historyLength: data.chat_history.length,
          promptCardsCount: data.prompt_cards.length,
          useStreaming: options.useStreaming === true,
          modelId: data.modelId,
          timestamp: new Date().toISOString(),
        });

        // üîß Ïä§Ìä∏Î¶¨Î∞ç ÏÇ¨Ïö© Ïó¨Î∂Ä ÌôïÏù∏ - Îçî ÏïàÏ†ÑÌïú Î°úÏßÅ
        if (options.useStreaming === true) {
          setIsStreaming(true);

          // Ïä§Ìä∏Î¶¨Î∞ç ÏΩúÎ∞± Ìï®Ïàò ÏÑ§Ï†ï
          const onChunk = options.onChunk || (() => {});
          const onError = (error) => {
            console.error("üîß Ïä§Ìä∏Î¶¨Î∞ç Ïò§Î•ò Ï≤òÎ¶¨:", error);
            setIsExecuting(false);
            setIsStreaming(false);
            setExecutionStatus("FAILED");

            // üîß Í∞úÏÑ†: Ïò§Î•ò ÌÉÄÏûÖÏóê Îî∞Î•∏ Ï†ÅÏ†àÌïú Î©îÏãúÏßÄ
            if (
              error.message?.includes("Gateway Timeout") ||
              error.message?.includes("504") ||
              error.code === "ECONNABORTED"
            ) {
              toast.error(
                "ÏÑúÎ≤Ñ ÏùëÎãµ ÏãúÍ∞ÑÏù¥ Ï¥àÍ≥ºÎêòÏóàÏäµÎãàÎã§. ÏöîÏ≤≠ÏùÑ Í∞ÑÎã®Ìûà ÌïòÍ±∞ÎÇò Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
              );
            } else if (
              error.message?.includes("CORS") ||
              error.message?.includes("Network Error")
            ) {
              toast.error(
                "ÏÑúÎ≤Ñ Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§. ÏÉàÎ°úÍ≥†Ïπ® ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
              );
            } else {
              toast.error(
                "Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
              );
            }

            if (options.onError) options.onError(error);
          };

          const onComplete = (response) => {
            console.log("‚úÖ Ïä§Ìä∏Î¶¨Î∞ç ÏôÑÎ£å:", {
              resultLength: response.result?.length || 0,
              timestamp: new Date().toISOString(),
            });
            setIsExecuting(false);
            setIsStreaming(false);
            setExecutionStatus("COMPLETED");
            if (options.onComplete) options.onComplete(response);
          };

          try {
            // üîß Í∞úÏÑ†: Ïä§Ìä∏Î¶¨Î∞ç API Ìò∏Ï∂ú (ÎÇ¥Î∂ÄÏóêÏÑú Ìè¥Î∞± Ï≤òÎ¶¨Îê®)
            return await generateAPI.generateTitleStream(
              projectId,
              data,
              onChunk,
              onError,
              onComplete
            );
          } catch (streamError) {
            console.error("üîß Ïä§Ìä∏Î¶¨Î∞ç ÏµúÏ¢Ö Ïã§Ìå®:", streamError);

            // üîß Ïä§Ìä∏Î¶¨Î∞ç ÏôÑÏ†Ñ Ïã§Ìå® ÏãúÏóêÎèÑ Ìè¥Î∞±Ïù¥ ÎÇ¥Î∂ÄÏóêÏÑú Ï≤òÎ¶¨ÎêòÎØÄÎ°ú
            // Ïó¨Í∏∞ÏÑúÎäî ÏÇ¨Ïö©ÏûêÏóêÍ≤å ÏïåÎ¶ºÎßå
            setIsStreaming(false);
            setIsExecuting(false);
            setExecutionStatus("FAILED");

            // ÏµúÏ¢Ö Ïã§Ìå® Î©îÏãúÏßÄ
            toast.error(
              "ÏÑúÎπÑÏä§Ïóê ÏùºÏãúÏ†ÅÏù∏ Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
            );
            throw streamError;
          }
        }

        // üîß ÏùºÎ∞ò API Ìò∏Ï∂ú (Ïä§Ìä∏Î¶¨Î∞ç ÎØ∏ÏÇ¨Ïö©)
        console.log("üìÑ ÏùºÎ∞ò API Ìò∏Ï∂ú ÏãúÏûë...");
        const response = await generateAPI.generateTitle(projectId, data);

        console.log("‚úÖ ÎåÄÌôî ÏÉùÏÑ± ÏôÑÎ£å:", {
          mode: response.mode,
          message: response.message,
          timestamp: new Date().toISOString(),
        });

        setIsExecuting(false);
        setExecutionStatus("COMPLETED");

        return response;
      } catch (error) {
        console.error("‚ùå Ï†úÎ™© ÏÉùÏÑ± Ïã§Ìå®:", {
          error: error.message,
          code: error.code,
          status: error.response?.status,
          timestamp: new Date().toISOString(),
        });

        setIsExecuting(false);
        setIsStreaming(false);
        setExecutionStatus("FAILED");

        // üîß Í∞úÏÑ†: ÏÉÅÏÑ∏Ìïú Ïò§Î•ò Ï≤òÎ¶¨
        if (error.response?.status === 504) {
          toast.error(
            "ÏÑúÎ≤Ñ ÏùëÎãµ ÏãúÍ∞ÑÏù¥ Ï¥àÍ≥ºÎêòÏóàÏäµÎãàÎã§. ÏûÖÎ†•ÏùÑ Í∞ÑÏÜåÌôîÌïòÍ±∞ÎÇò Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
          );
        } else if (
          error.message?.includes("CORS") ||
          error.code === "ERR_NETWORK"
        ) {
          toast.error(
            "ÏÑúÎ≤Ñ Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§. ÌéòÏù¥ÏßÄÎ•º ÏÉàÎ°úÍ≥†Ïπ®ÌïòÍ≥† Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
          );
        } else if (
          error.response?.status === 400 &&
          error.response?.data?.setup_required
        ) {
          toast.error("ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥ÎìúÎ•º Î®ºÏ†Ä ÏÑ§Ï†ïÌï¥Ï£ºÏÑ∏Ïöî!");
        } else if (error.code === "ECONNABORTED") {
          toast.error(
            "ÏöîÏ≤≠ Ï≤òÎ¶¨ ÏãúÍ∞ÑÏù¥ Ï¥àÍ≥ºÎêòÏóàÏäµÎãàÎã§. ÏûÖÎ†•ÏùÑ Ï§ÑÏù¥Í±∞ÎÇò Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
          );
        } else if (error.response?.status === 500) {
          toast.error(
            "ÏÑúÎ≤ÑÏóê ÏùºÏãúÏ†ÅÏù∏ Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
          );
        } else if (error.response?.status === 403) {
          toast.error("Í∂åÌïúÏù¥ ÏóÜÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏ ÏÉÅÌÉúÎ•º ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.");
        } else if (error.response?.status === 429) {
          toast.error("ÏöîÏ≤≠Ïù¥ ÎÑàÎ¨¥ ÎßéÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.");
        } else {
          toast.error(
            "Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. ÎÑ§Ìä∏ÏõåÌÅ¨ Ïó∞Í≤∞ÏùÑ ÌôïÏù∏ÌïòÍ≥† Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
          );
        }

        throw error;
      }
    },
    [projectId]
  );

  /**
   * Ïã§Ìñâ ÏÉÅÌÉú Ï°∞Ìöå (Step Functions ÏÇ¨Ïö© Ïãú)
   * @param {string} executionArn - Ïã§Ìñâ ARN
   * @param {Function} onComplete - ÏôÑÎ£å Ïãú ÏΩúÎ∞±
   * @param {Function} onError - ÏóêÎü¨ Ïãú ÏΩúÎ∞±
   */
  const pollOrchestrationResult = useCallback(
    async (executionArn, onComplete, onError) => {
      // Ïä§Ìä∏Î¶¨Î∞ç Î™®ÎìúÏóêÏÑúÎäî Ìè¥ÎßÅÏù¥ ÌïÑÏöî ÏóÜÏùå
      if (isStreaming) {
        return;
      }

      const poll = async () => {
        try {
          const result = await generateAPI.getExecutionStatus(executionArn);

          setExecutionStatus(result.status);

          if (result.status === "SUCCEEDED") {
            setIsExecuting(false);
            setExecutionStatus("COMPLETED");

            if (onComplete) {
              onComplete(result);
            }
          } else if (result.status === "FAILED") {
            setIsExecuting(false);
            setExecutionStatus("FAILED");

            if (onError) {
              onError(new Error("Ï≤òÎ¶¨ Ïã§Ìå®"));
            }
          } else if (result.status === "RUNNING") {
            // 3Ï¥à ÌõÑ Îã§Ïãú Ìè¥ÎßÅ
            setTimeout(poll, 3000);
          }
        } catch (error) {
          console.error("Ïã§Ìñâ ÏÉÅÌÉú Ï°∞Ìöå Ïã§Ìå®:", error);
          setIsExecuting(false);
          setExecutionStatus("FAILED");

          if (onError) {
            onError(error);
          }
        }
      };

      poll();
    },
    [projectId, isStreaming]
  );

  /**
   * Ïò§ÏºÄÏä§Ìä∏Î†àÏù¥ÏÖò ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
   */
  const resetOrchestration = useCallback(() => {
    setIsExecuting(false);
    setIsStreaming(false);
    setCurrentExecution(null);
    setExecutionStatus(null);
  }, []);

  return {
    isExecuting,
    isStreaming,
    currentExecution,
    executionStatus,
    executeOrchestration,
    pollOrchestrationResult,
    resetOrchestration,
  };
};
