import React, { useState, useCallback, useMemo } from "react";
import { ChatBubbleLeftRightIcon, SparklesIcon, PaperAirplaneIcon, StopIcon } from "@heroicons/react/24/outline";
import ConversationDrawer from "./ConversationDrawer";
import ModelSelector from "../ModelSelector";
import { useChat } from "../../hooks/useChat";
import { useMessages } from "../../hooks/useMessages";
import { useConversations } from "../../hooks/useConversations";
import { useConversationContext } from "../../contexts/ConversationContext";
import { useAuth } from "../../contexts/AuthContext";
import SimpleChatMessage from "../SimpleChatMessage";

const ChatWindow = ({ projectId, projectName, promptCards = [], isAdminMode = false }) => {
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  // Auth context for user info
  const { user } = useAuth();

  // Conversation context
  const { currentConversationId, setCurrentConversation } = useConversationContext();
  const { createConversation } = useConversations();

  // Messages hook for current conversation
  const {
    messages: historicalMessages,
    loading: messagesLoading,
  } = useMessages(currentConversationId);

  // Chat hook for sending messages
  console.log("üîç [DEBUG] ChatWindow - useChat Ìò∏Ï∂ú:", {
    projectId,
    projectName,
    currentConversationId,
    currentConversationIdType: typeof currentConversationId,
    isCurrentConversationIdNull: currentConversationId === null,
  });

  const {
    messages: chatMessages,
    inputValue,
    handleInputChange,
    copiedMessage,
    isGenerating,
    inputHeight,
    handleSendMessage,
    handleStopGeneration,
    handleKeyPress,
    handleCopyMessage,
    handleCopyTitle,
    wsConnected,
    wsConnecting,
    selectedModel,
    setSelectedModel,
  } = useChat(
    projectId, 
    projectName, 
    promptCards, 
    currentConversationId,
    createConversation,      // ÎåÄÌôî ÏÉùÏÑ± Ìï®Ïàò Ï†ÑÎã¨
    setCurrentConversation   // ÎåÄÌôî ÏÑ§Ï†ï Ìï®Ïàò Ï†ÑÎã¨
  );

  // ÌÜµÌï©Îêú Î©îÏãúÏßÄ Î™©Î°ù
  const allMessages = React.useMemo(() => {
    if (!currentConversationId) {
      // ÏÉà ÎåÄÌôî Î™®Îìú: useChatÏùò ÌòÑÏû¨ ÏÑ∏ÏÖò Î©îÏãúÏßÄÎßå ÌëúÏãú
      console.log("üîç [DEBUG] ÏÉà ÎåÄÌôî Î™®Îìú - ÌòÑÏû¨ ÏÑ∏ÏÖò Î©îÏãúÏßÄ:", chatMessages.length);
      return chatMessages;
    } else {
      // Í∏∞Ï°¥ ÎåÄÌôî Î™®Îìú: Ï†ÄÏû•Îêú Î©îÏãúÏßÄ + ÌòÑÏû¨ ÏÑ∏ÏÖòÏùò ÏÉà Î©îÏãúÏßÄ
      console.log("üîç [DEBUG] Í∏∞Ï°¥ ÎåÄÌôî Î™®Îìú - Ï†ÄÏû•Îêú:", historicalMessages.length, "ÌòÑÏû¨:", chatMessages.length);
      return [...historicalMessages, ...chatMessages];
    }
  }, [currentConversationId, historicalMessages, chatMessages]);

  return (
    <div className="flex h-full overflow-hidden">
      {/* ÎåÄÌôî ÎìúÎ°úÏñ¥ - ÏÇ¨Ïö©Ïûê Î™®ÎìúÏóêÏÑúÎßå ÌëúÏãú */}
      {!isAdminMode && <ConversationDrawer onCollapsedChange={setSidebarCollapsed} />}

      {/* Î©îÏù∏ Ï±ÑÌåÖ ÏòÅÏó≠ */}
      <div className={`flex-1 flex flex-col bg-gray-50 dark:bg-dark-primary transition-all duration-500 ease-out ${
        !isAdminMode ? (sidebarCollapsed ? "md:ml-14" : "md:ml-64") : ""
      }`}>
        {/* Î©îÏãúÏßÄ ÏòÅÏó≠ */}
        <div className="flex-1 overflow-y-auto px-4 md:px-6 py-4 min-h-0 bg-gray-50 dark:bg-dark-primary transition-all duration-300">
          <div className="max-w-4xl mx-auto space-y-6">
            {allMessages.length === 0 && !messagesLoading ? (
              // Îπà ÏÉÅÌÉú - ÏÉà ÎåÄÌôî ÏãúÏûë
              <div className="flex flex-col items-center justify-center min-h-[60vh]">
