import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import { ArrowLeftIcon } from "@heroicons/react/24/outline";
import { projectAPI, handleAPIError } from "../services/api";
import LoadingSpinner from "./LoadingSpinner";
import AdminView from "./AdminView";
import UserView from "./UserView";
import { useApp } from "../contexts/AppContext";
import { ChatInterfaceSkeleton } from "./skeleton/SkeletonComponents";
import DarkModeToggle from "./DarkModeToggle";

const ProjectDetail = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const { mode, setMode } = useApp();
  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadProjectData();
  }, [projectId, navigate]);

  const loadProjectData = async () => {
    try {
      setLoading(true);
      const projectData = await projectAPI.getProject(projectId);
      setProject(projectData);
    } catch (error) {
      console.error("ÌîÑÎ°úÏ†ùÌä∏ Î°úÎìú Ïã§Ìå®:", error);
      const errorInfo = handleAPIError(error);
      toast.error(errorInfo.message);
      navigate("/projects");
    } finally {
      setLoading(false);
    }
  };

  const handleBackNavigation = () => {
    navigate("/projects");
  };

  if (loading) {
    return <ChatInterfaceSkeleton />;
  }

  if (!project) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center transition-colors duration-200">
        <div className="text-center">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
            ÌîÑÎ°úÏ†ùÌä∏Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§
          </h2>
          <button
            onClick={() => navigate("/projects")}
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-blue-700 dark:text-blue-300 bg-blue-100 dark:bg-blue-900 hover:bg-blue-200 dark:hover:bg-blue-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors duration-200"
          >
            <ArrowLeftIcon className="h-5 w-5 mr-2" />
            ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ùÏúºÎ°ú
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="h-screen flex flex-col bg-gradient-to-br from-slate-50 to-indigo-50 dark:bg-dark-primary transition-all duration-500">
      {/* ÏÉÅÎã® ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò Î∞î */}
      <div className="flex-shrink-0 glass-dark border-b border-white/10 dark:border-white/5 px-6 py-4 transition-all duration-300">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-6">
            <button
              onClick={handleBackNavigation}
              className="btn-glass group relative overflow-hidden"
            >
              <ArrowLeftIcon className="h-4 w-4 mr-2 group-hover:-translate-x-1 transition-transform duration-300" />
              <span className="group-hover:text-cyan-300 transition-colors duration-300">
                Î™©Î°ùÏúºÎ°ú
              </span>
            </button>

            <div className="flex items-center space-x-4">
              <div className="w-2 h-8 bg-gradient-to-b from-cyan-400 to-purple-500 rounded-full" />
              <h1 className="text-xl font-bold bg-gradient-to-r from-gray-900 dark:from-white dark:to-cyan-300 bg-clip-text text-transparent">
                {project.name}
              </h1>
            </div>
          </div>

          <div className="flex items-center gap-4">
            {/* Îã§ÌÅ¨Î™®Îìú ÌÜ†Í∏Ä */}
            <DarkModeToggle size="md" />

            {/* Î™®Îìú Ï†ÑÌôò Î≤ÑÌäº */}
            <div className="glass-card p-1 rounded-xl">
              <div className="flex items-center">
                <button
                  onClick={() => setMode("user")}
                  className={`px-4 py-2 text-sm font-medium rounded-lg transition-all duration-300 ${
                    mode === "user"
                      ? "bg-gradient-to-r from-cyan-500 to-purple-500 text-white shadow-lg"
                      : "text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white hover:bg-white/10"
                  }`}
                >
                  üë• ÏÇ¨Ïö©Ïûê
                </button>
                <button
                  onClick={() => setMode("admin")}
                  className={`px-4 py-2 text-sm font-medium rounded-lg transition-all duration-300 ${
                    mode === "admin"
                      ? "bg-gradient-to-r from-emerald-500 to-teal-500 text-white shadow-lg"
                      : "text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white hover:bg-white/10"
                  }`}
                >
                  ‚öôÔ∏è Í¥ÄÎ¶¨Ïûê
                </button>
              </div>
            </div>

            {/* ÏÉÅÌÉú Ïù∏ÎîîÏºÄÏù¥ÌÑ∞ */}
            <div className="glass-card px-4 py-2 rounded-xl">
              <div className="flex items-center gap-2">
                <div className="relative">
                  <div className="w-2 h-2 bg-emerald-400 rounded-full animate-pulse" />
                  <div className="absolute inset-0 w-2 h-2 bg-emerald-400 rounded-full animate-ping opacity-30" />
                </div>
                <span className="text-xs text-emerald-300 font-medium">
                  Ï§ÄÎπÑÏôÑÎ£å
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† */}
      <div className="flex-1 overflow-hidden">
        {mode === "admin" ? (
          // Í¥ÄÎ¶¨Ïûê Î™®Îìú: ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú Í¥ÄÎ¶¨ + Ï±ÑÌåÖ
          <AdminView projectId={projectId} projectName={project.name} />
        ) : (
          // ÏÇ¨Ïö©Ïûê Î™®Îìú: Ï±ÑÌåÖÎßå
          <UserView projectId={projectId} projectName={project.name} />
        )}
      </div>
    </div>
  );
};

export default ProjectDetail;
