  // Ïã§Ï†ú Bedrock Claude Ìò∏Ï∂ú
  generateTitle: async (projectId, articleContent, userRequest = "") => {
    console.log("üöÄ Ï†úÎ™© ÏÉùÏÑ± ÏãúÏûë:", {
      projectId,
      articleLength: articleContent.length,
