import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  PaperAirplaneIcon,
  DocumentPlusIcon,
  XMarkIcon,
  ChatBubbleLeftRightIcon,
  SparklesIcon,
  StopIcon,
} from "@heroicons/react/24/outline";
import { useChat } from "../hooks/useChat";
import AnimatedChatMessage from "./AnimatedChatMessage";
import ModelSelector from "./ModelSelector";

const ChatInterface = ({ projectId, projectName, promptCards = [] }) => {
  const navigate = useNavigate();
  const [uploadedFile, setUploadedFile] = useState(null);
  const [dragOver, setDragOver] = useState(false);

  const {
    messages,
    inputValue,
    setInputValue,
    handleInputChange,
    copiedMessage,
    isGenerating,
    canSendMessage,
    messagesEndRef,
    inputRef,
    inputHeight,
    handleSendMessage,
    handleStopGeneration,
    handleKeyPress,
    handleCopyMessage,
    handleCopyTitle,
    wsConnected,
    wsConnecting,
    wsError,
    // Ïä§ÌÅ¨Î°§ Í¥ÄÎ†® Ï∂îÍ∞Ä
    scrollContainerRef,
    handleScroll,
    isUserScrolling,
    // Î™®Îç∏ ÏÑ†ÌÉù Í¥ÄÎ†® Ï∂îÍ∞Ä
    selectedModel,
    setSelectedModel,
  } = useChat(projectId, projectName, promptCards);

  const handleFileUpload = (event) => {
    const file = event.target.files?.[0];
    if (file && file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (e) => {
        const content = e.target?.result || "";
        handleInputChange(content); // ÎÜíÏù¥ ÏûêÎèô Ï°∞Ï†à
        setUploadedFile(file);
      };
      reader.readAsText(file);
    }
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setDragOver(false);
    const files = event.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type === "text/plain") {
        const reader = new FileReader();
        reader.onload = (e) => {
          const content = e.target?.result || "";
          handleInputChange(content); // ÎÜíÏù¥ ÏûêÎèô Ï°∞Ï†à
          setUploadedFile(file);
        };
        reader.readAsText(file);
      }
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = () => {
    setDragOver(false);
  };

  const removeFile = () => {
    setUploadedFile(null);
    handleInputChange(""); // ÎÜíÏù¥ Ï¥àÍ∏∞Ìôî
  };

  return (
    <div className="flex flex-col h-full bg-gray-50 dark:bg-dark-primary transition-colors duration-300">
      {/* Î©îÏãúÏßÄ ÏòÅÏó≠ */}
      <div
        ref={scrollContainerRef}
        onScroll={handleScroll}
        className="flex-1 overflow-y-auto px-6 py-6 min-h-0 bg-gray-50 dark:bg-dark-primary"
      >
        <div className="max-w-4xl mx-auto space-y-6">
          {messages.length === 0 ? (
            <div className="flex flex-col items-center justify-center py-24 mt-12">
              <div className="bg-white dark:bg-dark-secondary card-dark rounded-2xl shadow-md p-8 max-w-2xl w-full transition-colors duration-300">
                <div className="flex justify-center mb-6">
                  <div className="bg-blue-100 dark:bg-dark-tertiary p-3 rounded-full">
                    <ChatBubbleLeftRightIcon className="h-8 w-8 text-blue-600 dark:text-blue-400" />
                  </div>
                </div>
                <h2 className="text-xl font-semibold text-center text-gray-800 dark:text-white mb-4">
                  AI Ìó§ÎìúÎùºÏù∏ Ïñ¥ÏãúÏä§ÌÑ¥Ìä∏
                </h2>
                <p className="text-gray-600 dark:text-gray-300 text-center mb-6">
                  Î≥∏Î¨∏ÏùÑ ÏûÖÎ†•ÌïòÎ©¥ AIÍ∞Ä ÏµúÏ†ÅÏùò Ìó§ÎìúÎùºÏù∏ÏùÑ Ï†úÏïàÌï¥ ÎìúÎ¶ΩÎãàÎã§.
                </p>

                <div className="space-y-4">
                  <div className="flex items-start p-3 bg-gray-50 dark:bg-dark-tertiary rounded-lg">
                    <div className="mr-3 mt-1">
                      <SparklesIcon className="h-5 w-5 text-blue-500 dark:text-blue-400" />
                    </div>
                    <div>
                      <p className="text-sm text-gray-700 dark:text-gray-300">
                        <span className="font-medium">ÏãúÏûëÌïòÎäî Î∞©Î≤ï:</span> ÏïÑÎûò
                        ÏûÖÎ†•Ï∞ΩÏóê Î≥∏Î¨∏ÏùÑ Î∂ôÏó¨ÎÑ£Í≥† Ï†ÑÏÜ° Î≤ÑÌäºÏùÑ ÌÅ¥Î¶≠ÌïòÏÑ∏Ïöî
                      </p>
                    </div>
                  </div>

                  <div className="flex items-start p-3 bg-gray-50 dark:bg-dark-tertiary rounded-lg">
                    <div className="mr-3 mt-1">
                      <SparklesIcon className="h-5 w-5 text-blue-500 dark:text-blue-400" />
                    </div>
                    <div>
                      <p className="text-sm text-gray-700 dark:text-gray-300">
                        <span className="font-medium">ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú:</span>{" "}
                        Í¥ÄÎ¶¨Ïûê Î™®ÎìúÏóêÏÑú ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥ÎìúÎ•º ÏûëÏÑ±ÌïòÏó¨ Ï†úÎ™©ÏùÑ
                        ÏÉùÏÑ±ÌïòÏÑ∏Ïöî
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ) : (
            messages.map((message) => (
              <AnimatedChatMessage
                key={message.id}
                message={message}
                onCopyMessage={handleCopyMessage}
                onCopyTitle={handleCopyTitle}
                copiedMessage={copiedMessage}
              />
            ))
          )}
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* ÏûÖÎ†• ÏòÅÏó≠ */}
      <div className="flex-shrink-0 px-6 py-4">
        <div className="max-w-4xl mx-auto">
          {/* ÏóÖÎ°úÎìúÎêú ÌååÏùº ÌëúÏãú */}
          {uploadedFile && (
            <div className="mb-3 p-3 bg-blue-50 dark:bg-dark-tertiary border border-blue-200 dark:border-dark rounded-lg flex items-center justify-between">
              <div className="flex items-center gap-2">
                <DocumentPlusIcon className="h-4 w-4 text-blue-600 dark:text-blue-400" />
                <span className="text-sm text-blue-700 dark:text-white">
                  {uploadedFile.name}
                </span>
              </div>
              <button
                onClick={removeFile}
                className="text-blue-600 dark:text-blue-400 hover:text-blue-800 dark:hover:text-blue-300 p-1"
              >
                <XMarkIcon className="h-4 w-4" />
              </button>
            </div>
          )}

          <div
            className="bg-white dark:bg-dark-secondary card-dark rounded-[20px] shadow-md p-4 relative transition-colors duration-300 focus:outline-none flex flex-col gap-6 border-0 dark:border-0"
            style={{
              minHeight: Math.max(128, inputHeight + 80),
              transition: "min-height 0.2s ease-in-out",
            }}
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
          >
            <div className="flex-1 relative focus:outline-none">
              <textarea
                ref={inputRef}
                value={inputValue}
                onChange={(e) => handleInputChange(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={
                  isGenerating
                    ? "ÏÉùÏÑ± Ï§ëÏûÖÎãàÎã§..."
                    : "Í∏∞ÏÇ¨ Î≥∏Î¨∏ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
                }
                className={`w-full pr-14 pl-2 border-0 focus:outline-none resize-none transition-all duration-300 ${
                  dragOver
                    ? "bg-blue-50 dark:bg-dark-tertiary"
                    : isGenerating
                    ? "bg-gray-50 dark:bg-dark-primary"
                    : "bg-white dark:bg-dark-secondary text-gray-900 dark:text-white"
                }`}
                rows={1}
                style={{
                  height: `${inputHeight}px`,
                  minHeight: "24px",
                  maxHeight: "400px",
                  lineHeight: "1.4",
                  paddingTop: "0px",
                  paddingBottom: "6px",
                  overflowY: inputHeight > 200 ? "auto" : "hidden",
                  whiteSpace: "pre-wrap",
                  fontSize: "16px",
                  fontWeight: "400",
                  color: isGenerating ? "var(--text-muted)" : "inherit",
                  outline: "none",
                  boxShadow: "none",
                }}
                disabled={isGenerating}
              />

              {/* Ï†ÑÏÜ°/Ï§ëÎã® Î≤ÑÌäº - ÏûÖÎ†•Ï∞Ω ÎÇ¥Î∂ÄÏóê ÏúÑÏπò */}
              <div
                className="absolute right-3 bottom-0 flex items-center gap-2"
                style={{ bottom: "2px" }}
              >
                {isGenerating && (
                  <button
                    onClick={handleStopGeneration}
                    className="flex-shrink-0 bg-red-600 hover:bg-red-700 text-white p-2 rounded-full transition-colors flex items-center justify-center"
                    style={{ width: "32px", height: "32px" }}
                    title="ÏÉùÏÑ± Ï§ëÎã®"
                  >
                    <StopIcon className="h-4 w-4" />
                  </button>
                )}
                <button
                  onClick={handleSendMessage}
                  disabled={!inputValue.trim() || isGenerating}
                  className="flex-shrink-0 bg-blue-600 hover:bg-blue-700 text-white p-2 rounded-full disabled:opacity-70 disabled:cursor-not-allowed transition-colors flex items-center justify-center"
                  style={{ width: "32px", height: "32px" }}
                  title={
                    isGenerating
                      ? "ÏÉùÏÑ± Ï§ë..."
                      : wsConnected
                      ? "Ïã§ÏãúÍ∞Ñ Ïä§Ìä∏Î¶¨Î∞ç ÏÇ¨Ïö© Í∞ÄÎä•"
                      : "ÏùºÎ∞ò Î™®Îìú (WebSocket Ïó∞Í≤∞ ÏïàÎê®)"
                  }
                >
                  {isGenerating ? (
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
                  ) : (
                    <PaperAirplaneIcon className="h-5 w-5" />
                  )}
                </button>
              </div>
            </div>

            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                {/* Í∏ÄÏûê Ïàò ÌëúÏãú */}
                <div className="flex items-center gap-2 text-xs text-gray-400 dark:text-dark-muted">
                  <span>{inputValue.length}Ïûê</span>
                </div>

                {/* Î™®Îç∏ ÏÑ†ÌÉùÍ∏∞ */}
                <div className="flex items-center gap-2">
                  <ModelSelector
                    selectedModel={selectedModel}
                    onModelChange={setSelectedModel}
                  />
                </div>
              </div>

              {/* WebSocket Ïó∞Í≤∞ ÏÉÅÌÉú Î∞è Ïä§ÌÅ¨Î°§ ÏÉÅÌÉú ÌëúÏãú */}
              <div className="flex items-center gap-3 text-xs">
                {/* Ïä§ÌÅ¨Î°§ ÏÉÅÌÉú ÌëúÏãú */}
                {isUserScrolling && (
                  <div className="flex items-center gap-1 text-blue-600 dark:text-blue-400">
                    <span>üìú</span>
                    <span>ÏûêÏú† Ïä§ÌÅ¨Î°§</span>
                  </div>
                )}

                {/* WebSocket Ïó∞Í≤∞ ÏÉÅÌÉú */}
                {wsConnecting ? (
                  <div className="flex items-center gap-1 text-yellow-600 dark:text-yellow-400">
                    <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-yellow-600"></div>
                    <span>Ïó∞Í≤∞ Ï§ë...</span>
                  </div>
