import React, { createContext, useContext, useReducer, useEffect, useMemo } from "react";
import { useAuth } from "./AuthContext";
import { conversationAPI } from "../services/api";

// ÎåÄÌôî ÏÉÅÌÉú Í¥ÄÎ¶¨Î•º ÏúÑÌïú Context
const ConversationContext = createContext();

// Ï¥àÍ∏∞ ÏÉÅÌÉú
const initialState = {
  currentConversationId: null,
  conversations: [],
  currentMessages: [],
  isLoading: false,
  error: null,
  drawerOpen: false,
};

// Ïï°ÏÖò ÌÉÄÏûÖ Ï†ïÏùò
const ActionTypes = {
  SET_CURRENT_CONVERSATION: "SET_CURRENT_CONVERSATION",
  SET_CONVERSATIONS: "SET_CONVERSATIONS",
  ADD_CONVERSATION: "ADD_CONVERSATION",
  UPDATE_CONVERSATION: "UPDATE_CONVERSATION",
  SET_MESSAGES: "SET_MESSAGES",
  ADD_MESSAGE: "ADD_MESSAGE",
  UPDATE_MESSAGE: "UPDATE_MESSAGE",
  SET_LOADING: "SET_LOADING",
  SET_ERROR: "SET_ERROR",
  TOGGLE_DRAWER: "TOGGLE_DRAWER",
  CLEAR_STATE: "CLEAR_STATE",
};

// Î¶¨ÎìÄÏÑú Ìï®Ïàò
const conversationReducer = (state, action) => {
  switch (action.type) {
    case ActionTypes.SET_CURRENT_CONVERSATION:
      return {
        ...state,
        currentConversationId: action.payload,
        currentMessages: [], // ÏÉà ÎåÄÌôî ÏÑ†ÌÉùÏãú Î©îÏãúÏßÄ Ï¥àÍ∏∞Ìôî
      };

    case ActionTypes.SET_CONVERSATIONS:
      return {
        ...state,
        conversations: action.payload,
      };

    case ActionTypes.ADD_CONVERSATION:
      return {
        ...state,
        conversations: [action.payload, ...state.conversations],
      };

    case ActionTypes.UPDATE_CONVERSATION:
      return {
        ...state,
        conversations: state.conversations.map((conv) =>
          conv.id === action.payload.id
            ? { ...conv, ...action.payload.updates }
            : conv
        ),
      };

    case ActionTypes.SET_MESSAGES:
      return {
        ...state,
        currentMessages: action.payload,
      };

    case ActionTypes.ADD_MESSAGE:
      return {
        ...state,
        currentMessages: [...state.currentMessages, action.payload],
      };

    case ActionTypes.UPDATE_MESSAGE:
      return {
        ...state,
        currentMessages: state.currentMessages.map((msg) =>
          msg.id === action.payload.id
            ? { ...msg, ...action.payload.updates }
            : msg
        ),
      };

    case ActionTypes.SET_LOADING:
      return {
        ...state,
        isLoading: action.payload,
      };

    case ActionTypes.SET_ERROR:
      return {
        ...state,
        error: action.payload,
      };

    case ActionTypes.TOGGLE_DRAWER:
      return {
        ...state,
        drawerOpen:
          action.payload !== undefined ? action.payload : !state.drawerOpen,
      };

    case ActionTypes.CLEAR_STATE:
      return initialState;

    default:
      return state;
  }
};

// Provider Ïª¥Ìè¨ÎÑåÌä∏
export const ConversationProvider = ({ children }) => {
  const [state, dispatch] = useReducer(conversationReducer, initialState);
  const { user } = useAuth();

  // ÌòÑÏû¨ ÎåÄÌôî Î≥ÄÍ≤Ω
  const setCurrentConversation = (conversationId) => {
    console.log("üîç [DEBUG] ConversationContext - ÎåÄÌôî Î≥ÄÍ≤Ω ÏöîÏ≤≠:", {
      previousConversationId: state.currentConversationId,
      newConversationId: conversationId,
      isChanged: state.currentConversationId !== conversationId,
      conversationIdType: typeof conversationId,
      isConversationIdNull: conversationId === null,
      isConversationIdUndefined: conversationId === undefined,
    });

    dispatch({
      type: ActionTypes.SET_CURRENT_CONVERSATION,
      payload: conversationId,
    });

    console.log(
      "üîç [DEBUG] ConversationContext - ÎåÄÌôî Î≥ÄÍ≤Ω dispatch ÏôÑÎ£å:",
      conversationId
    );
  };

  // ÎåÄÌôî Î™©Î°ù ÏÑ§Ï†ï
  const setConversations = (conversations) => {
    dispatch({
      type: ActionTypes.SET_CONVERSATIONS,
      payload: conversations,
    });
  };

  // ÏÉà ÎåÄÌôî Ï∂îÍ∞Ä
  const addConversation = (conversation) => {
    dispatch({
      type: ActionTypes.ADD_CONVERSATION,
      payload: conversation,
    });
  };

  // ÎåÄÌôî ÏóÖÎç∞Ïù¥Ìä∏
  const updateConversation = (conversationId, updates) => {
    dispatch({
      type: ActionTypes.UPDATE_CONVERSATION,
      payload: { id: conversationId, updates },
    });
  };

  // Î©îÏãúÏßÄ ÏÑ§Ï†ï
  const setMessages = (messages) => {
    dispatch({
      type: ActionTypes.SET_MESSAGES,
      payload: messages,
    });
  };

  // ÏÉà Î©îÏãúÏßÄ Ï∂îÍ∞Ä
  const addMessage = (message) => {
    const newMessage = {
      id: message.id || Date.now().toString(),
      role: message.role,
      content: message.content,
      tokenCount: message.tokenCount || 0,
      timestamp: message.timestamp || new Date().toISOString(),
    };

    dispatch({
      type: ActionTypes.ADD_MESSAGE,
      payload: newMessage,
    });

    // ÌòÑÏû¨ ÎåÄÌôîÏùò ÎßàÏßÄÎßâ ÌôúÎèô ÏãúÍ∞Ñ ÏóÖÎç∞Ïù¥Ìä∏
    if (state.currentConversationId) {
      updateConversation(state.currentConversationId, {
        lastActivityAt: newMessage.timestamp,
      });
    }

    return newMessage;
  };

  // Î©îÏãúÏßÄ ÏóÖÎç∞Ïù¥Ìä∏ (Ïä§Ìä∏Î¶¨Î∞ç Ï§ë)
  const updateMessage = (messageId, updates) => {
    dispatch({
      type: ActionTypes.UPDATE_MESSAGE,
      payload: { id: messageId, updates },
    });
  };

  // Î°úÎî© ÏÉÅÌÉú ÏÑ§Ï†ï
  const setLoading = (loading) => {
    dispatch({
      type: ActionTypes.SET_LOADING,
      payload: loading,
    });
  };

  // ÏóêÎü¨ ÏÑ§Ï†ï
  const setError = (error) => {
    dispatch({
      type: ActionTypes.SET_ERROR,
      payload: error,
    });
  };

  // ÎìúÎ°úÏñ¥ ÌÜ†Í∏Ä
  const toggleDrawer = (open) => {
    dispatch({
      type: ActionTypes.TOGGLE_DRAWER,
      payload: open,
    });
  };

  // ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî (Î°úÍ∑∏ÏïÑÏõÉÏãú)
  const clearState = () => {
    dispatch({
      type: ActionTypes.CLEAR_STATE,
    });
  };

  // ÌòÑÏû¨ ÎåÄÌôî Ï†ïÎ≥¥ Ï°∞Ìöå
  const getCurrentConversation = () => {
    return state.conversations.find(
      (conv) => conv.id === state.currentConversationId
    );
  };

  // ÏÇ¨Ïö©Ïûê Î≥ÄÍ≤ΩÏãú ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî Î∞è ÎåÄÌôî Î™©Î°ù Î°úÎìú
  useEffect(() => {
    if (user) {
      console.log("üîç [DEBUG] ConversationContext - ÏÇ¨Ïö©Ïûê Î°úÍ∑∏Ïù∏, Ï¥àÍ∏∞ ÎåÄÌôî Î™©Î°ù Î°úÎìú");

      // Ï¥àÍ∏∞ ÎåÄÌôî Î™©Î°ù Î°úÎìú
      const loadInitialConversations = async () => {
        try {
          console.log("üîç [DEBUG] ConversationContext - Ï¥àÍ∏∞ ÎåÄÌôî Î™©Î°ù Î°úÎìú ÏãúÏûë");
          const response = await conversationAPI.getConversations();
          console.log("üîç [DEBUG] ConversationContext - Ï¥àÍ∏∞ ÎåÄÌôî Î™©Î°ù Î°úÎìú ÏôÑÎ£å:", response.conversations?.length);
          setConversations(response.conversations || []);
        } catch (error) {
          console.error("ConversationContext - Ï¥àÍ∏∞ ÎåÄÌôî Î™©Î°ù Î°úÎìú Ïã§Ìå®:", error);
          // Ïã§Ìå®Ìï¥ÎèÑ Í∏∞Î≥∏ Îπà Î∞∞Ïó¥ Ïú†ÏßÄ
        }
      };

      loadInitialConversations();
    } else {
      clearState();
    }
  }, [user]);

  // Context value ÏµúÏ†ÅÌôî - useMemoÎ°ú Î∂àÌïÑÏöîÌïú Ïû¨Î†åÎçîÎßÅ Î∞©ÏßÄ
  const value = useMemo(() => ({
    // ÏÉÅÌÉú
    ...state,

    // Ïï°ÏÖò Ìï®ÏàòÎì§
    setCurrentConversation,
    setConversations,
    addConversation,
    updateConversation,
    setMessages,
    addMessage,
    updateMessage,
    setLoading,
    setError,
    toggleDrawer,
    clearState,
    getCurrentConversation,
  }), [
    state,
    setCurrentConversation,
    setConversations,
    addConversation,
    updateConversation,
    setMessages,
    addMessage,
    updateMessage,
    setLoading,
    setError,
    toggleDrawer,
    clearState,
    getCurrentConversation,
  ]);

  return (
    <ConversationContext.Provider value={value}>
      {children}
    </ConversationContext.Provider>
  );
};

// Hook for using conversation context
export const useConversationContext = () => {
  const context = useContext(ConversationContext);
  if (!context) {
    throw new Error(
      "useConversationContext must be used within a ConversationProvider"
    );
  }
  return context;
};
