import axios from "axios";

// API ê¸°ë³¸ ì„¤ì •
const API_BASE_URL = process.env.REACT_APP_API_URL || "https://nq5qrt16lb.execute-api.us-east-1.amazonaws.com/prod";

// Mock data for development
const mockUsageData = {
  todayRequests: 127,
  todayTokens: 45320,
  monthlyLimit: 1000000,
  monthlyUsed: 523400,
  plan: {
    name: 'Professional',
    expiresAt: '2025-02-28',
    features: ['ì›” 100ë§Œ í† í°', 'ìš°ì„  ì§€ì›', 'API ì•¡ì„¸ìŠ¤']
  },
  chartData: Array.from({ length: 30 }, (_, i) => ({
    date: new Date(Date.now() - (29 - i) * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
    tokens: Math.floor(Math.random() * 30000) + 10000,
    requests: Math.floor(Math.random() * 100) + 50
  })),
  recentLogs: Array.from({ length: 20 }, (_, i) => ({
    id: `log-${i}`,
    timestamp: new Date(Date.now() - i * 60 * 60 * 1000).toISOString(),
    model: ['Claude 3 Sonnet', 'Claude 3.5 Haiku', 'Claude 3 Opus'][Math.floor(Math.random() * 3)],
    tokens: Math.floor(Math.random() * 5000) + 1000,
    duration: Math.floor(Math.random() * 3000) + 500,
    status: Math.random() > 0.1 ? 'success' : 'error'
  }))
};

// Axios ì¸ìŠ¤í„´ìŠ¤
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { "Content-Type": "application/json" },
  timeout: 300000, // 5ë¶„
});

// ìš”ì²­ ì¸í„°ì…‰í„° - ì¸ì¦ í† í° ìë™ ì¶”ê°€
api.interceptors.request.use(async (config) => {
  console.log("API ìš”ì²­:", config.method?.toUpperCase(), config.url);

  // ì¸ì¦ì´ í•„ìš”í•œ ìš”ì²­ì— í† í° ì¶”ê°€
  try {
    // AuthContextì—ì„œ í† í° ê°€ì ¸ì˜¤ê¸° (ë™ì  import ì‚¬ìš©)
    const { fetchAuthSession } = await import('aws-amplify/auth');
    const session = await fetchAuthSession();
    const token = session?.tokens?.idToken?.toString();

    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
      console.log("âœ… ì¸ì¦ í† í° ì¶”ê°€ë¨");
    } else {
      console.log("âš ï¸ ì¸ì¦ í† í° ì—†ìŒ");
    }
  } catch (error) {
    console.log("ğŸ“ ì¸ì¦ í† í° ê°€ì ¸ì˜¤ê¸° ì‹¤íŒ¨:", error.message);
    // ì¸ì¦ ì˜¤ë¥˜ê°€ ìˆì–´ë„ ìš”ì²­ì€ ê³„ì† ì§„í–‰ (public APIë„ ìˆì„ ìˆ˜ ìˆìŒ)
  }

  return config;
});

// ì‘ë‹µ ì¸í„°ì…‰í„° - 401 ì˜¤ë¥˜ ì‹œ ë¦¬ë‹¤ì´ë ‰ì…˜ ì²˜ë¦¬
api.interceptors.response.use(
  (response) => {
    console.log("API ì‘ë‹µ:", response.status, response.config.url);
    return response;
  },
  async (error) => {
    console.error("API ì˜¤ë¥˜ ìƒì„¸:", {
      status: error.response?.status,
      message: error.message,
      code: error.code,
      url: error.config?.url,
      data: error.response?.data,
    });

    // 401 Unauthorized ì˜¤ë¥˜ ì²˜ë¦¬
    if (error.response?.status === 401) {
      console.log("ğŸ” ì¸ì¦ ì˜¤ë¥˜ ë°œìƒ - ë¡œê·¸ì¸ í˜ì´ì§€ë¡œ ë¦¬ë‹¤ì´ë ‰ì…˜");

      try {
        // ë¡œê·¸ì•„ì›ƒ ì²˜ë¦¬
        const { signOut } = await import('aws-amplify/auth');
        await signOut();

        // ë¡œê·¸ì¸ í˜ì´ì§€ë¡œ ë¦¬ë‹¤ì´ë ‰ì…˜
        window.location.href = '/login';
      } catch (signOutError) {
        console.error("ë¡œê·¸ì•„ì›ƒ ì²˜ë¦¬ ì‹¤íŒ¨:", signOutError);
        // ê°•ì œ ë¦¬ë‹¤ì´ë ‰ì…˜
        window.location.href = '/login';
      }
    }

    return Promise.reject(error);
  }
);

// =============================================================================
// ğŸ”„ ë°ì´í„° ë§¤í•‘ ìœ í‹¸ë¦¬í‹° í•¨ìˆ˜ë“¤
// =============================================================================

/**
 * í”„ë¡ íŠ¸ì—”ë“œ â†’ ë°±ì—”ë“œ ë°ì´í„° ë³€í™˜
 */
const mapFrontendToBackend = {
  // ì±„íŒ… ë©”ì‹œì§€ ë°ì´í„° ë³€í™˜
  chatMessage: (frontendData) => ({
    userInput: frontendData.userInput || frontendData.message,
    chat_history: frontendData.chat_history || frontendData.messages || [],
    prompt_cards: frontendData.promptCards || frontendData.prompt_cards || [],
    modelId: frontendData.selectedModel || frontendData.modelId,
    conversationId: frontendData.conversationId || frontendData.conversation_id,
    userSub: frontendData.userId || frontendData.user_id,
  }),

  // í”„ë¡¬í”„íŠ¸ ì¹´ë“œ ë°ì´í„° ë³€í™˜
  promptCard: (frontendData) => ({
    title: frontendData.title,
    prompt_text: frontendData.prompt_text || frontendData.content,
    tags: frontendData.tags || [],
    isActive: frontendData.enabled !== false && frontendData.isActive !== false,
    stepOrder: frontendData.stepOrder || 1,
  }),

  // í”„ë¡œì íŠ¸ ë°ì´í„° ë³€í™˜
  project: (frontendData) => ({
    name: frontendData.name,
    description: frontendData.description || '',
    tags: frontendData.tags || [],
    aiRole: frontendData.aiRole || '',
    aiInstructions: frontendData.aiInstructions || '',
    targetAudience: frontendData.targetAudience || 'ì¼ë°˜ë…ì',
    outputFormat: frontendData.outputFormat || 'multiple',
    styleGuidelines: frontendData.styleGuidelines || '',
  }),
};

/**
 * ë°±ì—”ë“œ â†’ í”„ë¡ íŠ¸ì—”ë“œ ë°ì´í„° ë³€í™˜
 */
const mapBackendToFrontend = {
  // ì±„íŒ… ë©”ì‹œì§€ ë³€í™˜
  chatMessage: (backendData) => ({
    id: backendData.id || backendData.messageId || Date.now().toString(),
    role: backendData.role,
    content: backendData.content || backendData.text,
    timestamp: backendData.timestamp || backendData.createdAt || new Date().toISOString(),
    tokenCount: backendData.tokenCount || backendData.tokens_used,
  }),

  // í”„ë¡¬í”„íŠ¸ ì¹´ë“œ ë³€í™˜
  promptCard: (backendData) => ({
    promptId: backendData.promptId || backendData.prompt_id,
    title: backendData.title,
    prompt_text: backendData.prompt_text || backendData.content,
    tags: backendData.tags || [],
    isActive: backendData.isActive !== false,
    enabled: backendData.isActive !== false,
    stepOrder: backendData.stepOrder || 1,
    createdAt: backendData.createdAt,
    updatedAt: backendData.updatedAt,
  }),

  // í”„ë¡œì íŠ¸ ë³€í™˜
  project: (backendData) => ({
    projectId: backendData.projectId,
    name: backendData.name,
    description: backendData.description || '',
    status: backendData.status,
    tags: backendData.tags || [],
    aiRole: backendData.aiRole || '',
    aiInstructions: backendData.aiInstructions || '',
    targetAudience: backendData.targetAudience || 'ì¼ë°˜ë…ì',
    outputFormat: backendData.outputFormat || 'multiple',
    styleGuidelines: backendData.styleGuidelines || '',
    createdAt: backendData.createdAt,
    updatedAt: backendData.updatedAt,
    promptCount: backendData.promptCount || 0,
    conversationCount: backendData.conversationCount || 0,
  }),

  // ëŒ€í™” ëª©ë¡ ë³€í™˜
  conversation: (backendData) => ({
    id: backendData.id || backendData.conversationId,
    title: backendData.title,
    startedAt: backendData.startedAt || backendData.createdAt,
    lastActivityAt: backendData.lastActivityAt || backendData.updatedAt,
    tokenSum: backendData.tokenSum || backendData.totalTokens || 0,
  }),
};

/**
 * Mock ë°ì´í„°ì™€ ì‹¤ì œ API ê°„ ì „í™˜ì„ ìœ„í•œ í”Œë˜ê·¸
 */
const USE_MOCK_DATA = process.env.REACT_APP_USE_MOCK_DATA === 'true';

// =============================================================================
// í”„ë¡œì íŠ¸ API (ê¸°ì¡´ ìœ ì§€)
// =============================================================================

export const projectAPI = {
  getProjects: async () => {
    try {
      const response = await api.get("/projects");
      // ë°±ì—”ë“œ ë°ì´í„°ë¥¼ í”„ë¡ íŠ¸ì—”ë“œ í˜•ì‹ìœ¼ë¡œ ë³€í™˜
      const projects = response.data.projects || response.data;
      return {
        projects: Array.isArray(projects) ? projects.map(mapBackendToFrontend.project) : [],
        count: response.data.count || projects.length,
        hasMore: response.data.hasMore || false,
        nextKey: response.data.nextKey,
      };
    } catch (error) {
      console.error("í”„ë¡œì íŠ¸ ëª©ë¡ ì¡°íšŒ ì‹¤íŒ¨:", error);
      throw error;
    }
  },

  getProject: async (projectId) => {
    try {
      const response = await api.get(`/projects/${projectId}`);
      // ë°±ì—”ë“œ ë°ì´í„°ë¥¼ í”„ë¡ íŠ¸ì—”ë“œ í˜•ì‹ìœ¼ë¡œ ë³€í™˜
      return mapBackendToFrontend.project(response.data);
    } catch (error) {
      console.error("í”„ë¡œì íŠ¸ ìƒì„¸ ì¡°íšŒ ì‹¤íŒ¨:", error);
      throw error;
    }
  },

  createProject: async (projectData) => {
    try {
      // í”„ë¡ íŠ¸ì—”ë“œ ë°ì´í„°ë¥¼ ë°±ì—”ë“œ í˜•ì‹ìœ¼ë¡œ ë³€í™˜
      const backendData = mapFrontendToBackend.project(projectData);
      const response = await api.post("/projects", backendData);
      // ì‘ë‹µì„ í”„ë¡ íŠ¸ì—”ë“œ í˜•ì‹ìœ¼ë¡œ ë³€í™˜
      return mapBackendToFrontend.project(response.data);
    } catch (error) {
      console.error("í”„ë¡œì íŠ¸ ìƒì„± ì‹¤íŒ¨:", error);
      throw error;
    }
  },

  updateProject: async (projectId, projectData) => {
    try {
      // í”„ë¡ íŠ¸ì—”ë“œ ë°ì´í„°ë¥¼ ë°±ì—”ë“œ í˜•ì‹ìœ¼ë¡œ ë³€í™˜
      const backendData = mapFrontendToBackend.project(projectData);
      const response = await api.put(`/projects/${projectId}`, backendData);
      // ì‘ë‹µì„ í”„ë¡ íŠ¸ì—”ë“œ í˜•ì‹ìœ¼ë¡œ ë³€í™˜
      return mapBackendToFrontend.project(response.data);
    } catch (error) {
      console.error("í”„ë¡œì íŠ¸ ì—…ë°ì´íŠ¸ ì‹¤íŒ¨:", error);
      throw error;
    }
  },

  deleteProject: async (projectId) => {
    try {
      const response = await api.delete(`/projects/${projectId}`);
      return response.data;
    } catch (error) {
      console.error("í”„ë¡œì íŠ¸ ì‚­ì œ ì‹¤íŒ¨:", error);
      throw error;
    }
  },

  getUploadUrl: async (projectId, fileName) => {
    try {
      const response = await api.get(`/projects/${projectId}/upload-url`, {
        params: { fileName },
      });
      return response.data;
    } catch (error) {
      console.error("ì—…ë¡œë“œ URL ìƒì„± ì‹¤íŒ¨:", error);
      throw error;
    }
  },
};

// =============================================================================
// í”„ë¡¬í”„íŠ¸ ì¹´ë“œ API (ê¸°ì¡´ ìœ ì§€)
// =============================================================================

export const promptCardAPI = {
  getPromptCards: async (
    projectId,
    includeContent = false,
    includeStats = false
  ) => {
    const response = await api.get(`/prompts/${projectId}`, {
      params: { includeContent, includeStats },
    });
    return response.data;
  },

  createPromptCard: async (projectId, promptData) => {
    const response = await api.post(`/prompts/${projectId}`, promptData);
    return response.data;
  },

  updatePromptCard: async (projectId, promptId, promptData) => {
    const response = await api.put(
      `/prompts/${projectId}/${promptId}`,
      promptData
    );
    return response.data;
  },

  getPromptContent: async (projectId, promptId) => {
    const response = await api.get(`/prompts/${projectId}/${promptId}/content`);
    return response.data;
  },

  deletePromptCard: async (projectId, promptId) => {
    const response = await api.delete(`/prompts/${projectId}/${promptId}`);
    return response.data;
  },

  reorderPromptCards: async (projectId, reorderData) => {
    const updatePromises = reorderData.map(({ promptId, stepOrder }) =>
      api.put(`/prompts/${projectId}/${promptId}`, { stepOrder })
    );

    const responses = await Promise.all(updatePromises);
    return {
      message: "í”„ë¡¬í”„íŠ¸ ì¹´ë“œ ìˆœì„œê°€ ì—…ë°ì´íŠ¸ë˜ì—ˆìŠµë‹ˆë‹¤.",
      updatedCards: responses.map((r) => r.data),
    };
  },
};

// =============================================================================
// ğŸ”§ ì™„ì „ ìˆ˜ì •ëœ ì œëª© ìƒì„± API
// =============================================================================

export const generateAPI = {
  generateTitle: async (projectId, data) => {
    console.log("ëŒ€í™” ìƒì„± ìš”ì²­ ì‹œì‘:", {
      projectId,
      inputLength: data.userInput.length,
      historyLength: data.chat_history?.length || 0,
      timestamp: new Date().toISOString(),
    });

    try {
      const response = await api.post(`/projects/${projectId}/generate`, data);

      console.log("ëŒ€í™” ìƒì„± ì„±ê³µ:", {
        status: response.status,
        mode: response.data.mode,
        message: response.data.message,
        timestamp: new Date().toISOString(),
      });

      return response.data;
    } catch (error) {
      console.error("ëŒ€í™” ìƒì„± ì‹¤íŒ¨:", {
        code: error.code,
        message: error.message,
        status: error.response?.status,
        responseData: error.response?.data,
        timestamp: new Date().toISOString(),
      });
      throw error;
    }
  },

  // ğŸ”§ ì‹¤ì œ ìŠ¤íŠ¸ë¦¬ë° êµ¬í˜„ - Server-Sent Events ì‚¬ìš©
  generateTitleStream: async (
    projectId,
    data,
    onChunk,
    onError,
    onComplete
  ) => {
    console.log("ìŠ¤íŠ¸ë¦¬ë° ëŒ€í™” ìƒì„± ìš”ì²­ ì‹œì‘:", {
      projectId,
      inputLength: data.userInput.length,
      historyLength: data.chat_history?.length || 0,
      timestamp: new Date().toISOString(),
    });

    try {
      // 1. ë¨¼ì € ì‹¤ì œ ìŠ¤íŠ¸ë¦¬ë° API ì‹œë„
      const streamingUrl = `${API_BASE_URL}/projects/${projectId}/generate/stream`;

      console.log("ğŸš€ ì‹¤ì œ ìŠ¤íŠ¸ë¦¬ë° API ì‹œë„:", streamingUrl);

      const response = await fetch(streamingUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'text/event-stream',
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      // 2. ì‘ë‹µì´ ìŠ¤íŠ¸ë¦¬ë° í˜•ì‹ì¸ì§€ í™•ì¸
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('text/event-stream')) {
        console.log("âŒ ìŠ¤íŠ¸ë¦¬ë° ì‘ë‹µì´ ì•„ë‹˜, í´ë°± ì²˜ë¦¬");
        throw new Error("ìŠ¤íŠ¸ë¦¬ë° ì‘ë‹µì´ ì•„ë‹™ë‹ˆë‹¤");
      }

      // 3. ì‹¤ì œ ìŠ¤íŠ¸ë¦¬ë° ì²˜ë¦¬
      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let buffer = '';
      let fullResponse = '';

      try {
        while (true) {
          const { done, value } = await reader.read();
          if (done) break;

          buffer += decoder.decode(value, { stream: true });
          const lines = buffer.split('\n');
          buffer = lines.pop() || '';

          for (const line of lines) {
            if (line.startsWith('data: ')) {
              try {
                const eventData = JSON.parse(line.slice(6));

                if (eventData.type === 'start') {
                  console.log("âœ… ìŠ¤íŠ¸ë¦¬ë° ì‹œì‘");
                } else if (eventData.type === 'chunk') {
                  fullResponse += eventData.response;
                  if (onChunk) {
                    onChunk(eventData.response, { content: eventData.response });
                  }
                } else if (eventData.type === 'complete') {
                  console.log("âœ… ìŠ¤íŠ¸ë¦¬ë° ì™„ë£Œ");
                  if (onComplete) {
                    onComplete({
                      result: eventData.fullResponse || fullResponse,
                      timestamp: new Date().toISOString(),
                    });
                  }
                  return { result: eventData.fullResponse || fullResponse };
                } else if (eventData.type === 'error') {
                  throw new Error(eventData.error);
                }
              } catch (parseError) {
                console.error("JSON íŒŒì‹± ì˜¤ë¥˜:", parseError);
              }
            }
          }
        }
      } finally {
        reader.releaseLock();
      }

      return { result: fullResponse };

    } catch (streamError) {
      console.log("âš ï¸ ìŠ¤íŠ¸ë¦¬ë° ì‹¤íŒ¨, í´ë°± ì²˜ë¦¬:", streamError.message);

      // 4. í´ë°±: ì¼ë°˜ API í˜¸ì¶œ
      try {
        const fallbackResponse = await api.post(
          `/projects/${projectId}/generate`,
          data
        );

        console.log("âœ… í´ë°± API ì„±ê³µ:", {
          mode: fallbackResponse.data.mode,
          timestamp: new Date().toISOString(),
        });

        // í´ë°± ì‘ë‹µì„ ìŠ¤íŠ¸ë¦¬ë°ì²˜ëŸ¼ ì‹œë®¬ë ˆì´ì…˜
        if (fallbackResponse.data.result && onChunk) {
          const fullText = fallbackResponse.data.result;
          const words = fullText.split(" ");

          for (let i = 0; i < words.length; i++) {
            const word = words[i] + (i < words.length - 1 ? " " : "");
            onChunk(word, { content: word });
            await new Promise((resolve) => setTimeout(resolve, 30));
          }
        }

        // ì™„ë£Œ ì½œë°± í˜¸ì¶œ
        if (onComplete) {
          onComplete({
            result: fallbackResponse.data.result,
            model_info: fallbackResponse.data.model_info,
            performance_metrics: fallbackResponse.data.performance_metrics,
            timestamp: new Date().toISOString(),
          });
        }

        return fallbackResponse.data;
      } catch (fallbackError) {
        console.error("âŒ í´ë°± APIë„ ì‹¤íŒ¨:", fallbackError);
        if (onError) {
          onError(
            new Error("ì„œë¹„ìŠ¤ì— ì—°ê²°í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤. ì ì‹œ í›„ ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”.")
          );
        }
        throw new Error("ì„œë¹„ìŠ¤ë¥¼ ì‚¬ìš©í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.");
      }
    }
  },

  getExecutionStatus: async (executionArn) => {
    return {
      status: "SUCCEEDED",
      output: "{}",
    };
  },
};

// =============================================================================
// ğŸ†• CrewAI ë©€í‹°-ì—ì´ì „íŠ¸ API (ìƒˆë¡œ ì¶”ê°€)
// =============================================================================

export const crewAPI = {
  // í”„ë¡¬í”„íŠ¸ ì¸ìŠ¤í„´ìŠ¤ ìƒì„± (í”„ë¡¬í”„íŠ¸ ì¹´ë“œë“¤ì„ ì—ì´ì „íŠ¸ë¡œ ë³€í™˜)
