import React, { useState, useEffect, useRef } from "react";
import ReactMarkdown from "react-markdown";
import { useNavigate } from "react-router-dom";
import {
  PaperAirplaneIcon,
  ClipboardDocumentIcon,
  CheckCircleIcon,
  UserIcon,
  CpuChipIcon,
  ArrowLeftIcon,
} from "@heroicons/react/24/outline";
import { toast } from "react-hot-toast";
import { generateAPI } from "../services/api";

const ChatInterface = ({ projectId, projectName }) => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [copiedMessage, setCopiedMessage] = useState(null);
  const [streamingMessage, setStreamingMessage] = useState("");
  const [isStreaming, setIsStreaming] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);
  const streamingRef = useRef(null);

  // ëŒ€í™” ì„¸ì…˜ ì´ˆê¸°í™”
  useEffect(() => {
    const welcomeMessage = {
      id: Date.now(),
      type: "ai",
      content: `ì•ˆë…•í•˜ì„¸ìš”! ì €ëŠ” ${projectName}ì˜ AI ì œëª© ì‘ê°€ì…ë‹ˆë‹¤. ğŸ¯\n\nê¸°ì‚¬ ë‚´ìš©ì„ ì…ë ¥í•´ì£¼ì‹œë©´ ë‹¤ì–‘í•œ ìŠ¤íƒ€ì¼ì˜ ì œëª©ì„ ì œì•ˆí•´ë“œë¦´ê²Œìš”. ì œëª©ì„ ìˆ˜ì •í•˜ê±°ë‚˜ ë‹¤ë¥¸ ìŠ¤íƒ€ì¼ë¡œ ë°”ê¾¸ê³  ì‹¶ìœ¼ì‹œë©´ ì–¸ì œë“  ë§ì”€í•´ì£¼ì„¸ìš”!`,
      timestamp: new Date(),
    };
    setMessages([welcomeMessage]);
  }, [projectName]);

  // ë©”ì‹œì§€ ì¶”ê°€ ì‹œ ìŠ¤í¬ë¡¤ í•˜ë‹¨ìœ¼ë¡œ
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  // ê°œì„ ëœ ìŠ¤íŠ¸ë¦¬ë° íƒ€ì´í•‘ íš¨ê³¼ (ì‚¬ê³ ê³¼ì • í¬í•¨)
  const simulateStreamingWithThinking = (text, onComplete) => {
    setIsStreaming(true);
    setStreamingMessage("");

    // 1ë‹¨ê³„: ì‚¬ê³ ê³¼ì • í‘œì‹œ (2ì´ˆ)
    const thinkingMessages = [
      "ğŸ¤” ê¸°ì‚¬ ë‚´ìš©ì„ ë¶„ì„í•˜ê³  ìˆìŠµë‹ˆë‹¤...",
      "ğŸ“ í•µì‹¬ í‚¤ì›Œë“œë¥¼ ì¶”ì¶œí•˜ê³  ìˆìŠµë‹ˆë‹¤...", 
      "ğŸ’¡ ë‹¤ì–‘í•œ ì œëª© í›„ë³´ë¥¼ ìƒì„±í•˜ê³  ìˆìŠµë‹ˆë‹¤...",
      "âœ¨ ìµœì ì˜ ì œëª©ì„ ì„ ë³„í•˜ê³  ìˆìŠµë‹ˆë‹¤..."
    ];

    let thinkingIndex = 0;
    const thinkingInterval = setInterval(() => {
      if (thinkingIndex < thinkingMessages.length) {
        setStreamingMessage(thinkingMessages[thinkingIndex]);
        thinkingIndex++;
        scrollToBottom();
      } else {
        clearInterval(thinkingInterval);

        // 2ë‹¨ê³„: ì‹¤ì œ ë‹µë³€ ìŠ¤íŠ¸ë¦¬ë°
        setStreamingMessage("");
        let textIndex = 0;
        const textInterval = setInterval(() => {
          if (textIndex < text.length) {
            setStreamingMessage((prev) => prev + text[textIndex]);
            textIndex++;
            scrollToBottom();
          } else {
            clearInterval(textInterval);
            setIsStreaming(false);
            setStreamingMessage("");
            onComplete();
          }
        }, 12); // ì¡°ê¸ˆ ë” ë¹ ë¥¸ íƒ€ì´í•‘

        streamingRef.current = textInterval;
      }
    }, 500); // ì‚¬ê³ ê³¼ì • ê° ë‹¨ê³„ë§ˆë‹¤ 0.5ì´ˆ

    streamingRef.current = thinkingInterval;
  };

  // ë©”ì‹œì§€ ì „ì†¡
  const handleSendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      type: "user",
      content: inputMessage,
      timestamp: new Date(),
    };

    setMessages((prev) => [...prev, userMessage]);
    const currentInput = inputMessage;
    setInputMessage("");
    setIsLoading(true);

    try {
      // ëŒ€í™” ì»¨í…ìŠ¤íŠ¸ ìƒì„± (ìµœê·¼ 5ê°œ ë©”ì‹œì§€)
      const recentMessages = messages.slice(-5);
      const conversationContext = recentMessages
        .map(
          (msg) =>
            `${msg.type === "user" ? "Human" : "Assistant"}: ${msg.content}`
        )
        .join("\n\n");

      const contextualInput =
        recentMessages.length > 0
          ? `[ì´ì „ ëŒ€í™” ì»¨í…ìŠ¤íŠ¸]\n${conversationContext}\n\n[ìƒˆë¡œìš´ ìš”ì²­]\nHuman: ${currentInput}`
          : currentInput;

      const result = await generateAPI.generateTitle(
        projectId,
        contextualInput,
        null // onProgress callback
      );

      // ì‚¬ê³ ê³¼ì •ì´ í¬í•¨ëœ ìŠ¤íŠ¸ë¦¬ë° íš¨ê³¼ë¡œ AI ì‘ë‹µ ì¶œë ¥
      simulateStreamingWithThinking(result.result, () => {
        const aiMessage = {
          id: Date.now() + 1,
          type: "ai",
          content: result.result,
          timestamp: new Date(),
          debug: result.debug,
        };
        setMessages((prev) => [...prev, aiMessage]);
      });
    } catch (error) {
      console.error("ë©”ì‹œì§€ ì „ì†¡ ì‹¤íŒ¨:", error);
      const errorMessage = {
        id: Date.now() + 1,
        type: "ai",
        content:
          "ì£„ì†¡í•©ë‹ˆë‹¤. ë©”ì‹œì§€ ì²˜ë¦¬ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤. ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”.",
        timestamp: new Date(),
        isError: true,
      };
      setMessages((prev) => [...prev, errorMessage]);
      toast.error("ë©”ì‹œì§€ ì „ì†¡ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.");
    } finally {
      setIsLoading(false);
    }
  };

  // Enter í‚¤ë¡œ ì „ì†¡
  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // ì œëª©ë§Œ ì¶”ì¶œí•˜ì—¬ ë³µì‚¬
  const copyTitlesOnly = (text, messageId) => {
    const extractedTitles = extractTitles(text);
    const textToCopy =
      extractedTitles.length > 0 ? extractedTitles.join("\n") : text;

    navigator.clipboard
      .writeText(textToCopy)
      .then(() => {
        setCopiedMessage(messageId);
        toast.success("ì œëª©ì´ í´ë¦½ë³´ë“œì— ë³µì‚¬ë˜ì—ˆìŠµë‹ˆë‹¤!");
        setTimeout(() => setCopiedMessage(null), 2000);
      })
      .catch(() => {
        toast.error("ë³µì‚¬ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.");
      });
  };

  // ì „ì²´ í…ìŠ¤íŠ¸ ë³µì‚¬
  const copyFullText = (text, messageId) => {
    navigator.clipboard
      .writeText(text)
      .then(() => {
        setCopiedMessage(messageId + "_full");
        toast.success("ì „ì²´ í…ìŠ¤íŠ¸ê°€ ë³µì‚¬ë˜ì—ˆìŠµë‹ˆë‹¤!");
        setTimeout(() => setCopiedMessage(null), 2000);
      })
      .catch(() => {
        toast.error("ë³µì‚¬ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.");
      });
  };

  // ì œëª© ì¶”ì¶œ í•¨ìˆ˜ - ë‹¤ì–‘í•œ í˜•ì‹ ì§€ì›
  const extractTitles = (text) => {
    const titles = [];

    // 1. ë²ˆí˜¸ í˜•ì‹: "1. [ì œëª©]" (ìƒˆ í…œí”Œë¦¿)
    const numberedMatches = text.match(/^\d+\.\s+(.+?)(?=\n\s*-|$)/gm);
    if (numberedMatches) {
      numberedMatches.forEach((match) => {
        const title = match.replace(/^\d+\.\s+/, "").trim();
        if (title && !title.includes("í’ˆì§ˆ í‰ê°€")) {
          titles.push(title);
        }
      });
    }

    // 2. Bullet í˜•ì‹: "â€¢ [ì œëª©]" (í˜„ì¬ ì¶œë ¥ í˜•ì‹)
    const bulletMatches = text.match(/â€¢ "([^"]+)"/g);
    if (bulletMatches) {
      bulletMatches.forEach((match) => {
        const title = match.replace(/â€¢ "(.+)"/, "$1").trim();
        if (title) {
          titles.push(title);
        }
      });
    }

    // 3. ì¼ë°˜ Bullet í˜•ì‹: "â€¢ [ì œëª©]"
    if (titles.length === 0) {
      const simpleBulletMatches = text.match(/â€¢ ([^\n]+)/g);
      if (simpleBulletMatches) {
        simpleBulletMatches.forEach((match) => {
          const title = match.replace("â€¢ ", "").trim();
          // ì„¤ëª…ë¬¸ì´ ì•„ë‹Œ ì œëª©ë§Œ ì¶”ì¶œ
          if (
            title &&
            !title.includes("ì´ìœ :") &&
            !title.includes("ì´ê²½ê·œì˜") &&
            title.length < 100
          ) {
            titles.push(title);
          }
        });
      }
    }

    return titles;
  };

  // ê°œë³„ ì œëª© ë³µì‚¬
  const copyIndividualTitle = (title, messageId, index) => {
    navigator.clipboard
      .writeText(title)
      .then(() => {
        setCopiedMessage(`${messageId}_title_${index}`);
        toast.success("ì œëª©ì´ ë³µì‚¬ë˜ì—ˆìŠµë‹ˆë‹¤!");
        setTimeout(() => setCopiedMessage(null), 2000);
      })
      .catch(() => {
        toast.error("ë³µì‚¬ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.");
      });
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-lg shadow-sm border border-gray-200">
      {/* ì±„íŒ… í—¤ë” */}
      <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-white rounded-t-lg">
        <div className="flex items-center space-x-3">
          <button
            onClick={() => navigate("/")}
            className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors"
            title="í”„ë¡œì íŠ¸ ëª©ë¡ìœ¼ë¡œ ëŒì•„ê°€ê¸°"
          >
            <ArrowLeftIcon className="h-5 w-5" />
          </button>
          <CpuChipIcon className="h-5 w-5 text-gray-600" />
          <h3 className="text-md font-medium text-gray-900">AI ëŒ€í™”</h3>
          <span className="text-sm text-gray-500">{projectName}</span>
        </div>
        <button
          onClick={() => setMessages(messages.slice(0, 1))} // ì²« ë²ˆì§¸ í™˜ì˜ ë©”ì‹œì§€ë§Œ ë‚¨ê¸°ê¸°
          className="text-sm text-gray-500 hover:text-gray-700 px-2 py-1 rounded hover:bg-gray-100 transition-colors"
        >
          ì´ˆê¸°í™”
        </button>
      </div>

      {/* ë©”ì‹œì§€ ì˜ì—­ */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 min-h-0">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`flex ${
              message.type === "user" ? "justify-end" : "justify-start"
            }`}
          >
            <div
              className={`flex max-w-[80%] ${
                message.type === "user" ? "flex-row-reverse" : "flex-row"
              }`}
            >
              {/* ì•„ë°”íƒ€ */}
              <div
                className={`flex-shrink-0 ${
                  message.type === "user" ? "ml-3" : "mr-3"
                }`}
              >
                <div
                  className={`w-8 h-8 rounded-full flex items-center justify-center ${
                    message.type === "user"
                      ? "bg-blue-600 text-white"
                      : "bg-gray-200 text-gray-600"
                  }`}
                >
                  {message.type === "user" ? (
                    <UserIcon className="w-5 h-5" />
                  ) : (
                    <CpuChipIcon className="w-5 h-5" />
                  )}
                </div>
              </div>

