import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import {
  ArrowLeftIcon,
  EllipsisHorizontalIcon,
} from "@heroicons/react/24/outline";
import { projectAPI, promptCardAPI, handleAPIError } from "../services/api";
import LoadingSpinner from "./LoadingSpinner";
import PromptCardManager from "./PromptCardManager";

const ProjectDetail = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const [promptCards, setPromptCards] = useState([]);
  const [showProjectMenu, setShowProjectMenu] = useState(false);

  useEffect(() => {
    loadProject();
    loadPromptCards();
  }, [projectId]);

  const loadProject = async () => {
    try {
      setLoading(true);
      const data = await projectAPI.getProject(projectId);
      setProject(data);
    } catch (error) {
      console.error("ÌîÑÎ°úÏ†ùÌä∏ Î°úÎìú Ïã§Ìå®:", error);
      handleAPIError(error, "ÌîÑÎ°úÏ†ùÌä∏Î•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      navigate("/");
    } finally {
      setLoading(false);
    }
  };

  const loadPromptCards = async () => {
    try {
      const response = await promptCardAPI.getPromptCards(projectId);
      console.log("üß™ ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú ÏùëÎãµ:", response);

      // API ÏùëÎãµ Íµ¨Ï°∞Ïóê Îî∞Îùº promptCards Î∞∞Ïó¥ Ï∂îÏ∂ú
      const cards = response?.promptCards || response || [];
      setPromptCards(cards);
      console.log("üß™ ÏÑ§Ï†ïÎêú ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú:", cards);
    } catch (error) {
      console.error("ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú Î°úÎìú Ïã§Ìå®:", error);
      handleAPIError(error, "ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥ÎìúÎ•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    }
  };

  const handleDeleteProject = async () => {
    if (
      !window.confirm(
        "Ï†ïÎßêÎ°ú Ïù¥ ÌîÑÎ°úÏ†ùÌä∏Î•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå? Ïù¥ ÏûëÏóÖÏùÄ ÎêòÎèåÎ¶¥ Ïàò ÏóÜÏäµÎãàÎã§."
      )
    ) {
      return;
    }

    try {
      await projectAPI.deleteProject(projectId);
      toast.success("ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.");
      navigate("/");
    } catch (error) {
      console.error("ÌîÑÎ°úÏ†ùÌä∏ ÏÇ≠Ï†ú Ïã§Ìå®:", error);
      handleAPIError(error, "ÌîÑÎ°úÏ†ùÌä∏ ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    }
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  if (!project) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-xl font-semibold text-gray-900 mb-2">
            ÌîÑÎ°úÏ†ùÌä∏Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§
          </h2>
          <button
            onClick={() => navigate("/")}
            className="text-blue-600 hover:text-blue-800"
          >
            ÌôàÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† - ÏÉàÎ°úÏö¥ PromptCardManager ÏÇ¨Ïö© */}
      <PromptCardManager
        projectId={projectId}
        projectName={project.title}
        promptCards={promptCards}
        onCardsChanged={loadPromptCards}
      />

    </div>
  );
};

export default ProjectDetail;
