import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import {
  PlusIcon,
  FolderIcon,
  XMarkIcon,
  ChevronDownIcon,
  CheckIcon,
} from "@heroicons/react/24/outline";
import { projectAPI, handleAPIError, categoryAPI } from "../services/api";

const CreateProject = ({ isOpen, onClose, onSuccess }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [categoryDropdownOpen, setCategoryDropdownOpen] = useState(false);
  const [userCategories, setUserCategories] = useState([]);
  const [categoriesLoading, setCategoriesLoading] = useState(false);
  const categoryDropdownRef = useRef(null);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    category: "", // Îπà Î¨∏ÏûêÏó¥Î°ú Ï¥àÍ∏∞Ìôî
    tags: [],
  });
  const [tagInput, setTagInput] = useState("");

  // ÏÇ¨Ïö©Ïûê Ïπ¥ÌÖåÍ≥†Î¶¨ Î°úÎìú
  const loadUserCategories = async () => {
    try {
      setCategoriesLoading(true);
      const data = await categoryAPI.getUserCategories();
      const categories = data.categories || [];
      setUserCategories(categories);

      // Ïπ¥ÌÖåÍ≥†Î¶¨Í∞Ä ÏûàÏúºÎ©¥ Ï≤´ Î≤àÏß∏ Ïπ¥ÌÖåÍ≥†Î¶¨Î•º Í∏∞Î≥∏Í∞íÏúºÎ°ú ÏÑ§Ï†ï
      if (categories.length > 0 && !formData.category) {
        setFormData((prev) => ({
          ...prev,
          category: categories[0].id,
        }));
      }
    } catch (error) {
      console.error("Ïπ¥ÌÖåÍ≥†Î¶¨ Î°úÎìú Ïã§Ìå®:", error);
      toast.error(
        "Ïπ¥ÌÖåÍ≥†Î¶¨Î•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§. Î®ºÏ†Ä Ïπ¥ÌÖåÍ≥†Î¶¨Î•º ÏÉùÏÑ±Ìï¥Ï£ºÏÑ∏Ïöî."
      );
      setUserCategories([]);
    } finally {
      setCategoriesLoading(false);
    }
  };

  // Î™®Îã¨Ïù¥ Ïó¥Î¶¥ Îïå Ïπ¥ÌÖåÍ≥†Î¶¨ Î°úÎìú
  useEffect(() => {
    if (isOpen) {
      loadUserCategories();
    }
  }, [isOpen]);

  // ÌòÑÏû¨ ÏÑ†ÌÉùÎêú Ïπ¥ÌÖåÍ≥†Î¶¨ Ï†ïÎ≥¥
  const currentCategory = userCategories.find(
    (option) => option.id === formData.category
  );

  // ÎìúÎ°≠Îã§Ïö¥ Ïô∏Î∂Ä ÌÅ¥Î¶≠ Í∞êÏßÄ
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        categoryDropdownRef.current &&
        !categoryDropdownRef.current.contains(event.target)
      ) {
        setCategoryDropdownOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.name.trim()) {
      toast.error("ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }

    if (!formData.category) {
      toast.error("Ïπ¥ÌÖåÍ≥†Î¶¨Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }

    try {
      setLoading(true);
      const result = await projectAPI.createProject(formData);
      console.log("‚úÖ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± ÏÑ±Í≥µ:", result);
      toast.success("ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§!");

      // Ìèº Ï¥àÍ∏∞Ìôî
      setFormData({
        name: "",
        description: "",
        category: userCategories[0]?.id || "",
        tags: [],
      });
      setTagInput("");

      // Î™®Îã¨ Îã´Í∏∞
      onClose();

      // ÏÑ±Í≥µ ÏΩúÎ∞± Ìò∏Ï∂ú (Î™®Îã¨ Îã´Í∏∞ ÌõÑÏóê Ìò∏Ï∂úÌïòÏó¨ ÌôïÏã§Ìûà Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®)
      if (onSuccess) {
        console.log("üîÑ onSuccess ÏΩúÎ∞± Ìò∏Ï∂ú");
        onSuccess();
      }

      // ÌîÑÎ°úÏ†ùÌä∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÏßÄ ÏïäÍ≥†, ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ùÏóê Î®∏Î¨ºÍ∏∞
      // navigate(`/projects/${result.projectId}`);
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Ïã§Ìå®: ${errorInfo.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleCategorySelect = (categoryId) => {
    setFormData((prev) => ({
      ...prev,
      category: categoryId,
    }));
    setCategoryDropdownOpen(false);
  };

  const handleClose = () => {
    // Ìèº Ï¥àÍ∏∞Ìôî
    setFormData({
      name: "",
      description: "",
      category: userCategories[0]?.id || "",
      tags: [],
    });
    setTagInput("");
    setCategoryDropdownOpen(false);
    onClose();
  };

  // ÌÉúÍ∑∏ Ï∂îÍ∞Ä Ìï®Ïàò
  const addTag = () => {
    const tag = tagInput.trim();
    if (tag && !formData.tags.includes(tag) && formData.tags.length < 10) {
      setFormData({
        ...formData,
        tags: [...formData.tags, tag]
      });
      setTagInput("");
    }
  };

  // ÌÉúÍ∑∏ Ï†úÍ±∞ Ìï®Ïàò
  const removeTag = (tagToRemove) => {
    setFormData({
      ...formData,
      tags: formData.tags.filter(tag => tag !== tagToRemove)
    });
  };

  // Enter ÌÇ§Î°ú ÌÉúÍ∑∏ Ï∂îÍ∞Ä
  const handleTagKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addTag();
    }
  };

  if (!isOpen) {
    return null;
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        {/* Î™®Îã¨ Ìó§Îçî */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-blue-100 rounded-lg">
              <FolderIcon className="h-6 w-6 text-blue-600" />
            </div>
            <div>
              <h2 className="text-xl font-bold text-gray-900">
                ÏÉà ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±
              </h2>
              <p className="text-sm text-gray-600">
                AI Ï†úÎ™© ÏÉùÏÑ±ÏùÑ ÏúÑÌïú ÏÉà ÌîÑÎ°úÏ†ùÌä∏Î•º ÎßåÎì§Ïñ¥Î≥¥ÏÑ∏Ïöî
              </p>
            </div>
          </div>
          <button
            onClick={handleClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>

        {/* Ìèº */}
        <div className="p-6">
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label
                htmlFor="name"
                className="block text-sm font-medium text-gray-700 mb-2"
              >
                ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ *
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Ïòà: ÏÑúÏö∏Í≤ΩÏ†úÏã†Î¨∏ Í∏∞ÏÇ¨ Ï†úÎ™©"
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={loading}
              />
            </div>

            <div>
              <label
                htmlFor="description"
                className="block text-sm font-medium text-gray-700 mb-2"
              >
                ÏÑ§Î™Ö (ÏÑ†ÌÉùÏÇ¨Ìï≠)
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                placeholder="ÌîÑÎ°úÏ†ùÌä∏Ïóê ÎåÄÌïú Í∞ÑÎã®Ìïú ÏÑ§Î™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
                rows={4}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                disabled={loading}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Ïπ¥ÌÖåÍ≥†Î¶¨ *
              </label>
              {userCategories.length === 0 && !categoriesLoading ? (
                <div className="text-center py-8 border-2 border-dashed border-gray-300 rounded-lg">
                  <p className="text-gray-500 mb-4">
                    ÏÇ¨Ïö©Ìï† Ïàò ÏûàÎäî Ïπ¥ÌÖåÍ≥†Î¶¨Í∞Ä ÏóÜÏäµÎãàÎã§.
                  </p>
                  <p className="text-sm text-gray-400">
                    ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ùÏóêÏÑú Ïπ¥ÌÖåÍ≥†Î¶¨Î•º Î®ºÏ†Ä ÏÉùÏÑ±Ìï¥Ï£ºÏÑ∏Ïöî.
                  </p>
                </div>
              ) : (
                <div className="relative" ref={categoryDropdownRef}>
                  <button
                    type="button"
                    onClick={() =>
                      setCategoryDropdownOpen(!categoryDropdownOpen)
                    }
                    className="w-full flex items-center justify-between px-4 py-3 bg-white border border-gray-300 rounded-lg hover:border-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200"
                    disabled={
                      loading ||
                      categoriesLoading ||
                      userCategories.length === 0
                    }
                  >
                    <div className="flex items-center space-x-2">
                      {currentCategory && (
                        <div
                          className={`w-3 h-3 rounded-full bg-${currentCategory.color}-500`}
                        ></div>
                      )}
                      <span className="text-gray-700 font-medium">
                        {categoriesLoading
                          ? "Î°úÎî© Ï§ë..."
                          : currentCategory?.name || "Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÑ†ÌÉù"}
                      </span>
                    </div>
                    <ChevronDownIcon
                      className={`h-4 w-4 text-gray-400 transition-transform duration-200 ${
                        categoryDropdownOpen ? "rotate-180" : ""
                      }`}
                    />
                  </button>

                  {/* ÎìúÎ°≠Îã§Ïö¥ Î©îÎâ¥ */}
                  {categoryDropdownOpen && !categoriesLoading && (
                    <div className="absolute top-full left-0 mt-1 w-full bg-white border border-gray-200 rounded-lg shadow-lg z-50 overflow-hidden">
                      {userCategories.map((category) => (
                        <button
                          key={category.id}
                          type="button"
                          onClick={() => handleCategorySelect(category.id)}
                          className={`w-full flex items-center justify-between px-4 py-3 text-left hover:bg-gray-50 transition-colors duration-150 ${
                            formData.category === category.id
                              ? "bg-blue-50 text-blue-600"
                              : "text-gray-700"
                          }`}
                        >
                          <div className="flex items-center space-x-3">
                            <div
                              className={`w-3 h-3 rounded-full bg-${category.color}-500`}
                            ></div>
                            <span className="font-medium">{category.name}</span>
                          </div>
                          {formData.category === category.id && (
                            <CheckIcon className="h-4 w-4 text-blue-600" />
                          )}
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              )}
            </div>

            {/* ÌÉúÍ∑∏ Í¥ÄÎ¶¨ */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                ÌÉúÍ∑∏ ({formData.tags.length}/10)
              </label>

              {/* ÌòÑÏû¨ ÌÉúÍ∑∏Îì§ */}
              {formData.tags.length > 0 && (
                <div className="flex flex-wrap gap-2 mb-3">
                  {formData.tags.map((tag, index) => (
                    <span
                      key={index}
                      className="inline-flex items-center px-3 py-1 rounded-full text-sm bg-blue-100 text-blue-800"
                    >
                      {tag}
                      <button
                        type="button"
                        onClick={() => removeTag(tag)}
                        className="ml-2 text-blue-600 hover:text-blue-800"
                      >
                        <XMarkIcon className="h-3 w-3" />
                      </button>
                    </span>
                  ))}
                </div>
              )}

              {/* ÌÉúÍ∑∏ ÏûÖÎ†• */}
              <div className="flex gap-2">
                <input
                  type="text"
                  value={tagInput}
                  onChange={(e) => setTagInput(e.target.value)}
                  onKeyPress={handleTagKeyPress}
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="ÌÉúÍ∑∏Î•º ÏûÖÎ†•ÌïòÍ≥† EnterÎ•º ÎàÑÎ•¥ÏÑ∏Ïöî"
                  maxLength={20}
                  disabled={formData.tags.length >= 10 || loading}
                />
                <button
                  type="button"
                  onClick={addTag}
                  disabled={!tagInput.trim() || formData.tags.length >= 10 || loading}
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Ï∂îÍ∞Ä
                </button>
              </div>
              <p className="text-xs text-gray-500 mt-1">
                ÌÉúÍ∑∏Îäî ÏµúÎåÄ 10Í∞úÍπåÏßÄ Ï∂îÍ∞ÄÌï† Ïàò ÏûàÏäµÎãàÎã§. Í∞Å ÌÉúÍ∑∏Îäî 20Ïûê Ïù¥ÎÇ¥Î°ú ÏûÖÎ†•ÌïòÏÑ∏Ïöî.
              </p>
            </div>

            <div className="flex items-center justify-end space-x-4 pt-6 border-t border-gray-200">
              <button
                type="button"
                onClick={handleClose}
                className="px-6 py-3 text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-lg transition-colors"
                disabled={loading}
              >
                Ï∑®ÏÜå
              </button>
              <button
                type="submit"
                disabled={
                  loading ||
                  !formData.name.trim() ||
                  !formData.category ||
                  categoriesLoading
                }
                className="flex items-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {loading ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    ÏÉùÏÑ± Ï§ë...
                  </>
                ) : (
                  <>
                    <PlusIcon className="h-5 w-5 mr-2" />
                    ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CreateProject;
