import React, { useState, useEffect, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import {
  ArrowLeftIcon,
  ChatBubbleLeftRightIcon,
  DocumentTextIcon,
  CheckCircleIcon,
  SparklesIcon,
  InformationCircleIcon,
  Cog6ToothIcon,
} from "@heroicons/react/24/outline";
import {
  projectAPI,
  handleAPIError,
  promptCardAPI,
} from "../services/api";
import PromptCardManager from "./PromptCardManager";
import ChatInterface from "./ChatInterface";

const ProjectDetail = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();

  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState("chat");
  const [promptCards, setPromptCards] = useState([]);

  // Helper function to check if we can generate titles (for UI display purposes)
  const canGenerate = () => {
    return promptCards.length > 0 || true; // Always allow in chat mode
  };

  const loadProject = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const projectData = await projectAPI.getProject(projectId);
      setProject(projectData);
    } catch (err) {
      const errorInfo = handleAPIError(err);
      setError(errorInfo.message);
      toast.error(errorInfo.message);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  const loadPromptCards = useCallback(async () => {
    try {
      const response = await promptCardAPI.getPromptCards(
        projectId,
        false,
        true
      );
      setPromptCards(response.promptCards || []);
    } catch (error) {
      console.error("ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú Î°úÎî© Ïã§Ìå®:", error);
      setPromptCards([]);
    }
  }, [projectId]);

  useEffect(() => {
    loadProject();
    loadPromptCards();
  }, [loadProject, loadPromptCards]);

  const handleCardReorder = useCallback(async (fromIndex, toIndex) => {
    const sortedCards = [...promptCards].sort((a, b) => (a.stepOrder || 0) - (b.stepOrder || 0));
    const newCards = [...sortedCards];
    const [movedCard] = newCards.splice(fromIndex, 1);
    newCards.splice(toIndex, 0, movedCard);

    // stepOrder ÏóÖÎç∞Ïù¥Ìä∏
    const updatedCards = newCards.map((card, index) => ({
      ...card,
      stepOrder: index + 1
    }));

    setPromptCards(updatedCards);

    try {
      // API Ìò∏Ï∂úÎ°ú ÏàúÏÑú ÏóÖÎç∞Ïù¥Ìä∏
      await Promise.all(
        updatedCards.map(card => 
          promptCardAPI.updatePromptCard(projectId, card.promptId, {
            ...card,
            stepOrder: card.stepOrder
          })
        )
      );
      toast.success("Ïπ¥Îìú ÏàúÏÑúÍ∞Ä Î≥ÄÍ≤ΩÎêòÏóàÏäµÎãàÎã§!");
    } catch (error) {
      console.error("Ïπ¥Îìú ÏàúÏÑú Î≥ÄÍ≤Ω Ïã§Ìå®:", error);
      toast.error("Ïπ¥Îìú ÏàúÏÑú Î≥ÄÍ≤ΩÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      // Ïã§Ìå®Ïãú ÏõêÎûò ÏàúÏÑúÎ°ú Î≥µÏõê
      loadPromptCards();
    }
  }, [promptCards, projectId, loadPromptCards]);

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">ÌîÑÎ°úÏ†ùÌä∏ Î°úÎî© Ï§ë...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={() => navigate(-1)}
            className="text-blue-600 hover:underline"
          >
            ÎèåÏïÑÍ∞ÄÍ∏∞
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Ìó§Îçî */}
      <div className="bg-white shadow-sm border-b border-gray-200 mb-8 sticky top-0 z-30">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-6">
              <button
                onClick={() => navigate(-1)}
                className="flex items-center text-gray-600 hover:text-gray-900 transition-all duration-200 hover:bg-gray-100 px-3 py-2 rounded-lg group"
              >
                <ArrowLeftIcon className="h-5 w-5 mr-2 group-hover:-translate-x-1 transition-transform duration-200" />
                <span className="font-medium">ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù</span>
              </button>

              <div className="flex items-center space-x-4">
                <div className="w-1 h-12 bg-blue-500 rounded-full"></div>
                <div>
                  <h1 className="text-3xl font-bold text-gray-900">
                    {project?.name}
                  </h1>
                  <p className="text-gray-600 mt-1 text-lg">
                    {project?.description || "AI Í∏∞Î∞ò Ï†úÎ™© ÏÉùÏÑ± ÌîÑÎ°úÏ†ùÌä∏"}
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* ÌîÑÎ°úÏ†ùÌä∏ ÏÉÅÌÉú Ï†ïÎ≥¥ */}
          <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="bg-white border border-blue-200 rounded-xl p-6 shadow-sm">
              <div className="flex items-center justify-between mb-4">
                <div className="p-3 bg-blue-100 rounded-lg">
                  <DocumentTextIcon className="h-6 w-6 text-blue-600" />
                </div>
                <div className="text-right">
                  <p className="text-3xl font-bold text-blue-600">
                    {promptCards.length}
                  </p>
                  <p className="text-sm text-blue-500 font-medium">
                    ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú
                  </p>
                </div>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">ÌôúÏÑ± Ïπ¥Îìú</span>
                <span className="text-sm font-semibold text-blue-600">
                  {promptCards.filter((card) => card.enabled !== false).length}Í∞ú
                </span>
              </div>
            </div>

            <div className="bg-white border border-green-200 rounded-xl p-6 shadow-sm">
              <div className="flex items-center justify-between mb-4">
                <div className="p-3 bg-green-100 rounded-lg">
                  <CheckCircleIcon className="h-6 w-6 text-green-600" />
                </div>
                <div className="text-right">
                  <p className="text-3xl font-bold text-green-600">
                    {canGenerate() ? "Ï§ÄÎπÑ" : "ÎåÄÍ∏∞"}
                  </p>
                  <p className="text-sm text-green-500 font-medium">
                    ÏÉùÏÑ± ÏÉÅÌÉú
                  </p>
                </div>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">
                  {canGenerate() ? "Ï†úÎ™© ÏÉùÏÑ± Í∞ÄÎä•" : "ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú ÌïÑÏöî"}
                </span>
                <div className={`w-3 h-3 rounded-full ${canGenerate() ? 'bg-green-400' : 'bg-yellow-400'}`}></div>
              </div>
            </div>

            <div className="bg-white border border-purple-200 rounded-xl p-6 shadow-sm">
              <div className="flex items-center justify-between mb-4">
                <div className="p-3 bg-purple-100 rounded-lg">
                  <SparklesIcon className="h-6 w-6 text-purple-600" />
                </div>
                <div className="text-right">
                  <p className="text-lg font-bold text-purple-600">
                    Claude 3.5
                  </p>
                  <p className="text-sm text-purple-500 font-medium">
                    AI Î™®Îç∏
                  </p>
                </div>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">ÏµúÍ≥† ÏÑ±Îä• Î™®Îç∏</span>
                <div className="flex space-x-1">
                  {[...Array(5)].map((_, i) => (
                    <div key={i} className="w-1.5 h-1.5 bg-purple-400 rounded-full"></div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* ÌÉ≠ ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="mb-8">
          <div className="bg-white/70 backdrop-blur-sm rounded-2xl p-2 shadow-sm border border-gray-200/50">
            <nav className="flex space-x-2">
              <button
                onClick={() => setActiveTab("chat")}
                className={`flex items-center px-6 py-3 rounded-xl font-medium text-sm transition-all duration-200 ${
                  activeTab === "chat"
                    ? "bg-blue-600 text-white shadow-md shadow-blue-600/25"
                    : "text-gray-600 hover:text-gray-900 hover:bg-gray-100/70"
                }`}
              >
                <ChatBubbleLeftRightIcon className="h-5 w-5 mr-2" />
                <span>AI ÎåÄÌôî</span>
              </button>

              <button
                onClick={() => setActiveTab("prompt-cards")}
                className={`flex items-center px-6 py-3 rounded-xl font-medium text-sm transition-all duration-200 ${
                  activeTab === "prompt-cards"
                    ? "bg-blue-600 text-white shadow-md shadow-blue-600/25"
                    : "text-gray-600 hover:text-gray-900 hover:bg-gray-100/70"
                }`}
              >
                <Cog6ToothIcon className="h-5 w-5 mr-2" />
                <span>ÌîÑÎ°¨ÌîÑÌä∏ ÏÑ§Ï†ï</span>
                <span className={`ml-3 px-2 py-1 text-xs rounded-full font-semibold ${
                  activeTab === "prompt-cards" 
                    ? "bg-white/20 text-white" 
                    : "bg-blue-100 text-blue-600"
                }`}>
                  {promptCards.length}
                </span>
              </button>
            </nav>
          </div>
        </div>

        {/* ÌÉ≠ ÏÑ§Î™Ö */}
        <div className="mb-8">
          {activeTab === "chat" && (
            <div className="bg-gradient-to-r from-green-50 to-emerald-50 border border-green-200/50 rounded-2xl p-6 backdrop-blur-sm">
              <div className="flex items-start space-x-4">
                <div className="p-3 bg-green-100 rounded-xl">
                  <ChatBubbleLeftRightIcon className="h-6 w-6 text-green-600" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-green-900 mb-2">
                    AI ÎåÄÌôîÌòï Ï†úÎ™© ÏÉùÏÑ±
                  </h3>
                  <p className="text-green-800 leading-relaxed">
                    ChatGPTÎÇò ClaudeÏ≤òÎüº AIÏôÄ ÎåÄÌôîÌïòÎ©∞ Ï†úÎ™©ÏùÑ ÏÉùÏÑ±ÌïòÍ≥† ÏàòÏ†ïÌï† Ïàò ÏûàÏäµÎãàÎã§. 
                    Í∏∞ÏÇ¨ ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÍ±∞ÎÇò Í∏∞Ï°¥ Ï†úÎ™© ÏàòÏ†ïÏùÑ ÏöîÏ≤≠ÌïòÎ©∞ Ïó∞ÏÜçÏ†ÅÏúºÎ°ú ÎåÄÌôîÌï† Ïàò ÏûàÏäµÎãàÎã§.
                  </p>
                </div>
              </div>
            </div>
          )}

          {activeTab === "prompt-cards" && (
            <div className="bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-200/50 rounded-2xl p-6 backdrop-blur-sm">
              <div className="flex items-start space-x-4">
                <div className="p-3 bg-blue-100 rounded-xl">
                  <Cog6ToothIcon className="h-6 w-6 text-blue-600" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-blue-900 mb-2">
                    ÌîÑÎ°¨ÌîÑÌä∏ ÏÑ§Ï†ï Í¥ÄÎ¶¨
                  </h3>
                  <p className="text-blue-800 leading-relaxed">
                    AIÍ∞Ä ÎåÄÌôîÌï† Îïå ÏÇ¨Ïö©Ìï† ÏßÄÏπ®Í≥º Í∑úÏπôÏùÑ Ïπ¥Îìú ÌòïÌÉúÎ°ú Í¥ÄÎ¶¨Ìï©ÎãàÎã§. 
                    Ïπ¥ÎìúÏùò ÏàúÏÑúÎ•º ÎìúÎûòÍ∑∏Ïï§ÎìúÎ°≠ÏúºÎ°ú Ï°∞Ï†ïÌïòÍ≥† ÌôúÏÑ±/ÎπÑÌôúÏÑ±ÏùÑ ÏÑ§Ï†ïÌï† Ïàò ÏûàÏäµÎãàÎã§.
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* ÌÉ≠ ÏΩòÌÖêÏ∏† */}
        <div className="min-h-screen">
          {activeTab === "prompt-cards" && (
            <PromptCardManager
              projectId={projectId}
              onCardsChanged={loadPromptCards}
            />
          )}

          {activeTab === "chat" && (
            <div className="h-[calc(100vh-300px)]">
              <ChatInterface
                projectId={projectId}
                projectName={project?.name}
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú ÎØ∏Îãà Ïª¥Ìè¨ÎÑåÌä∏
const PromptCardMini = ({ card, index, total, onReorder }) => {
  const [isDragging, setIsDragging] = useState(false);

  const getCategoryIcon = (category) => {
    switch (category) {
      case "instruction":
        return "üìã";
      case "knowledge":
        return "üìö";
      case "summary":
        return "üìù";
      case "style_guide":
        return "üé®";
      case "validation":
        return "‚úÖ";
      case "enhancement":
        return "‚ú®";
      default:
        return "üìÑ";
    }
  };

  const getCategoryColor = (category) => {
    switch (category) {
      case "instruction":
        return "blue";
      case "knowledge":
        return "green";
      case "summary":
        return "purple";
      case "style_guide":
        return "pink";
      case "validation":
        return "orange";
      case "enhancement":
        return "indigo";
      default:
        return "gray";
    }
  };

  const categoryColor = getCategoryColor(card.category);

  const handleDragStart = (e) => {
    setIsDragging(true);
    e.dataTransfer.setData("text/plain", index.toString());
    e.dataTransfer.effectAllowed = "move";
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = "move";
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const draggedIndex = parseInt(e.dataTransfer.getData("text/plain"));
    if (draggedIndex !== index && onReorder) {
      onReorder(draggedIndex, index);
    }
  };

  const handleDragEnd = () => {
    setIsDragging(false);
  };

  return (
    <div 
      draggable
      onDragStart={handleDragStart}
      onDragOver={handleDragOver}
      onDrop={handleDrop}
      onDragEnd={handleDragEnd}
      className={`p-3 rounded-lg border transition-all duration-200 cursor-move ${
        isDragging ? "opacity-50 scale-95" : ""
      } ${
        card.enabled !== false
          ? `bg-${categoryColor}-50 border-${categoryColor}-200 hover:bg-${categoryColor}-100`
          : "bg-gray-50 border-gray-200 opacity-60"
      }`}
    >
      <div className="flex items-start justify-between">
        <div className="flex items-start space-x-2 flex-1">
          <span className="text-lg flex-shrink-0">
            {getCategoryIcon(card.category)}
          </span>
          <div className="flex-1 min-w-0">
            <div className="flex items-center space-x-2 mb-1">
              <span className="text-xs font-medium text-gray-500 bg-white px-2 py-0.5 rounded-full">
                {card.stepOrder || index + 1}
              </span>
              <span className={`text-xs font-medium px-2 py-0.5 rounded-full ${
                card.enabled !== false
                  ? `bg-${categoryColor}-100 text-${categoryColor}-700`
                  : "bg-gray-100 text-gray-500"
              }`}>
                {card.category?.replace('_', ' ').toUpperCase()}
              </span>
            </div>
            <h4 className={`text-sm font-medium truncate ${
              card.enabled !== false ? "text-gray-900" : "text-gray-500"
            }`}>
              {card.title || `${card.category} Ïπ¥Îìú`}
            </h4>
            {card.description && (
              <p className="text-xs text-gray-600 truncate mt-1">
                {card.description}
              </p>
            )}
          </div>
        </div>
        <div className="flex items-center space-x-2">
          {card.enabled !== false ? (
            <CheckCircleIcon className="h-4 w-4 text-green-500 flex-shrink-0" />
          ) : (
            <div className="w-4 h-4 rounded-full bg-gray-300 flex-shrink-0"></div>
          )}
          <div className="text-gray-400">
            <svg className="h-4 w-4" fill="currentColor" viewBox="0 0 20 20">
              <path d="M10 3a1 1 0 01.707.293l3 3a1 1 0 01-1.414 1.414L10 5.414 7.707 7.707a1 1 0 01-1.414-1.414l3-3A1 1 0 0110 3zM10 17a1 1 0 01-.707-.293l-3-3a1 1 0 011.414-1.414L10 14.586l2.293-2.293a1 1 0 011.414 1.414l-3 3A1 1 0 0110 17z" />
            </svg>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProjectDetail;
