        console.log("üöÄ ÎåÄÌôî ÏÉùÏÑ± ÏöîÏ≤≠ ÏãúÏûë:", {
          projectId,
          inputLength: userInput.length,
          historyLength: data.chat_history.length,
          promptCardsCount: data.prompt_cards.length,
          useStreaming: options.useStreaming === true,
          timestamp: new Date().toISOString(),
        });

        // üîß Ïä§Ìä∏Î¶¨Î∞ç ÏÇ¨Ïö© Ïó¨Î∂Ä ÌôïÏù∏ - Îçî ÏïàÏ†ÑÌïú Î°úÏßÅ
        if (options.useStreaming === true) {
          setIsStreaming(true);

          // Ïä§Ìä∏Î¶¨Î∞ç ÏΩúÎ∞± Ìï®Ïàò ÏÑ§Ï†ï
          const onChunk = options.onChunk || (() => {});
          const onError = (error) => {
            console.error("üîß Ïä§Ìä∏Î¶¨Î∞ç Ïò§Î•ò Ï≤òÎ¶¨:", error);
            setIsExecuting(false);
            setIsStreaming(false);
            setExecutionStatus("FAILED");

            // üîß Í∞úÏÑ†: Ïò§Î•ò ÌÉÄÏûÖÏóê Îî∞Î•∏ Ï†ÅÏ†àÌïú Î©îÏãúÏßÄ
            if (
              error.message?.includes("Gateway Timeout") ||
              error.message?.includes("504") ||
              error.code === "ECONNABORTED"
            ) {
              toast.error(
                "ÏÑúÎ≤Ñ ÏùëÎãµ ÏãúÍ∞ÑÏù¥ Ï¥àÍ≥ºÎêòÏóàÏäµÎãàÎã§. ÏöîÏ≤≠ÏùÑ Í∞ÑÎã®Ìûà ÌïòÍ±∞ÎÇò Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
              );
            } else if (
              error.message?.includes("CORS") ||
              error.message?.includes("Network Error")
