            console.log("‚úÖ Ïä§Ìä∏Î¶¨Î∞ç ÏôÑÎ£å:", {
              resultLength: response.result?.length || 0,
              timestamp: new Date().toISOString(),
            });
            setIsExecuting(false);
            setIsStreaming(false);
            setExecutionStatus("COMPLETED");
            if (options.onComplete) options.onComplete(response);
          };

          try {
            // üîß Í∞úÏÑ†: Ïä§Ìä∏Î¶¨Î∞ç API Ìò∏Ï∂ú (ÎÇ¥Î∂ÄÏóêÏÑú Ìè¥Î∞± Ï≤òÎ¶¨Îê®)
            return await generateAPI.generateTitleStream(
              projectId,
              data,
              onChunk,
              onError,
              onComplete
            );
          } catch (streamError) {
            console.error("üîß Ïä§Ìä∏Î¶¨Î∞ç ÏµúÏ¢Ö Ïã§Ìå®:", streamError);

            // üîß Ïä§Ìä∏Î¶¨Î∞ç ÏôÑÏ†Ñ Ïã§Ìå® ÏãúÏóêÎèÑ Ìè¥Î∞±Ïù¥ ÎÇ¥Î∂ÄÏóêÏÑú Ï≤òÎ¶¨ÎêòÎØÄÎ°ú
            // Ïó¨Í∏∞ÏÑúÎäî ÏÇ¨Ïö©ÏûêÏóêÍ≤å ÏïåÎ¶ºÎßå
            setIsStreaming(false);
            setIsExecuting(false);
            setExecutionStatus("FAILED");

            // ÏµúÏ¢Ö Ïã§Ìå® Î©îÏãúÏßÄ
            toast.error(
              "ÏÑúÎπÑÏä§Ïóê ÏùºÏãúÏ†ÅÏù∏ Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî."
            );
            throw streamError;
          }
        }

        // üîß ÏùºÎ∞ò API Ìò∏Ï∂ú (Ïä§Ìä∏Î¶¨Î∞ç ÎØ∏ÏÇ¨Ïö©)
        console.log("üìÑ ÏùºÎ∞ò API Ìò∏Ï∂ú ÏãúÏûë...");
        const response = await generateAPI.generateTitle(projectId, data);

        console.log("‚úÖ ÎåÄÌôî ÏÉùÏÑ± ÏôÑÎ£å:", {
          mode: response.mode,
          message: response.message,
          timestamp: new Date().toISOString(),
        });

        setIsExecuting(false);
        setExecutionStatus("COMPLETED");

        return response;
