  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false); // ðŸš¨ ê°œë°œ ëª¨ë“œ: ë¡œë”© ë¹„í™œì„±í™”
  const [isAuthenticated, setIsAuthenticated] = useState(true); // ðŸš¨ ê°œë°œ ëª¨ë“œ: ê¸°ë³¸ ì¸ì¦ true

  // ì»´í¬ë„ŒíŠ¸ ë§ˆìš´íŠ¸ ì‹œ ì¸ì¦ ìƒíƒœ í™•ì¸
  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = () => {
    // ðŸš¨ ê°œë°œ ëª¨ë“œ: ì¸ì¦ ì²´í¬ ë¹„í™œì„±í™”
    console.log("ðŸ§ª ê°œë°œ ëª¨ë“œ: ì¸ì¦ ì²´í¬ ê±´ë„ˆë›°ê¸° - í•­ìƒ ì¸ì¦ëœ ìƒíƒœë¡œ ì²˜ë¦¬");

    try {
      // ê°œë°œ ëª¨ë“œì—ì„œëŠ” í•­ìƒ ì¸ì¦ëœ ìƒíƒœë¡œ ì„¤ì •
      setIsAuthenticated(true);
      setUser({
        email: "dev@example.com",
        name: "ê°œë°œìž",
        sub: "dev-user"
      });
    } catch (error) {
      console.error("ì¸ì¦ ìƒíƒœ í™•ì¸ ì˜¤ë¥˜:", error);
      // ê°œë°œ ëª¨ë“œì—ì„œëŠ” ì˜¤ë¥˜ê°€ ìžˆì–´ë„ ì¸ì¦ëœ ìƒíƒœ ìœ ì§€
      setIsAuthenticated(true);
      setUser({
        email: "dev@example.com",
        name: "ê°œë°œìž",
        sub: "dev-user"
      });
    } finally {
      setLoading(false);
    }
  };

  const login = async (credentials) => {
    try {
      const response = await authAPI.signin(credentials);
      const currentUser = authAPI.getCurrentUser();

      setUser(currentUser);
      setIsAuthenticated(true);

      return response;
    } catch (error) {
      setUser(null);
      setIsAuthenticated(false);
      throw error;
    }
  };

  const logout = async () => {
    try {
      await authAPI.signout();
    } catch (error) {
      console.error("ë¡œê·¸ì•„ì›ƒ ì˜¤ë¥˜:", error);
    } finally {
      setUser(null);
      setIsAuthenticated(false);
    }
  };

  const signup = async (userData) => {
    return await authAPI.signup(userData);
  };

  const verifyEmail = async (verificationData) => {
    return await authAPI.verifyEmail(verificationData);
  };

  const refreshToken = async () => {
    try {
      await authAPI.refreshToken();
      const currentUser = authAPI.getCurrentUser();
      setUser(currentUser);
      setIsAuthenticated(true);
    } catch (error) {
      setUser(null);
      setIsAuthenticated(false);
      throw error;
    }
  };

  const value = {
    user,
    loading,
    isAuthenticated,
    login,
    logout,
    signup,
    verifyEmail,
    refreshToken,
    checkAuthStatus,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
