import React, { useState, useRef, useEffect } from 'react';
import { toast } from 'react-hot-toast';
import {
  PaperAirplaneIcon,
  ArrowPathIcon,
  DocumentDuplicateIcon,
  TrashIcon,
} from '@heroicons/react/24/outline';
import { orchestrationAPI } from '../../services/api';

const ChatPanel = ({ 
  projectId, 
  projectName,
  promptCards = [],
  onToggleCardPanel,
  cardPanelVisible,
  className = "" 
}) => {
  const [messages, setMessages] = useState([
    {
      id: 'welcome',
      type: 'assistant',
      content: `ÏïàÎÖïÌïòÏÑ∏Ïöî! Ï†ÄÎäî ${projectName}Ïùò AI Ï†úÎ™© ÏûëÍ∞ÄÏûÖÎãàÎã§. üéØ\n\nÍ∏∞ÏÇ¨ ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏãúÎ©¥ Îã§ÏñëÌïú Ïä§ÌÉÄÏùºÏùò Ï†úÎ™©ÏùÑ Ï†úÏïàÌï¥ÎìúÎ¶¥Í≤åÏöî. Ï†úÎ™©ÏùÑ ÏàòÏ†ïÌïòÍ±∞ÎÇò Îã§Î•∏ Ïä§ÌÉÄÏùºÎ°ú Î∞îÍæ∏Í≥† Ïã∂ÏúºÏãúÎ©¥ Ïñ∏Ï†úÎì† ÎßêÏîÄÌï¥Ï£ºÏÑ∏Ïöî!`,
      timestamp: new Date(),
    }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [currentExecution, setCurrentExecution] = useState(null);

  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Î©îÏãúÏßÄ ÎÅùÏúºÎ°ú Ïä§ÌÅ¨Î°§
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // ÌÇ§Î≥¥Îìú Îã®Ï∂ïÌÇ§ Ï≤òÎ¶¨
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Ctrl/Cmd + B: Ïπ¥Îìú Ìå®ÎÑê ÌÜ†Í∏Ä
      if ((e.ctrlKey || e.metaKey) && e.key === 'b') {
        e.preventDefault();
        onToggleCardPanel();
        inputRef.current?.focus();
      }

      // /: Ïπ¥Îìú Îπ†Î•∏ Í≤ÄÏÉâ (Ï∂îÌõÑ Íµ¨ÌòÑ)
      if (e.key === '/' && !e.ctrlKey && !e.metaKey && document.activeElement !== inputRef.current) {
        e.preventDefault();
        onToggleCardPanel();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [onToggleCardPanel]);

  // Î©îÏãúÏßÄ Ï†ÑÏÜ°
  const handleSendMessage = async () => {
    if (!inputValue.trim() || isGenerating) return;

    const userMessage = {
      id: Date.now() + Math.random(),
      type: 'user',
      content: inputValue,
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsGenerating(true);

    try {
      // Ïò§ÏºÄÏä§Ìä∏Î†àÏù¥ÏÖò Ïã§Ìñâ
      const response = await orchestrationAPI.executeOrchestration(
        projectId,
        inputValue,
        {
          useAllSteps: true,
          enabledSteps: promptCards.filter(card => card.enabled).map(card => card.category),
          maxRetries: 3,
          temperature: 0.7
        }
      );

      setCurrentExecution(response.executionId);

      // ÏûÑÏãú Î°úÎî© Î©îÏãúÏßÄ
      const loadingMessage = {
        id: 'loading-' + Date.now(),
        type: 'assistant',
        content: 'üéØ AIÍ∞Ä Ï†úÎ™©ÏùÑ ÏÉùÏÑ±ÌïòÍ≥† ÏûàÏäµÎãàÎã§...\n\nÎã®Í≥ÑÎ≥ÑÎ°ú Ï≤òÎ¶¨ Ï§ëÏù¥Îãà Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî!',
        timestamp: new Date(),
        isLoading: true,
      };

      setMessages(prev => [...prev, loadingMessage]);

      // Í≤∞Í≥º Ìè¥ÎßÅ
      pollOrchestrationResult(response.executionId);

    } catch (error) {
      console.error('Î©îÏãúÏßÄ Ï†ÑÏÜ° Ïò§Î•ò:', error);

      const errorMessage = {
        id: 'error-' + Date.now(),
        type: 'assistant',
        content: 'Ï£ÑÏÜ°Ìï©ÎãàÎã§. Ï†úÎ™© ÏÉùÏÑ± Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.',
