import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { handleAPIError } from "../services/api";
import { useAuth } from "../contexts/AuthContext";

const Login = ({ onLoginSuccess, onSwitchToSignup }) => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    console.log("üöÄ handleSubmit Ìï®Ïàò Ìò∏Ï∂úÎê®!");
    e.preventDefault();

    console.log("üìù Ìèº Îç∞Ïù¥ÌÑ∞ ÌôïÏù∏:", formData);
    console.log("‚ö° login Ìï®Ïàò Ï°¥Ïû¨ Ïó¨Î∂Ä:", typeof login);

    setLoading(true);
    setError("");

    try {
      console.log("üîÑ Î°úÍ∑∏Ïù∏ Ìï®Ïàò Ìò∏Ï∂ú ÏãúÏûë...");
      const response = await login(formData);
      console.log("‚úÖ Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ:", response);

      if (onLoginSuccess) {
        onLoginSuccess(response);
      }
    } catch (error) {
      console.error("‚ùå Î°úÍ∑∏Ïù∏ Ïò§Î•ò:", error);
      console.error("‚ùå Ïò§Î•ò ÏÉÅÏÑ∏:", {
        name: error.name,
        message: error.message,
        stack: error.stack
      });

      const apiError = await handleAPIError(error);
      console.log("üîç Ï≤òÎ¶¨Îêú API Ïò§Î•ò:", apiError);

      // Î¶¨Îã§Ïù¥Î†âÌä∏Í∞Ä ÌïÑÏöîÌïú Í≤ΩÏö∞ (401 Ïò§Î•ò Îì±)
      if (apiError.shouldRedirect) {
        return;
      }

      // Ïù¥Î©îÏùº Ïù∏Ï¶ùÏù¥ ÌïÑÏöîÌïú Í≤ΩÏö∞ Ïù∏Ï¶ù ÌéòÏù¥ÏßÄÎ°ú Î¶¨Îã§Ïù¥Î†âÌä∏
      if (error.name === "UserNotConfirmedException" || (apiError.userMessage && apiError.userMessage.includes("Ïù¥Î©îÏùº Ïù∏Ï¶ù"))) {
        console.log("üìß Ïù¥Î©îÏùº Ïù∏Ï¶ù ÌéòÏù¥ÏßÄÎ°ú Î¶¨Îã§Ïù¥Î†âÌä∏");
        navigate(`/verify?email=${encodeURIComponent(formData.email)}`);
        return;
      }

      setError(apiError.userMessage || error.message || "Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    } finally {
      console.log("üèÅ Î°úÍ∑∏Ïù∏ Ï≤òÎ¶¨ ÏôÑÎ£å, loading Ìï¥Ï†ú");
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900 dark:text-white">
            Î°úÍ∑∏Ïù∏
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600 dark:text-gray-300">
            TITLE-NOMICS AI Ï†úÎ™© ÏÉùÏÑ± ÏãúÏä§ÌÖú
          </p>
        </div>

        <form 
          className="mt-8 space-y-6" 
          onSubmit={(e) => {
            console.log("üìã Ìèº Ï†úÏ∂ú Ïù¥Î≤§Ìä∏ Î∞úÏÉù!");
            handleSubmit(e);
          }}
        >
          <div className="space-y-4">
            <div>
              <label
                htmlFor="email"
                className="block text-sm font-medium text-gray-700 dark:text-gray-300"
              >
                Ïù¥Î©îÏùº
              </label>
              <input
                id="email"
                name="email"
                type="email"
                required
                value={formData.email}
                onChange={handleInputChange}
                className="mt-1 appearance-none relative block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 placeholder-gray-500 dark:placeholder-gray-400 text-gray-900 dark:text-white bg-white dark:bg-gray-700 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm transition-colors duration-200"
                placeholder="Ïù¥Î©îÏùº Ï£ºÏÜå"
              />
            </div>

            <div>
              <label
                htmlFor="password"
                className="block text-sm font-medium text-gray-700 dark:text-gray-300"
              >
                ÎπÑÎ∞ÄÎ≤àÌò∏
              </label>
              <input
                id="password"
                name="password"
                type="password"
                required
                value={formData.password}
                onChange={handleInputChange}
                className="mt-1 appearance-none relative block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 placeholder-gray-500 dark:placeholder-gray-400 text-gray-900 dark:text-white bg-white dark:bg-gray-700 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm transition-colors duration-200"
                placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
              />
            </div>
          </div>

          {error && (
            <div className="bg-red-50 dark:bg-red-900 border border-red-200 dark:border-red-700 text-red-700 dark:text-red-300 px-4 py-3 rounded relative">
              {error}
            </div>
          )}

          <div>
            <button
              type="submit"
              disabled={loading}
              onClick={() => console.log("üñ±Ô∏è Î°úÍ∑∏Ïù∏ Î≤ÑÌäº ÌÅ¥Î¶≠Îê®!")}
              className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? "Î°úÍ∑∏Ïù∏ Ï§ë..." : "Î°úÍ∑∏Ïù∏"}
            </button>
          </div>

          <div className="text-center space-y-2">
            <button
              type="button"
              onClick={() => navigate('/forgot-password')}
              className="text-blue-600 hover:text-blue-500 dark:text-blue-400 dark:hover:text-blue-300 text-sm transition-colors duration-200 block"
            >
              ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûäÏúºÏÖ®ÎÇòÏöî?
            </button>
            <button
              type="button"
              onClick={onSwitchToSignup || (() => navigate('/signup'))}
              className="text-blue-600 hover:text-blue-500 dark:text-blue-400 dark:hover:text-blue-300 text-sm transition-colors duration-200 block"
            >
              Í≥ÑÏ†ïÏù¥ ÏóÜÏúºÏã†Í∞ÄÏöî? ÌöåÏõêÍ∞ÄÏûÖ
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
