import axios from "axios";

// API Í∏∞Î≥∏ URL (ÌôòÍ≤Ω Î≥ÄÏàò ÎòêÎäî Í∏∞Î≥∏Í∞í)
const API_BASE_URL =
  process.env.REACT_APP_API_URL ||
  "https://vph0fu827a.execute-api.us-east-1.amazonaws.com/prod";

// Axios Ïù∏Ïä§ÌÑ¥Ïä§ ÏÉùÏÑ±
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

// ÏöîÏ≤≠ Ïù∏ÌÑ∞ÏÖâÌÑ∞
api.interceptors.request.use(
  (config) => {
    console.log("API ÏöîÏ≤≠:", config.method?.toUpperCase(), config.url);

    // Ïù∏Ï¶ù ÌÜ†ÌÅ∞ Ï∂îÍ∞Ä (API Gateway Cognito AuthorizerÎäî ID TokenÏùÑ ÏöîÍµ¨)
    const token = localStorage.getItem('idToken') || localStorage.getItem('accessToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }

    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// ÏùëÎãµ Ïù∏ÌÑ∞ÏÖâÌÑ∞
api.interceptors.response.use(
  (response) => {
    console.log("API ÏùëÎãµ:", response.status, response.config.url);
    return response;
  },
  (error) => {
    console.error(
      "API Ïò§Î•ò:",
      error.response?.status,
      error.response?.data || error.message
    );

    // 401 Ïò§Î•ò Ïãú ÌÜ†ÌÅ∞ Í∞±Ïã† ÏãúÎèÑ ÎòêÎäî Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Î¶¨Îã§Ïù¥Î†âÌä∏
    if (error.response?.status === 401) {
      // ÌÜ†ÌÅ∞ ÎßåÎ£å Ï≤òÎ¶¨
      localStorage.removeItem('accessToken');
      localStorage.removeItem('idToken');
      localStorage.removeItem('refreshToken');

      // Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Î¶¨Îã§Ïù¥Î†âÌä∏ (Ïã§Ï†ú Íµ¨ÌòÑ Ïãú React Router ÏÇ¨Ïö©)
      window.location.href = '/login';
    }

    return Promise.reject(error);
  }
);

