import React, { useState, useEffect, useCallback } from "react";
import { toast } from "react-hot-toast";
import { PlusIcon, Bars3Icon } from "@heroicons/react/24/outline";
import { promptCardAPI, handleAPIError } from "../services/api";
import ChatInterface from "./ChatInterface";
import PromptCard from "./prompts/PromptCard";
import PromptEditForm from "./prompts/PromptEditForm";
import { SidebarSkeleton } from "./SkeletonLoader";

// AI ÏõåÌÅ¨ÌîåÎ°úÏö∞ Í∏∞Î∞ò ÌîÑÎ°¨ÌîÑÌä∏ ÌÖúÌîåÎ¶ø
const FIXED_PROMPT_CATEGORIES = [
  {
    id: "instruction", 
    name: "Ïó≠Ìï† Î∞è Î™©Ìëú",
    order: 1,
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
    icon: "üéØ",
    description: "AIÏùò Ïó≠Ìï†, Ï†ïÏ≤¥ÏÑ±, ÌïµÏã¨ Î™©ÌëúÎ•º Î™ÖÌôïÌûà Ï†ïÏùòÌï©ÎãàÎã§",
    defaultContent: "ÎãπÏã†ÏùÄ Ï†ÑÎ¨∏Ï†ÅÏù∏ Í∏∞ÏÇ¨ Ï†úÎ™© ÏÉùÏÑ± AIÏûÖÎãàÎã§.\nÎ™©Ìëú: ÎèÖÏûêÏùò Í¥ÄÏã¨ÏùÑ ÎÅåÍ≥† Ï†ïÌôïÌïú Ï†ïÎ≥¥Î•º Ï†ÑÎã¨ÌïòÎäî Ï†úÎ™© ÏÉùÏÑ±",
    placeholder: "AIÏùò Ïó≠Ìï†Í≥º Ï£ºÏöî Î™©ÌëúÎ•º Íµ¨Ï≤¥Ï†ÅÏúºÎ°ú ÏûëÏÑ±ÌïòÏÑ∏Ïöî...",
  },
  {
    id: "knowledge",
    name: "ÏßÄÏãù Î≤†Ïù¥Ïä§", 
    order: 2,
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
    icon: "üìö",
    description: "ÏûëÏóÖ ÏàòÌñâÏóê ÌïÑÏöîÌïú ÌïµÏã¨ ÏßÄÏãùÍ≥º ÏõêÏπôÏùÑ Ï†úÍ≥µÌï©ÎãàÎã§",
    defaultContent: "Ï†úÎ™© ÏûëÏÑ± ÏõêÏπô:\n- Í∞ÑÍ≤∞ÌïòÍ≥† Î™ÖÌôïÌïú ÌëúÌòÑ\n- ÌïµÏã¨ ÌÇ§ÏõåÎìú Ìè¨Ìï®\n- ÎèÖÏûêÏùò Í¥ÄÏã¨ Ïú†Î∞ú",
    placeholder: "ÏûëÏóÖÏóê ÌïÑÏöîÌïú ÌïµÏã¨ ÏßÄÏãùÍ≥º ÏõêÏπôÏùÑ ÏûëÏÑ±ÌïòÏÑ∏Ïöî...",
  },
  {
    id: "secondary",
    name: "CoT (ÏÇ¨Í≥† Í≥ºÏ†ï)",
    order: 3, 
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
    icon: "üß†",
    description: "Îã®Í≥ÑÎ≥Ñ Ï∂îÎ°† Í≥ºÏ†ïÏùÑ ÌÜµÌï¥ ÏÇ¨Í≥†Ïùò Ìà¨Î™ÖÏÑ±ÏùÑ ÌôïÎ≥¥Ìï©ÎãàÎã§",
    defaultContent: "Îã§Ïùå Îã®Í≥ÑÎ°ú ÏÇ¨Í≥†ÌïòÏÑ∏Ïöî:\n1. Í∏∞ÏÇ¨Ïùò ÌïµÏã¨ ÎÇ¥Ïö© ÌååÏïÖ\n2. Ï£ºÏöî ÌÇ§ÏõåÎìú Ï∂îÏ∂ú\n3. ÎèÖÏûê Í¥ÄÏ†êÏóêÏÑú Ìù•ÎØ∏ÎèÑ ÌèâÍ∞Ä\n4. Ï†úÎ™© ÌõÑÎ≥¥ ÏÉùÏÑ±\n5. ÏµúÏ†Å Ï†úÎ™© ÏÑ†ÌÉù",
    placeholder: "Îã®Í≥ÑÎ≥Ñ ÏÇ¨Í≥† Í≥ºÏ†ïÏùÑ Íµ¨Ï≤¥Ï†ÅÏúºÎ°ú ÏûëÏÑ±ÌïòÏÑ∏Ïöî...",
    isCoT: true,
  },
  {
    id: "style_guide",
    name: "Ïä§ÌÉÄÏùº Í∞ÄÏù¥Îìú",
    order: 4,
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80", 
    icon: "‚ú®",
    description: "ÏùºÍ¥ÄÎêú Ïä§ÌÉÄÏùºÍ≥º ÌòïÏãùÏùÑ Ïú†ÏßÄÌïòÍ∏∞ ÏúÑÌïú Í∞ÄÏù¥ÎìúÎùºÏù∏",
    defaultContent: "Ïä§ÌÉÄÏùº ÏöîÍµ¨ÏÇ¨Ìï≠:\n- Í∏∏Ïù¥: 15-25Ïûê Í∂åÏû•\n- ÌÜ§: Ï†ÑÎ¨∏Ï†ÅÏù¥Î©¥ÏÑú ÏπúÍ∑º\n- ÌòïÏãù: Î™ÖÏÇ¨Ìòï Ï¢ÖÍ≤∞",
    placeholder: "Ïä§ÌÉÄÏùºÍ≥º ÌòïÏãù ÏöîÍµ¨ÏÇ¨Ìï≠ÏùÑ ÏûëÏÑ±ÌïòÏÑ∏Ïöî...",
  },
  {
    id: "validation", 
    name: "ReAct (Ï∂îÎ°†+ÌñâÎèô)",
    order: 5,
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
    icon: "‚ö°",
    description: "Ï∂îÎ°†Í≥º ÌñâÎèôÏùÑ Í≤∞Ìï©ÌïòÏó¨ Îçî Ï†ïÌôïÌïú Í≤∞Í≥ºÎ•º ÎèÑÏ∂úÌï©ÎãàÎã§",
    defaultContent: "Thought: Ïù¥ Í∏∞ÏÇ¨Ïùò ÌïµÏã¨ÏùÄ Î¨¥ÏóáÏù∏Í∞Ä?\nAction: ÌÇ§ÏõåÎìúÎ•º Ï∂îÏ∂úÌïòÍ≥† Ï§ëÏöîÎèÑÎ•º ÌèâÍ∞ÄÌïúÎã§\nObservation: Ï∂îÏ∂úÎêú Ï†ïÎ≥¥Î•º Î∞îÌÉïÏúºÎ°ú Ï†úÎ™©ÏùÑ Íµ¨ÏÑ±ÌïúÎã§\nThought: ÏÉùÏÑ±Îêú Ï†úÎ™©Ïù¥ ÏöîÍµ¨ÏÇ¨Ìï≠ÏùÑ Ï∂©Ï°±ÌïòÎäîÍ∞Ä?\nAction: ÌïÑÏöîÏãú ÏàòÏ†ïÌïòÍ≥† ÏµúÏ¢Ö Í≤ÄÏ¶ùÌïúÎã§",
    placeholder: "Thought/Action/Observation Ìå®ÌÑ¥ÏúºÎ°ú ÏûëÏÑ±ÌïòÏÑ∏Ïöî...",
    isReAct: true,
  },
  {
    id: "enhancement",
    name: "ÌíàÏßà Í≤ÄÏ¶ù",
    order: 6,
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
    icon: "üîç", 
    description: "ÏµúÏ¢Ö Í≤∞Í≥ºÎ¨ºÏùò ÌíàÏßàÏùÑ Í≤ÄÏ¶ùÌïòÍ≥† Í∞úÏÑ†Ìï©ÎãàÎã§",
    defaultContent: "ÌíàÏßà Í≤ÄÏ¶ù Í∏∞Ï§Ä:\n- Ï†ïÌôïÏÑ±: Í∏∞ÏÇ¨ ÎÇ¥Ïö©Í≥º ÏùºÏπò\n- Îß§Î†•ÎèÑ: ÎèÖÏûê Í¥ÄÏã¨ Ïú†Î∞ú\n- Ï†ÅÏ†àÏÑ±: Îß§Ï≤¥ ÏÑ±Í≤©Ïóê Î∂ÄÌï©\n- ÏôÑÏÑ±ÎèÑ: Î¨∏Î≤ïÍ≥º ÌëúÌòÑÏùò Ï†ïÌôïÏÑ±",
    placeholder: "ÌíàÏßà Í≤ÄÏ¶ù Í∏∞Ï§ÄÍ≥º Í∞úÏÑ† Î∞©ÏïàÏùÑ ÏûëÏÑ±ÌïòÏÑ∏Ïöî...",
  },
];

// ÌîÑÎ°¨ÌîÑÌä∏ Ìé∏Ïßë Î™®Îã¨
const PromptEditModal = ({ isOpen, onSubmit, onCancel, initialData }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <PromptEditForm
          onSubmit={onSubmit}
          onCancel={onCancel}
          initialData={initialData}
          isModal={true}
        />
      </div>
    </div>
  );
};

const PromptCardManager = ({ projectId, onCardsChanged, projectName }) => {
  // ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [promptCards, setPromptCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingCard, setEditingCard] = useState(null);

  // ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú Î°úÎìú Î∞è Í∏∞Î≥∏ ÌÖúÌîåÎ¶ø ÏÉùÏÑ±
  const loadPromptCards = useCallback(async () => {
    try {
      setLoading(true);
      const response = await promptCardAPI.getPromptCards(projectId);

      // API ÏùëÎãµ Íµ¨Ï°∞ ÌôïÏù∏ Î∞è ÏïàÏ†ÑÌïú Ï≤òÎ¶¨
      let cards = [];
      if (Array.isArray(response)) {
        cards = response;
      } else if (response && Array.isArray(response.promptCards)) {
        cards = response.promptCards;
      } else if (response && Array.isArray(response.data)) {
        cards = response.data;
      } else {
        console.warn("Unexpected API response structure:", response);
        cards = [];
      }

      // Í≥†Ï†ïÎêú ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÎì§Í≥º Í∏∞Ï°¥ Ïπ¥ÎìúÎì§ÏùÑ Îß§Ïπ≠
      const templateCards = FIXED_PROMPT_CATEGORIES.map((template) => {
        const existingCard = cards.find(
          (card) => card.category === template.id
        );

        if (existingCard) {
          // Í∏∞Ï°¥ Ïπ¥ÎìúÍ∞Ä ÏûàÏúºÎ©¥ Ìï¥Îãπ Ïπ¥Îìú ÏÇ¨Ïö©
          return {
            ...existingCard,
            ...template, // ÌÖúÌîåÎ¶ø Ï†ïÎ≥¥Î°ú ÎçÆÏñ¥Ïì∞Í∏∞ (name, order, color, description)
            title: existingCard.title || `${template.name} ÌîÑÎ°¨ÌîÑÌä∏`,
            prompt_text: existingCard.prompt_text || template.defaultContent,
          };
        } else {
          // Í∏∞Ï°¥ Ïπ¥ÎìúÍ∞Ä ÏóÜÏúºÎ©¥ ÌÖúÌîåÎ¶øÏúºÎ°ú Í∏∞Î≥∏ Ïπ¥Îìú ÏÉùÏÑ±
          return {
            id: `template-${template.id}`,
            promptId: `template-${template.id}`,
            title: `${template.name} ÌîÑÎ°¨ÌîÑÌä∏`,
            category: template.id,
            prompt_text: template.defaultContent,
            model_name: "claude-3-5-sonnet-20241022",
            temperature: 0.7,
            enabled: true,
            step_order: template.order,
            isTemplate: true, // ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÏûÑÏùÑ ÌëúÏãú
            ...template,
          };
        }
      });

      // ÏàúÏÑúÎåÄÎ°ú Ï†ïÎ†¨
      templateCards.sort((a, b) => (a.order || 999) - (b.order || 999));

      setPromptCards(templateCards);
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú Î°úÎî© Ïã§Ìå®: ${errorInfo.message}`);

      // Ïò§Î•ò ÏãúÏóêÎèÑ Í∏∞Î≥∏ ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÎì§ ÌëúÏãú
      const defaultCards = FIXED_PROMPT_CATEGORIES.map((template) => ({
        id: `template-${template.id}`,
        promptId: `template-${template.id}`,
        title: `${template.name} ÌîÑÎ°¨ÌîÑÌä∏`,
        category: template.id,
        prompt_text: template.defaultContent,
        model_name: "claude-3-5-sonnet-20241022",
        temperature: 0.7,
        enabled: true,
        step_order: template.order,
        isTemplate: true,
        ...template,
      }));

      setPromptCards(defaultCards);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  useEffect(() => {
    loadPromptCards();
  }, [loadPromptCards]);

  // Ïπ¥Îìú ÏóÖÎç∞Ïù¥Ìä∏
  const handleUpdateCard = async (cardData) => {
    try {
      if (editingCard && !editingCard.isTemplate) {
        // Í∏∞Ï°¥ Ïπ¥Îìú Ìé∏Ïßë - Í∏∞Ï°¥ Ï†ïÎ≥¥ Ïú†ÏßÄ
        const updateData = {
          ...cardData,
          category: editingCard.category,
          title: editingCard.title,
          step_order: editingCard.step_order || editingCard.order,
        };
        await promptCardAPI.updatePromptCard(
          projectId,
          editingCard.promptId || editingCard.id,
          updateData
        );
        toast.success("ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥ÎìúÍ∞Ä ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§!");
      } else if (editingCard && editingCard.isTemplate) {
        // ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÎ•º Ïã§Ï†ú Ïπ¥ÎìúÎ°ú ÏÉùÏÑ±
        const newCardData = {
          ...cardData,
          category: editingCard.category,
          title: editingCard.title,
          step_order: editingCard.order,
        };
        await promptCardAPI.createPromptCard(projectId, newCardData);
        toast.success("ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥ÎìúÍ∞Ä ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§!");
      } else {
        // ÏÉà Ïπ¥Îìú ÏÉùÏÑ±
        const newCardData = {
          ...cardData,
          category: "instruction", // Í∏∞Î≥∏ Ïπ¥ÌÖåÍ≥†Î¶¨
          title: "ÏÉà ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥Îìú",
          step_order: 1,
        };
        await promptCardAPI.createPromptCard(projectId, newCardData);
        toast.success("ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥ÎìúÍ∞Ä ÏÉùÏÑ±ÎêòÏóàÏäµÎãàÎã§!");
      }

      await loadPromptCards();
      setShowForm(false);
      setEditingCard(null);

      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(
        `Ïπ¥Îìú ${editingCard ? "ÏàòÏ†ï" : "ÏÉùÏÑ±"} Ïã§Ìå®: ${errorInfo.message}`
      );
    }
  };

  // Ïπ¥Îìú ÌÜ†Í∏Ä (ÏµúÏ†ÅÌôî UI)
  const handleToggleCard = async (promptId, enabled) => {
    // promptCardsÍ∞Ä Î∞∞Ïó¥Ïù∏ÏßÄ ÌôïÏù∏
    if (!Array.isArray(promptCards)) {
      console.error("promptCards is not an array:", promptCards);
      return;
    }

    // ÎÇôÍ¥ÄÏ†Å ÏóÖÎç∞Ïù¥Ìä∏: Ï¶âÏãú UI Î∞òÏòÅ
    const originalCards = [...promptCards];
    const updatedCards = promptCards.map((card) =>
      (card.promptId || card.id) === promptId ? { ...card, enabled } : card
    );
    setPromptCards(updatedCards);

    try {
      // ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÍ∞Ä ÏïÑÎãå Í≤ΩÏö∞ÏóêÎßå API Ìò∏Ï∂ú
      const card = promptCards.find((c) => (c.promptId || c.id) === promptId);
      if (card && !card.isTemplate) {
        await promptCardAPI.updatePromptCard(projectId, promptId, { enabled });
      }
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      // Ïã§Ìå® Ïãú Î≥µÏõê
      setPromptCards(originalCards);
      const errorInfo = handleAPIError(error);
      toast.error(`Ïπ¥Îìú ÏÉÅÌÉú Î≥ÄÍ≤Ω Ïã§Ìå®: ${errorInfo.message}`);
    }
  };

  // Ïπ¥Îìú ÏÇ≠Ï†ú - ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÎäî ÏÇ≠Ï†ú Î∂àÍ∞Ä
  const handleDeleteCard = async (promptId) => {
    const card = promptCards.find((c) => (c.promptId || c.id) === promptId);

    if (card && card.isTemplate) {
      toast.error("Í∏∞Î≥∏ ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÎäî ÏÇ≠Ï†úÌï† Ïàò ÏóÜÏäµÎãàÎã§.");
      return;
    }

    if (!window.confirm("Ï†ïÎßêÎ°ú Ïù¥ ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥ÎìúÎ•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) {
      return;
    }

    try {
      await promptCardAPI.deletePromptCard(projectId, promptId);
      await loadPromptCards(); // ÏÇ≠Ï†ú ÌõÑ Îã§Ïãú Î°úÎìúÌïòÏó¨ ÌÖúÌîåÎ¶ø Ïπ¥ÎìúÎ°ú Î≥µÏõê
      toast.success("ÌîÑÎ°¨ÌîÑÌä∏ Ïπ¥ÎìúÍ∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§!");
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
